@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE - Inspect360 Cyan/Teal Theme */
:root {
  --button-outline: rgba(0,0,0, .08);
  --badge-outline: rgba(0,0,0, .04);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -6; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .02);
  --elevate-2: rgba(0,0,0, .05);

  /* Main backgrounds */
  --background: 0 0% 100%;
  --foreground: 30 8% 15%;

  /* Borders */
  --border: 30 8% 90%;

  /* Cards - clean white */
  --card: 0 0% 100%;
  --card-foreground: 30 8% 15%;
  --card-border: 30 8% 92%;

  /* Sidebar - very light */
  --sidebar: 0 0% 100%;
  --sidebar-foreground: 30 8% 15%;
  --sidebar-border: 30 8% 90%;
  --sidebar-primary: 174 100% 42%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 174 30% 97%;
  --sidebar-accent-foreground: 193 40% 38%;
  --sidebar-ring: 174 100% 42%;

  /* Popovers */
  --popover: 0 0% 100%;
  --popover-foreground: 30 8% 15%;
  --popover-border: 30 8% 90%;

  /* Primary - Bright Cyan (from Inspect360 logo) */
  --primary: 174 100% 42%;
  --primary-foreground: 0 0% 100%;

  /* Secondary - Soft gray */
  --secondary: 30 8% 92%;
  --secondary-foreground: 30 8% 20%;

  /* Muted */
  --muted: 30 8% 95%;
  --muted-foreground: 30 5% 45%;

  /* Accent - Teal (from Inspect360 logo house) */
  --accent: 193 40% 38%;
  --accent-foreground: 0 0% 100%;

  /* Destructive */
  --destructive: 0 80% 55%;
  --destructive-foreground: 0 0% 100%;

  /* Input & Ring */
  --input: 30 8% 88%;
  --ring: 174 100% 42%;

  /* Charts - Cyan/Teal Palette */
  --chart-1: 174 100% 42%;
  --chart-2: 193 40% 38%;
  --chart-3: 145 60% 50%;
  --chart-4: 35 90% 55%;
  --chart-5: 280 60% 60%;

  /* Typography */
  --font-sans: Inter, -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'SF Mono', Menlo, Monaco, 'Courier New', monospace;

  /* Border Radius */
  --radius: 0.75rem; /* 12px - clean, modern */

  /* Soft Shadows */
  --shadow-2xs: 0 1px 2px 0 rgb(0 0 0 / 0.02);
  --shadow-xs: 0 1px 3px 0 rgb(0 0 0 / 0.04), 0 1px 2px -1px rgb(0 0 0 / 0.02);
  --shadow-sm: 0 2px 8px 0 rgb(0 0 0 / 0.04);
  --shadow: 0 4px 12px -1px rgb(0 0 0 / 0.06), 0 2px 4px -2px rgb(0 0 0 / 0.03);
  --shadow-md: 0 6px 16px -2px rgb(0 0 0 / 0.08), 0 3px 6px -3px rgb(0 0 0 / 0.04);
  --shadow-lg: 0 8px 20px -4px rgb(0 0 0 / 0.10), 0 4px 8px -4px rgb(0 0 0 / 0.05);
  --shadow-xl: 0 12px 28px -6px rgb(0 0 0 / 0.12), 0 6px 12px -6px rgb(0 0 0 / 0.06);
  --shadow-2xl: 0 20px 40px -8px rgb(0 0 0 / 0.14), 0 8px 16px -8px rgb(0 0 0 / 0.07);

  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

/* DARK MODE - Maintains clean aesthetic with proper contrast */
.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 8;

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .03);
  --elevate-2: rgba(255,255,255, .07);

  /* Main backgrounds */
  --background: 220 15% 12%;
  --foreground: 0 0% 98%;

  /* Borders */
  --border: 220 10% 25%;

  /* Cards */
  --card: 220 13% 16%;
  --card-foreground: 0 0% 98%;
  --card-border: 220 10% 22%;

  /* Sidebar */
  --sidebar: 220 13% 16%;
  --sidebar-foreground: 0 0% 98%;
  --sidebar-border: 220 10% 25%;
  --sidebar-primary: 174 100% 48%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 220 10% 20%;
  --sidebar-accent-foreground: 174 100% 48%;
  --sidebar-ring: 174 100% 48%;

  /* Popovers */
  --popover: 220 13% 16%;
  --popover-foreground: 0 0% 98%;
  --popover-border: 220 10% 25%;

  /* Primary - Bright Cyan (adjusted for dark mode) */
  --primary: 174 100% 48%;
  --primary-foreground: 0 0% 100%;

  /* Secondary */
  --secondary: 220 10% 20%;
  --secondary-foreground: 0 0% 95%;

  /* Muted */
  --muted: 220 10% 18%;
  --muted-foreground: 30 5% 70%;

  /* Accent - Teal (adjusted for dark mode) */
  --accent: 193 40% 45%;
  --accent-foreground: 0 0% 100%;

  /* Destructive */
  --destructive: 0 80% 60%;
  --destructive-foreground: 0 0% 100%;

  /* Input & Ring */
  --input: 220 10% 30%;
  --ring: 174 100% 48%;

  /* Charts - Adjusted for dark */
  --chart-1: 174 100% 48%;
  --chart-2: 193 40% 45%;
  --chart-3: 145 60% 58%;
  --chart-4: 35 90% 60%;
  --chart-5: 280 60% 68%;

  /* Darker shadows */
  --shadow-2xs: 0 1px 2px 0 rgb(0 0 0 / 0.30);
  --shadow-xs: 0 1px 3px 0 rgb(0 0 0 / 0.40), 0 1px 2px -1px rgb(0 0 0 / 0.30);
  --shadow-sm: 0 2px 8px 0 rgb(0 0 0 / 0.50);
  --shadow: 0 4px 12px -1px rgb(0 0 0 / 0.55), 0 2px 4px -2px rgb(0 0 0 / 0.40);
  --shadow-md: 0 6px 16px -2px rgb(0 0 0 / 0.60), 0 3px 6px -3px rgb(0 0 0 / 0.45);
  --shadow-lg: 0 8px 20px -4px rgb(0 0 0 / 0.65), 0 4px 8px -4px rgb(0 0 0 / 0.50);
  --shadow-xl: 0 12px 28px -6px rgb(0 0 0 / 0.70), 0 6px 12px -6px rgb(0 0 0 / 0.55);
  --shadow-2xl: 0 20px 40px -8px rgb(0 0 0 / 0.75), 0 8px 16px -8px rgb(0 0 0 / 0.60);

  /* Automatically computed borders */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Elevation System - Automatic contrast adjustment
 * 
 * Usage:
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 */
@layer utilities {

  /* Hide search cancel button in Chrome */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* Escape hatches for default elevation */
  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  /* Toggle elevation system */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Hover/active elevation system */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Clean Card System (replaces glassmorphic) */
  .clean-card {
    @apply bg-card border border-card-border shadow-sm rounded-xl;
  }

  .clean-card-hover {
    @apply clean-card transition-all duration-200 ease-out;
  }

  .clean-card-hover:hover {
    @apply shadow-md -translate-y-0.5;
  }

  /* Modern Transitions */
  .transition-smooth {
    @apply transition-all duration-200 ease-out;
  }

  .transition-fast {
    @apply transition-all duration-150 ease-out;
  }

  /* Focus Ring */
  .focus-ring {
    @apply focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-primary focus-visible:ring-offset-2;
  }

  /* Skeleton Loader with Shimmer */
  @keyframes shimmer {
    0% {
      background-position: -1000px 0;
    }
    100% {
      background-position: 1000px 0;
    }
  }

  .skeleton {
    @apply bg-muted rounded animate-pulse;
    background-image: linear-gradient(
      90deg,
      hsl(var(--muted)) 0px,
      hsl(174 100% 42% / 0.1) 40px,
      hsl(var(--muted)) 80px
    );
    background-size: 1000px 100%;
    animation: shimmer 2s infinite linear;
  }

  /* Uppy Modal Customization */
  .uppy-Dashboard-inner {
    background-color: hsl(var(--background)) !important;
    border: 1px solid hsl(var(--border)) !important;
    border-radius: var(--radius) !important;
  }

  .uppy-Dashboard-AddFiles {
    border-color: hsl(var(--border)) !important;
  }

  .uppy-Dashboard-AddFiles-title {
    color: hsl(var(--foreground)) !important;
  }

  .uppy-DashboardTab {
    background-color: hsl(var(--card)) !important;
    border: 1px solid hsl(var(--border)) !important;
    border-radius: 0.5rem !important;
    color: hsl(var(--foreground)) !important;
    transition: all 150ms ease-out;
  }

  .uppy-DashboardTab:hover {
    background-color: hsl(var(--accent)) !important;
    color: hsl(var(--accent-foreground)) !important;
    border-color: hsl(var(--accent)) !important;
  }

  .uppy-DashboardTab-btn {
    display: flex !important;
    flex-direction: column !important;
    align-items: center !important;
    gap: 0.5rem !important;
    padding: 1.5rem 2rem !important;
  }

  .uppy-DashboardTab-name {
    font-weight: 500 !important;
    font-size: 0.875rem !important;
  }

  /* Make tabs display side by side on larger screens */
  .uppy-Dashboard-AddFilesPanel {
    display: flex !important;
    flex-direction: column !important;
    gap: 1.5rem !important;
  }

  .uppy-DashboardTabs {
    display: grid !important;
    grid-template-columns: repeat(auto-fit, minmax(140px, 1fr)) !important;
    gap: 1rem !important;
  }

  @media (min-width: 640px) {
    .uppy-DashboardTabs {
      grid-template-columns: repeat(2, 1fr) !important;
    }
  }

  .uppy-Dashboard-poweredBy {
    display: none !important;
  }

  .uppy-StatusBar {
    background-color: hsl(var(--muted)) !important;
    border-top: 1px solid hsl(var(--border)) !important;
  }

  .uppy-StatusBar-progress {
    background-color: hsl(var(--primary)) !important;
  }

  .uppy-size--md .uppy-Dashboard-AddFiles {
    padding: 2rem !important;
  }

  /* Ensure Uppy modal has correct z-index and doesn't block parent dialog */
  .uppy-Dashboard--modal {
    z-index: 10001 !important;
  }

  .uppy-Dashboard--modal .uppy-Dashboard-overlay {
    z-index: 10000 !important;
  }
}

