import { useQuery } from "@tanstack/react-query";
import { useRoute, Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import ComplianceCalendar from "@/components/ComplianceCalendar";
import { ArrowLeft, Building2, MapPin, Users, CheckCircle2, Calendar, AlertTriangle, FileCheck } from "lucide-react";

interface PropertyStats {
  totalUnits: number;
  occupiedUnits: number;
  occupancyStatus: string;
  complianceRate: number;
  complianceStatus: string;
  inspectionsDue: number;
  inspectionsOverdue: number;
}

interface Property {
  id: string;
  name: string;
  address: string;
  blockId: string | null;
  stats: PropertyStats;
}

interface Block {
  id: string;
  name: string;
  address: string;
  notes?: string | null;
}

export default function BlockDetail() {
  const [, params] = useRoute("/blocks/:id");
  const blockId = params?.id;

  const { data: block, isLoading: blockLoading } = useQuery<Block>({
    queryKey: ["/api/blocks", blockId],
    queryFn: async () => {
      const res = await fetch(`/api/blocks/${blockId}`, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch block");
      return res.json();
    },
    enabled: !!blockId,
  });

  const { data: properties = [], isLoading: propertiesLoading } = useQuery<Property[]>({
    queryKey: ["/api/blocks", blockId, "properties"],
    queryFn: async () => {
      const res = await fetch(`/api/blocks/${blockId}/properties`, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch properties");
      return res.json();
    },
    enabled: !!blockId,
  });

  const { data: complianceReport, isLoading: complianceReportLoading } = useQuery({
    queryKey: ["/api/blocks", blockId, "compliance-report"],
    queryFn: async () => {
      const res = await fetch(`/api/blocks/${blockId}/compliance-report`, { credentials: "include" });
      if (!res.ok) return null;
      return res.json();
    },
    enabled: !!blockId,
  });

  if (blockLoading || propertiesLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="text-center py-12">Loading...</div>
      </div>
    );
  }

  if (!block) {
    return (
      <div className="container mx-auto p-6">
        <div className="text-center py-12">
          <p className="text-muted-foreground">Block not found</p>
          <Link href="/blocks">
            <Button variant="outline" className="mt-4">
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Blocks
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6">
      {/* Header */}
      <div className="mb-6">
        <Link href="/blocks">
          <Button variant="ghost" className="mb-4" data-testid="button-back-to-blocks">
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to Blocks
          </Button>
        </Link>

        <div className="flex items-start justify-between">
          <div>
            <h1 className="text-3xl font-bold flex items-center gap-2">
              <Building2 className="h-8 w-8 text-primary" />
              {block.name}
            </h1>
            <p className="text-muted-foreground mt-2 flex items-center gap-2">
              <MapPin className="h-4 w-4" />
              {block.address}
            </p>
            {block.notes && (
              <p className="text-sm text-muted-foreground mt-3 bg-muted p-3 rounded-md">
                {block.notes}
              </p>
            )}
          </div>
        </div>
      </div>

      {/* Tabs */}
      <Tabs defaultValue="properties" className="space-y-6">
        <TabsList>
          <TabsTrigger value="properties" data-testid="tab-properties">
            <Building2 className="h-4 w-4 mr-2" />
            Properties ({properties.length})
          </TabsTrigger>
          <TabsTrigger value="compliance" data-testid="tab-compliance">
            <FileCheck className="h-4 w-4 mr-2" />
            Compliance
          </TabsTrigger>
        </TabsList>

        {/* Properties Tab */}
        <TabsContent value="properties" className="space-y-4">
          {properties.length === 0 ? (
            <Card>
              <CardContent className="flex flex-col items-center justify-center py-12">
                <Building2 className="h-12 w-12 text-muted-foreground mb-4" />
                <h3 className="text-lg font-semibold mb-2">No properties yet</h3>
                <p className="text-muted-foreground text-center mb-4">
                  Properties assigned to this block will appear here
                </p>
                <Link href="/properties">
                  <Button>View All Properties</Button>
                </Link>
              </CardContent>
            </Card>
          ) : (
            <div className="grid gap-4">
              {properties.map((property) => (
                <Link key={property.id} href={`/properties/${property.id}`}>
                  <Card className="hover-elevate cursor-pointer" data-testid={`card-property-${property.id}`}>
                    <CardHeader>
                      <div className="flex items-start justify-between">
                        <div>
                          <CardTitle className="flex items-center gap-2">
                            <Building2 className="h-5 w-5 text-primary" />
                            {property.name}
                          </CardTitle>
                          <p className="text-sm text-muted-foreground mt-1">{property.address}</p>
                        </div>
                      </div>
                    </CardHeader>
                    <CardContent>
                      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                        {/* Occupancy */}
                        <div className="space-y-1">
                          <div className="flex items-center gap-2 text-sm font-medium">
                            <Users className="h-4 w-4 text-muted-foreground" />
                            Occupancy
                          </div>
                          <p className="text-sm font-semibold">{property.stats?.occupancyStatus || 'No data'}</p>
                        </div>

                        {/* Compliance */}
                        <div className="space-y-1">
                          <div className="flex items-center gap-2 text-sm font-medium">
                            <CheckCircle2 className="h-4 w-4 text-muted-foreground" />
                            Compliance
                          </div>
                          <Badge 
                            variant={(property.stats?.complianceRate || 0) >= 80 ? "default" : "destructive"}
                            className="text-xs"
                          >
                            {property.stats?.complianceStatus || 'No data'}
                          </Badge>
                        </div>

                        {/* Due Soon */}
                        <div className="space-y-1">
                          <div className="flex items-center gap-2 text-sm font-medium">
                            <Calendar className="h-4 w-4 text-muted-foreground" />
                            Due Soon
                          </div>
                          <Badge 
                            variant={(property.stats?.inspectionsDue || 0) > 0 ? "secondary" : "outline"}
                            className="text-xs"
                          >
                            {property.stats?.inspectionsDue || 0} inspection{(property.stats?.inspectionsDue || 0) !== 1 ? 's' : ''}
                          </Badge>
                        </div>

                        {/* Overdue */}
                        <div className="space-y-1">
                          <div className="flex items-center gap-2 text-sm font-medium">
                            <AlertTriangle className="h-4 w-4 text-muted-foreground" />
                            Overdue
                          </div>
                          <Badge 
                            variant={(property.stats?.inspectionsOverdue || 0) > 0 ? "destructive" : "outline"}
                            className="text-xs"
                          >
                            {property.stats?.inspectionsOverdue || 0} inspection{(property.stats?.inspectionsOverdue || 0) !== 1 ? 's' : ''}
                          </Badge>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                </Link>
              ))}
            </div>
          )}
        </TabsContent>

        {/* Compliance Tab */}
        <TabsContent value="compliance" className="space-y-6">
          <ComplianceCalendar 
            report={complianceReport} 
            isLoading={complianceReportLoading}
            entityType="block"
          />
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/WorkOrders.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Calendar, DollarSign, User, AlertCircle, CheckCircle2, Clock } from "lucide-react";
import { formatDistanceToNow } from "date-fns";

interface WorkOrder {
  id: string;
  status: string;
  slaDue?: string | null;
  costEstimate?: number | null;
  costActual?: number | null;
  createdAt: string;
  maintenanceRequest: {
    id: string;
    title: string;
    description?: string;
    priority: string;
  };
  contractor?: {
    firstName?: string;
    lastName?: string;
    email: string;
  };
}

const statusColors: Record<string, string> = {
  assigned: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300",
  in_progress: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300",
  waiting_parts: "bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-300",
  completed: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300",
  rejected: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300",
};

const priorityColors: Record<string, string> = {
  low: "bg-gray-100 text-gray-800",
  medium: "bg-blue-100 text-blue-800",
  high: "bg-orange-100 text-orange-800",
  urgent: "bg-red-100 text-red-800",
};

export default function WorkOrders() {
  const { user } = useAuth();
  const { toast } = useToast();

  const { data: workOrders = [], isLoading } = useQuery<WorkOrder[]>({
    queryKey: ["/api/work-orders"],
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: string; status: string }) => {
      return apiRequest("PATCH", `/api/work-orders/${id}/status`, { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/work-orders"] });
      toast({ title: "Status updated successfully" });
    },
    onError: () => {
      toast({ title: "Failed to update status", variant: "destructive" });
    },
  });

  const formatCurrency = (amount?: number | null) => {
    if (!amount) return "N/A";
    return `$${(amount / 100).toFixed(2)}`;
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "completed":
        return <CheckCircle2 className="h-4 w-4" />;
      case "in_progress":
      case "waiting_parts":
        return <Clock className="h-4 w-4" />;
      case "rejected":
        return <AlertCircle className="h-4 w-4" />;
      default:
        return <User className="h-4 w-4" />;
    }
  };

  return (
    <div className="container mx-auto p-6">
      <div className="mb-6">
        <h1 className="text-3xl font-bold">Work Orders</h1>
        <p className="text-muted-foreground">
          {user?.role === "contractor" 
            ? "View and manage your assigned work orders" 
            : "Manage contractor work orders and assignments"}
        </p>
      </div>

      {isLoading ? (
        <div className="text-center py-8">Loading work orders...</div>
      ) : workOrders.length === 0 ? (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <User className="h-12 w-12 text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold mb-2">No work orders</h3>
            <p className="text-muted-foreground text-center">
              {user?.role === "contractor"
                ? "You don't have any assigned work orders yet"
                : "Create work orders from maintenance requests"}
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-4">
          {workOrders.map((workOrder) => (
            <Card key={workOrder.id} data-testid={`card-work-order-${workOrder.id}`}>
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-2">
                      {getStatusIcon(workOrder.status)}
                      <CardTitle className="text-lg">
                        {workOrder.maintenanceRequest.title}
                      </CardTitle>
                      <Badge className={priorityColors[workOrder.maintenanceRequest.priority]}>
                        {workOrder.maintenanceRequest.priority}
                      </Badge>
                    </div>
                    <CardDescription>
                      {workOrder.maintenanceRequest.description || "No description provided"}
                    </CardDescription>
                  </div>
                  <Badge className={statusColors[workOrder.status]}>
                    {workOrder.status.replace("_", " ")}
                  </Badge>
                </div>
              </CardHeader>
              <CardContent>
                <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
                  {workOrder.contractor && (
                    <div className="flex items-center gap-2 text-sm">
                      <User className="h-4 w-4 text-muted-foreground" />
                      <div>
                        <p className="font-medium">Contractor</p>
                        <p className="text-muted-foreground">
                          {workOrder.contractor.firstName} {workOrder.contractor.lastName}
                        </p>
                      </div>
                    </div>
                  )}

                  {workOrder.slaDue && (
                    <div className="flex items-center gap-2 text-sm">
                      <Calendar className="h-4 w-4 text-muted-foreground" />
                      <div>
                        <p className="font-medium">SLA Due</p>
                        <p className="text-muted-foreground">
                          {formatDistanceToNow(new Date(workOrder.slaDue), { addSuffix: true })}
                        </p>
                      </div>
                    </div>
                  )}

                  {(workOrder.costEstimate || workOrder.costActual) && (
                    <div className="flex items-center gap-2 text-sm">
                      <DollarSign className="h-4 w-4 text-muted-foreground" />
                      <div>
                        <p className="font-medium">Cost</p>
                        <p className="text-muted-foreground">
                          {workOrder.costActual 
                            ? `Actual: ${formatCurrency(workOrder.costActual)}`
                            : `Est: ${formatCurrency(workOrder.costEstimate)}`}
                        </p>
                      </div>
                    </div>
                  )}

                  <div className="flex items-center gap-2 text-sm">
                    <Clock className="h-4 w-4 text-muted-foreground" />
                    <div>
                      <p className="font-medium">Created</p>
                      <p className="text-muted-foreground">
                        {formatDistanceToNow(new Date(workOrder.createdAt), { addSuffix: true })}
                      </p>
                    </div>
                  </div>
                </div>

                {user?.role === "contractor" && workOrder.status !== "completed" && workOrder.status !== "rejected" && (
                  <div className="mt-4 flex items-center gap-2">
                    <label className="text-sm font-medium">Update Status:</label>
                    <Select
                      value={workOrder.status}
                      onValueChange={(status) => updateStatusMutation.mutate({ id: workOrder.id, status })}
                    >
                      <SelectTrigger className="w-48" data-testid={`select-status-${workOrder.id}`}>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="assigned">Assigned</SelectItem>
                        <SelectItem value="in_progress">In Progress</SelectItem>
                        <SelectItem value="waiting_parts">Waiting Parts</SelectItem>
                        <SelectItem value="completed">Completed</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                )}
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

