import { useState, useMemo, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Plus, Building, Pencil, Trash2, Users, CheckCircle2, Calendar, AlertTriangle, Search, Package, ClipboardCheck, FileText, Home } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { Link } from "wouter";
import { TagInput } from "@/components/TagInput";
import type { Tag } from "@shared/schema";

interface BlockStats {
  totalProperties: number;
  totalUnits: number;
  occupiedUnits: number;
  occupancyRate: number;
  complianceRate: number;
  inspectionsDue: number;
  overdueInspections: number;
}

interface Block {
  id: string;
  organizationId: string;
  name: string;
  address: string;
  notes?: string | null;
  createdAt: string | null;
  updatedAt: string | null;
  stats?: BlockStats;
}

export default function Blocks() {
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const [editingBlock, setEditingBlock] = useState<Block | null>(null);
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [notes, setNotes] = useState("");
  const [selectedTags, setSelectedTags] = useState<Tag[]>([]);
  const [searchQuery, setSearchQuery] = useState("");
  const { toast } = useToast();

  const { data: blocks = [], isLoading } = useQuery<Block[]>({
    queryKey: ["/api/blocks"],
  });

  // Filter blocks by search query
  const filteredBlocks = useMemo(() => {
    if (!searchQuery.trim()) return blocks;
    const query = searchQuery.toLowerCase();
    return blocks.filter(block => 
      block.name.toLowerCase().includes(query) ||
      block.address.toLowerCase().includes(query)
    );
  }, [blocks, searchQuery]);

  const createBlockMutation = useMutation({
    mutationFn: async (data: { name: string; address: string; notes?: string }) => {
      return apiRequest("POST", "/api/blocks", data);
    },
    onSuccess: async () => {
      await queryClient.refetchQueries({ queryKey: ["/api/blocks"] });
      toast({ title: "Block created successfully" });
      handleCloseDialog();
    },
    onError: (error: any) => {
      console.error("Block creation error:", error);
      const message = error?.message || "Failed to create block";
      toast({ title: message, variant: "destructive" });
    },
  });

  const updateBlockMutation = useMutation({
    mutationFn: async (data: { id: string; name: string; address: string; notes?: string }) => {
      return apiRequest("PATCH", `/api/blocks/${data.id}`, { 
        name: data.name, 
        address: data.address, 
        notes: data.notes 
      });
    },
    onSuccess: async () => {
      await queryClient.refetchQueries({ queryKey: ["/api/blocks"] });
      toast({ title: "Block updated successfully" });
      handleCloseDialog();
    },
    onError: () => {
      toast({ title: "Failed to update block", variant: "destructive" });
    },
  });

  const deleteBlockMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/blocks/${id}`);
    },
    onSuccess: async () => {
      await queryClient.refetchQueries({ queryKey: ["/api/blocks"] });
      toast({ title: "Block deleted successfully" });
    },
    onError: () => {
      toast({ title: "Failed to delete block", variant: "destructive" });
    },
  });

  const handleOpenCreate = () => {
    setEditingBlock(null);
    setName("");
    setAddress("");
    setNotes("");
    setSelectedTags([]);
    setIsCreateOpen(true);
  };

  const handleOpenEdit = async (block: Block) => {
    setEditingBlock(block);
    setName(block.name);
    setAddress(block.address);
    setNotes(block.notes || "");

    // Fetch tags for this block
    try {
      const res = await fetch(`/api/blocks/${block.id}/tags`, { credentials: "include" });
      if (res.ok) {
        const tags = await res.json();
        setSelectedTags(tags);
      }
    } catch (error) {
      console.error("Error fetching block tags:", error);
    }

    setIsCreateOpen(true);
  };

  const handleCloseDialog = () => {
    setIsCreateOpen(false);
    setEditingBlock(null);
    setName("");
    setAddress("");
    setNotes("");
    setSelectedTags([]);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!name || !address) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    try {
      if (editingBlock) {
        // Update the block
        const res = await apiRequest("PATCH", `/api/blocks/${editingBlock.id}`, { 
          name, 
          address, 
          notes: notes || undefined 
        });

        // Update tags
        await updateBlockTags(editingBlock.id, selectedTags);

        await queryClient.refetchQueries({ queryKey: ["/api/blocks"] });
        toast({ title: "Block updated successfully" });
        handleCloseDialog();
      } else {
        // Create the block
        const res = await apiRequest("POST", "/api/blocks", { 
          name, 
          address, 
          notes: notes || undefined 
        });
        const newBlock = await res.json();

        // Add tags to the new block
        await updateBlockTags(newBlock.id, selectedTags);

        await queryClient.refetchQueries({ queryKey: ["/api/blocks"] });
        toast({ title: "Block created successfully" });
        handleCloseDialog();
      }
    } catch (error: any) {
      console.error("Error saving block:", error);
      const message = error?.message || "Failed to save block";
      toast({ title: message, variant: "destructive" });
    }
  };

  const updateBlockTags = async (blockId: string, tags: Tag[]) => {
    // Get current tags for the block
    try {
      const res = await fetch(`/api/blocks/${blockId}/tags`, { credentials: "include" });
      const currentTags = res.ok ? await res.json() : [];

      // Remove tags that are no longer selected
      for (const currentTag of currentTags) {
        if (!tags.find(t => t.id === currentTag.id)) {
          await apiRequest("DELETE", `/api/blocks/${blockId}/tags/${currentTag.id}`);
        }
      }

      // Add new tags
      for (const tag of tags) {
        if (!currentTags.find((t: Tag) => t.id === tag.id)) {
          await apiRequest("POST", `/api/blocks/${blockId}/tags/${tag.id}`);
        }
      }
    } catch (error) {
      console.error("Error updating block tags:", error);
    }
  };

  const handleDelete = (id: string) => {
    if (confirm("Are you sure you want to delete this block? This cannot be undone.")) {
      deleteBlockMutation.mutate(id);
    }
  };

  return (
    <div className="container mx-auto p-6 md:p-8 lg:p-12 space-y-8">
      {/* Modern Header */}
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
        <div className="space-y-1">
          <h1 className="text-4xl md:text-5xl font-bold tracking-tight">Blocks & Buildings</h1>
          <p className="text-lg text-muted-foreground">Manage your property complexes and building blocks</p>
        </div>
        <Button onClick={handleOpenCreate} size="lg" className="transition-smooth" data-testid="button-create-block">
          <Plus className="mr-2 h-5 w-5" />
          New Block
        </Button>
      </div>

      {/* Modern Search Filter */}
      {blocks.length > 0 && (
        <div className="max-w-2xl">
          <div className="relative">
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-muted-foreground h-5 w-5" />
            <Input
              placeholder="Search blocks by name or address..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-12 h-12 text-base glass-card border-border/50"
              data-testid="input-search-blocks"
            />
          </div>
        </div>
      )}

      {/* Content Area */}
      {isLoading ? (
        <div className="flex items-center justify-center py-24">
          <div className="flex flex-col items-center gap-4">
            <div className="w-12 h-12 border-4 border-primary border-t-transparent rounded-full animate-spin" />
            <p className="text-muted-foreground">Loading blocks...</p>
          </div>
        </div>
      ) : blocks.length === 0 ? (
        <Card className="glass-card-strong">
          <CardContent className="flex flex-col items-center justify-center py-16 md:py-24">
            <div className="w-20 h-20 rounded-full bg-primary/10 flex items-center justify-center mb-6">
              <Building className="h-10 w-10 text-primary" />
            </div>
            <h3 className="text-2xl font-semibold mb-3">No blocks yet</h3>
            <p className="text-muted-foreground text-center mb-6 max-w-md">
              Create your first building block to organize properties and streamline management
            </p>
            <Button onClick={handleOpenCreate} size="lg" className="transition-smooth" data-testid="button-create-first-block">
              <Plus className="mr-2 h-5 w-5" />
              Create Block
            </Button>
          </CardContent>
        </Card>
      ) : filteredBlocks.length === 0 ? (
        <Card className="glass-card">
          <CardContent className="flex flex-col items-center justify-center py-16">
            <div className="w-16 h-16 rounded-full bg-muted flex items-center justify-center mb-4">
              <Search className="h-8 w-8 text-muted-foreground" />
            </div>
            <h3 className="text-xl font-semibold mb-2">No blocks found</h3>
            <p className="text-muted-foreground text-center mb-6">
              Try adjusting your search query
            </p>
            <Button variant="outline" onClick={() => setSearchQuery("")} className="transition-smooth">
              Clear Search
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-6 md:gap-8 sm:grid-cols-2 lg:grid-cols-3">
          {filteredBlocks.map((block) => (
            <Card key={block.id} data-testid={`card-block-${block.id}`} className="glass-card card-hover-lift overflow-hidden">
              <CardHeader className="pb-4">
                <div className="flex items-start justify-between gap-2">
                  <Link href={`/blocks/${block.id}`} className="flex-1 min-w-0">
                    <div className="flex items-center gap-3 cursor-pointer group">
                      <div className="w-10 h-10 rounded-xl bg-primary/10 flex items-center justify-center flex-shrink-0 group-hover:bg-primary/20 transition-smooth">
                        <Building className="h-5 w-5 text-primary" />
                      </div>
                      <CardTitle className="text-xl font-bold truncate">{block.name}</CardTitle>
                    </div>
                    <CardDescription className="line-clamp-2 cursor-pointer mt-3 text-base">{block.address}</CardDescription>
                  </Link>
                  <div className="flex gap-1 flex-shrink-0">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={(e) => {
                        e.preventDefault();
                        handleOpenEdit(block);
                      }}
                      className="transition-smooth"
                      data-testid={`button-edit-block-${block.id}`}
                    >
                      <Pencil className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={(e) => {
                        e.preventDefault();
                        handleDelete(block.id);
                      }}
                      className="transition-smooth"
                      data-testid={`button-delete-block-${block.id}`}
                    >
                      <Trash2 className="h-4 w-4 text-destructive" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-5 pt-0">
                {/* Divider */}
                <div className="h-px bg-border/30" />

                {/* Stats Grid */}
                <div className="grid grid-cols-2 gap-5">
                  {/* Occupancy */}
                  <div className="space-y-2">
                    <div className="flex items-center gap-2">
                      <div className="w-8 h-8 rounded-lg bg-chart-3/10 flex items-center justify-center">
                        <Users className="h-4 w-4 text-chart-3" />
                      </div>
                      <span className="text-xs font-medium text-muted-foreground">Occupancy</span>
                    </div>
                    <div className="flex items-baseline gap-2">
                      <span className="text-2xl font-bold" data-testid={`badge-occupancy-${block.id}`}>
                        {block.stats?.occupancyRate || 0}%
                      </span>
                      <span className="text-xs text-muted-foreground">
                        {block.stats?.occupiedUnits || 0}/{block.stats?.totalUnits || 0}
                      </span>
                    </div>
                  </div>

                  {/* Compliance */}
                  <div className="space-y-2">
                    <div className="flex items-center gap-2">
                      <div className={`w-8 h-8 rounded-lg flex items-center justify-center ${(block.stats?.complianceRate || 0) >= 80 ? 'bg-accent/10' : 'bg-destructive/10'}`}>
                        <CheckCircle2 className={`h-4 w-4 ${(block.stats?.complianceRate || 0) >= 80 ? 'text-accent' : 'text-destructive'}`} />
                      </div>
                      <span className="text-xs font-medium text-muted-foreground">Compliance</span>
                    </div>
                    <div className="flex items-baseline gap-2">
                      <span className={`text-2xl font-bold ${(block.stats?.complianceRate || 0) >= 80 ? 'text-accent' : 'text-destructive'}`} data-testid={`badge-compliance-${block.id}`}>
                        {block.stats?.complianceRate || 0}%
                      </span>
                    </div>
                  </div>

                  {/* Due Soon */}
                  <div className="space-y-2">
                    <div className="flex items-center gap-2">
                      <div className="w-8 h-8 rounded-lg bg-primary/10 flex items-center justify-center">
                        <Calendar className="h-4 w-4 text-primary" />
                      </div>
                      <span className="text-xs font-medium text-muted-foreground">Due Soon</span>
                    </div>
                    <div className="flex items-baseline gap-2">
                      <span className="text-2xl font-bold" data-testid={`badge-due-${block.id}`}>
                        {block.stats?.inspectionsDue || 0}
                      </span>
                    </div>
                  </div>

                  {/* Overdue */}
                  <div className="space-y-2">
                    <div className="flex items-center gap-2">
                      <div className={`w-8 h-8 rounded-lg flex items-center justify-center ${(block.stats?.overdueInspections || 0) > 0 ? 'bg-destructive/10' : 'bg-muted'}`}>
                        <AlertTriangle className={`h-4 w-4 ${(block.stats?.overdueInspections || 0) > 0 ? 'text-destructive' : 'text-muted-foreground'}`} />
                      </div>
                      <span className="text-xs font-medium text-muted-foreground">Overdue</span>
                    </div>
                    <div className="flex items-baseline gap-2">
                      <span className={`text-2xl font-bold ${(block.stats?.overdueInspections || 0) > 0 ? 'text-destructive' : ''}`} data-testid={`badge-overdue-${block.id}`}>
                        {block.stats?.overdueInspections || 0}
                      </span>
                    </div>
                  </div>
                </div>

                {/* Notes */}
                {block.notes && (
                  <>
                    <div className="h-px bg-border/30" />
                    <div className="space-y-1">
                      <span className="text-xs font-medium text-muted-foreground">Notes</span>
                      <p className="text-sm text-foreground line-clamp-2">{block.notes}</p>
                    </div>
                  </>
                )}

                {/* Quick Actions */}
                <div className="h-px bg-border/30" />
                <div className="flex items-center justify-around -mx-2">
                  <Link href={`/properties?blockId=${block.id}`}>
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className="flex flex-col h-auto py-2 px-3"
                      data-testid={`button-properties-${block.id}`}
                    >
                      <Home className="h-4 w-4 mb-1" />
                      <span className="text-xs">Properties</span>
                    </Button>
                  </Link>
                  <Link href={`/asset-inventory?blockId=${block.id}`}>
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className="flex flex-col h-auto py-2 px-3"
                      data-testid={`button-inventory-${block.id}`}
                    >
                      <Package className="h-4 w-4 mb-1" />
                      <span className="text-xs">Inventory</span>
                    </Button>
                  </Link>
                  <Link href={`/inspections?blockId=${block.id}&create=true`}>
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className="flex flex-col h-auto py-2 px-3"
                      data-testid={`button-inspect-${block.id}`}
                    >
                      <ClipboardCheck className="h-4 w-4 mb-1" />
                      <span className="text-xs">Inspect</span>
                    </Button>
                  </Link>
                  <Link href={`/blocks/${block.id}/tenants`}>
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className="flex flex-col h-auto py-2 px-3"
                      data-testid={`button-tenants-${block.id}`}
                    >
                      <Users className="h-4 w-4 mb-1" />
                      <span className="text-xs">Tenants</span>
                    </Button>
                  </Link>
                  <Link href={`/compliance?blockId=${block.id}`}>
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className="flex flex-col h-auto py-2 px-3"
                      data-testid={`button-compliance-${block.id}`}
                    >
                      <FileText className="h-4 w-4 mb-1" />
                      <span className="text-xs">Compliance</span>
                    </Button>
                  </Link>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      <Dialog open={isCreateOpen} onOpenChange={setIsCreateOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>{editingBlock ? "Edit Block" : "Create New Block"}</DialogTitle>
            <DialogDescription>
              {editingBlock ? "Update the block details below" : "Add a new building block to your organization"}
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="name">Block Name *</Label>
              <Input
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="e.g., Block A, East Wing, Tower 1"
                data-testid="input-block-name"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="address">Address *</Label>
              <Input
                id="address"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                placeholder="123 Main Street, City, Postcode"
                data-testid="input-block-address"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="notes">Notes</Label>
              <Textarea
                id="notes"
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Additional information about this block"
                rows={3}
                data-testid="input-block-notes"
              />
            </div>

            <div className="space-y-2">
              <Label>Tags</Label>
              <TagInput
                selectedTags={selectedTags}
                onTagsChange={setSelectedTags}
                placeholder="Add tags to organize this block..."
              />
            </div>

            <div className="flex justify-end gap-2">
              <Button type="button" variant="outline" onClick={handleCloseDialog} data-testid="button-cancel">
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createBlockMutation.isPending || updateBlockMutation.isPending}
                data-testid="button-submit-block"
              >
                {editingBlock ? "Update" : "Create"} Block
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/Settings.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger, DialogFooter } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Settings as SettingsIcon, Tags, Users, Plus, Edit2, Trash2, Plug, UsersIcon } from "lucide-react";
import { insertInspectionCategorySchema, type InspectionCategory } from "@shared/schema";
import { z } from "zod";
import Team from "./Team";
import FixfloIntegrationSettings from "@/components/FixfloIntegrationSettings";
import SettingsTeamsPanel from "@/components/SettingsTeamsPanel";

const categoryFormSchema = insertInspectionCategorySchema.extend({
  name: z.string().min(1, "Category name is required"),
  sortOrder: z.coerce.number().min(0, "Sort order must be 0 or greater"),
});

type CategoryFormValues = z.infer<typeof categoryFormSchema>;

export default function Settings() {
  const { toast } = useToast();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingCategory, setEditingCategory] = useState<InspectionCategory | null>(null);

  // Fetch inspection categories
  const { data: categories, isLoading: categoriesLoading } = useQuery<InspectionCategory[]>({
    queryKey: ["/api/inspection-categories"],
  });

  // Create category mutation
  const createMutation = useMutation({
    mutationFn: async (data: CategoryFormValues) => {
      return await apiRequest("POST", "/api/inspection-categories", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/inspection-categories"] });
      toast({
        title: "Success",
        description: "Inspection category created successfully",
      });
      setIsCreateDialogOpen(false);
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to create category",
      });
    },
  });

  // Update category mutation
  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<CategoryFormValues> }) => {
      return await apiRequest("PATCH", `/api/inspection-categories/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/inspection-categories"] });
      toast({
        title: "Success",
        description: "Inspection category updated successfully",
      });
      setEditingCategory(null);
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to update category",
      });
    },
  });

  // Delete category mutation
  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/inspection-categories/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/inspection-categories"] });
      toast({
        title: "Success",
        description: "Inspection category deleted successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to delete category",
      });
    },
  });

  const createForm = useForm<CategoryFormValues>({
    resolver: zodResolver(categoryFormSchema),
    defaultValues: {
      name: "",
      description: "",
      sortOrder: 0,
    },
  });

  const editForm = useForm<CategoryFormValues>({
    resolver: zodResolver(categoryFormSchema),
    defaultValues: {
      name: "",
      description: "",
      sortOrder: 0,
    },
  });

  // Update edit form when editingCategory changes
  if (editingCategory && editForm.getValues().name !== editingCategory.name) {
    editForm.reset({
      name: editingCategory.name,
      description: editingCategory.description ?? "",
      sortOrder: editingCategory.sortOrder ?? 0,
    });
  }

  const onCreateSubmit = (data: CategoryFormValues) => {
    createMutation.mutate(data);
  };

  const onEditSubmit = (data: CategoryFormValues) => {
    if (editingCategory) {
      updateMutation.mutate({ id: editingCategory.id, data });
    }
  };

  return (
    <div className="p-8 bg-background min-h-screen">
      <div className="max-w-6xl mx-auto">
        <div className="flex items-center gap-3 mb-8">
          <div className="p-3 rounded-2xl bg-primary/10 backdrop-blur-xl">
            <SettingsIcon className="w-8 h-8 text-primary" />
          </div>
          <div>
            <h1 className="text-4xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-primary to-primary/60">
              Settings
            </h1>
            <p className="text-muted-foreground mt-1">Manage your organization configuration</p>
          </div>
        </div>

        <Tabs defaultValue="categories" className="w-full">
          <TabsList className="grid w-full max-w-3xl grid-cols-4 mb-8">
            <TabsTrigger value="categories" className="gap-2" data-testid="tab-inspection-categories">
              <Tags className="w-4 h-4" />
              Inspection Categories
            </TabsTrigger>
            <TabsTrigger value="teams" className="gap-2" data-testid="tab-teams">
              <UsersIcon className="w-4 h-4" />
              Teams
            </TabsTrigger>
            <TabsTrigger value="team" className="gap-2" data-testid="tab-team-members">
              <Users className="w-4 h-4" />
              Team Members
            </TabsTrigger>
            <TabsTrigger value="integrations" className="gap-2" data-testid="tab-integrations">
              <Plug className="w-4 h-4" />
              Integrations
            </TabsTrigger>
          </TabsList>

          <TabsContent value="categories" className="space-y-6">
            <Card className="border-2 rounded-2xl bg-card/80 backdrop-blur-xl shadow-lg">
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle className="text-2xl">Inspection Categories</CardTitle>
                    <CardDescription className="mt-2">
                      Manage inspection item categories for consistent reporting
                    </CardDescription>
                  </div>
                  <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
                    <DialogTrigger asChild>
                      <Button className="gap-2" data-testid="button-create-category">
                        <Plus className="w-4 h-4" />
                        Add Category
                      </Button>
                    </DialogTrigger>
                    <DialogContent>
                      <DialogHeader>
                        <DialogTitle>Create Inspection Category</DialogTitle>
                        <DialogDescription>
                          Add a new category for organizing inspection items
                        </DialogDescription>
                      </DialogHeader>
                      <Form {...createForm}>
                        <form onSubmit={createForm.handleSubmit(onCreateSubmit)} className="space-y-4">
                          <FormField
                            control={createForm.control}
                            name="name"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Category Name</FormLabel>
                                <FormControl>
                                  <Input placeholder="e.g., Kitchen, Bathroom, Living Room" {...field} data-testid="input-category-name" />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={createForm.control}
                            name="description"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Description (Optional)</FormLabel>
                                <FormControl>
                                  <Input placeholder="Brief description of this category" {...field} value={field.value ?? ""} data-testid="input-category-description" />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={createForm.control}
                            name="sortOrder"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Sort Order</FormLabel>
                                <FormControl>
                                  <Input type="number" placeholder="0" {...field} data-testid="input-category-sort-order" />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <DialogFooter>
                            <Button type="submit" disabled={createMutation.isPending} data-testid="button-submit-category">
                              {createMutation.isPending ? "Creating..." : "Create Category"}
                            </Button>
                          </DialogFooter>
                        </form>
                      </Form>
                    </DialogContent>
                  </Dialog>
                </div>
              </CardHeader>
              <CardContent>
                {categoriesLoading ? (
                  <div className="text-center py-12 text-muted-foreground">Loading categories...</div>
                ) : !categories || categories.length === 0 ? (
                  <div className="text-center py-12">
                    <Tags className="w-12 h-12 mx-auto mb-4 text-muted-foreground/40" />
                    <p className="text-muted-foreground">No inspection categories yet</p>
                    <p className="text-sm text-muted-foreground/60 mt-1">
                      Create categories to organize your inspection items
                    </p>
                  </div>
                ) : (
                  <div className="grid gap-4">
                    {categories
                      .sort((a, b) => (a.sortOrder || 0) - (b.sortOrder || 0))
                      .map((category) => (
                        <Card key={category.id} className="bg-muted/30 hover-elevate" data-testid={`category-card-${category.id}`}>
                          <CardContent className="p-4">
                            <div className="flex items-center justify-between">
                              <div className="flex-1">
                                <h3 className="font-semibold text-lg">{category.name}</h3>
                                {category.description && (
                                  <p className="text-sm text-muted-foreground mt-1">{category.description}</p>
                                )}
                                <p className="text-xs text-muted-foreground/60 mt-1">Sort Order: {category.sortOrder || 0}</p>
                              </div>
                              <div className="flex items-center gap-2">
                                <Dialog open={editingCategory?.id === category.id} onOpenChange={(open) => !open && setEditingCategory(null)}>
                                  <DialogTrigger asChild>
                                    <Button variant="ghost" size="sm" onClick={() => setEditingCategory(category)} data-testid={`button-edit-category-${category.id}`}>
                                      <Edit2 className="w-4 h-4" />
                                    </Button>
                                  </DialogTrigger>
                                  <DialogContent>
                                    <DialogHeader>
                                      <DialogTitle>Edit Inspection Category</DialogTitle>
                                      <DialogDescription>
                                        Update the category details
                                      </DialogDescription>
                                    </DialogHeader>
                                    <Form {...editForm}>
                                      <form onSubmit={editForm.handleSubmit(onEditSubmit)} className="space-y-4">
                                        <FormField
                                          control={editForm.control}
                                          name="name"
                                          render={({ field }) => (
                                            <FormItem>
                                              <FormLabel>Category Name</FormLabel>
                                              <FormControl>
                                                <Input {...field} data-testid="input-edit-category-name" />
                                              </FormControl>
                                              <FormMessage />
                                            </FormItem>
                                          )}
                                        />
                                        <FormField
                                          control={editForm.control}
                                          name="description"
                                          render={({ field }) => (
                                            <FormItem>
                                              <FormLabel>Description (Optional)</FormLabel>
                                              <FormControl>
                                                <Input {...field} value={field.value ?? ""} data-testid="input-edit-category-description" />
                                              </FormControl>
                                              <FormMessage />
                                            </FormItem>
                                          )}
                                        />
                                        <FormField
                                          control={editForm.control}
                                          name="sortOrder"
                                          render={({ field }) => (
                                            <FormItem>
                                              <FormLabel>Sort Order</FormLabel>
                                              <FormControl>
                                                <Input type="number" {...field} data-testid="input-edit-category-sort-order" />
                                              </FormControl>
                                              <FormMessage />
                                            </FormItem>
                                          )}
                                        />
                                        <DialogFooter>
                                          <Button type="submit" disabled={updateMutation.isPending} data-testid="button-update-category">
                                            {updateMutation.isPending ? "Updating..." : "Update Category"}
                                          </Button>
                                        </DialogFooter>
                                      </form>
                                    </Form>
                                  </DialogContent>
                                </Dialog>
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  onClick={() => {
                                    if (confirm("Are you sure you want to delete this category?")) {
                                      deleteMutation.mutate(category.id);
                                    }
                                  }}
                                  disabled={deleteMutation.isPending}
                                  data-testid={`button-delete-category-${category.id}`}
                                >
                                  <Trash2 className="w-4 h-4 text-destructive" />
                                </Button>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                      ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="teams">
            <SettingsTeamsPanel />
          </TabsContent>

          <TabsContent value="team">
            <Team />
          </TabsContent>

          <TabsContent value="integrations">
            <FixfloIntegrationSettings />
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

