import { useState, useMemo } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage, FormDescription } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Plus, Edit2, Trash2, FileText, Copy, Eye, Layers, Search, X, ArrowUpDown } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { insertInspectionTemplateSchema, insertTemplateCategorySchema, type InspectionTemplate, type TemplateCategory } from "@shared/schema";
import { z } from "zod";
import { TemplateBuilder } from "../components/TemplateBuilder";

// Form schemas
const templateFormSchema = insertInspectionTemplateSchema.extend({
  name: z.string().min(1, "Template name is required"),
  structureJson: z.any(), // Will be validated by the builder
});

const categoryFormSchema = insertTemplateCategorySchema.extend({
  name: z.string().min(1, "Category name is required"),
});

type TemplateFormValues = z.infer<typeof templateFormSchema>;
type CategoryFormValues = z.infer<typeof categoryFormSchema>;

export default function InspectionTemplates() {
  const { toast } = useToast();
  const [selectedTemplate, setSelectedTemplate] = useState<InspectionTemplate | null>(null);
  const [isBuilderOpen, setIsBuilderOpen] = useState(false);
  const [editingTemplate, setEditingTemplate] = useState<InspectionTemplate | null>(null);
  const [isCategoryDialogOpen, setIsCategoryDialogOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState<string>("");
  const [filterCategory, setFilterCategory] = useState<string>("all");
  const [filterScope, setFilterScope] = useState<string>("all");
  const [filterActive, setFilterActive] = useState<string>("all");
  const [sortBy, setSortBy] = useState<string>("name-asc");

  // Fetch templates (server-side filtering)
  const { data: rawTemplates, isLoading: templatesLoading } = useQuery<InspectionTemplate[]>({
    queryKey: ["/api/inspection-templates", filterCategory, filterScope, filterActive],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (filterCategory !== "all") params.append("categoryId", filterCategory);
      if (filterScope !== "all") params.append("scope", filterScope);
      if (filterActive !== "all") params.append("active", filterActive);
      const query = params.toString();
      const response = await fetch(`/api/inspection-templates${query ? `?${query}` : ""}`, {
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to fetch templates");
      return response.json();
    },
  });

  // Client-side filtering and sorting
  const templates = useMemo(() => {
    if (!rawTemplates) return [];

    // Create a copy to avoid mutating rawTemplates
    let filtered = [...rawTemplates];

    // Apply search filter
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(t => 
        t.name.toLowerCase().includes(query) ||
        (t.description?.toLowerCase().includes(query))
      );
    }

    // Apply sorting (create new sorted array)
    const sorted = [...filtered].sort((a, b) => {
      switch (sortBy) {
        case "name-asc":
          return a.name.localeCompare(b.name);
        case "name-desc":
          return b.name.localeCompare(a.name);
        case "newest": {
          // Fallback to updatedAt if createdAt is missing
          const aTime = new Date(a.createdAt || a.updatedAt || Date.now()).getTime();
          const bTime = new Date(b.createdAt || b.updatedAt || Date.now()).getTime();
          return bTime - aTime;
        }
        case "oldest": {
          // Fallback to updatedAt if createdAt is missing
          const aTime = new Date(a.createdAt || a.updatedAt || Date.now()).getTime();
          const bTime = new Date(b.createdAt || b.updatedAt || Date.now()).getTime();
          return aTime - bTime;
        }
        case "version":
          return (b.version || 1) - (a.version || 1);
        default:
          return 0;
      }
    });

    return sorted;
  }, [rawTemplates, searchQuery, sortBy]);

  // Fetch categories
  const { data: categories } = useQuery<TemplateCategory[]>({
    queryKey: ["/api/template-categories"],
  });

  // Category form
  const categoryForm = useForm<CategoryFormValues>({
    resolver: zodResolver(categoryFormSchema),
    defaultValues: {
      name: "",
      description: "",
      color: "#5AB5E8",
    },
  });

  // Create category mutation
  const createCategoryMutation = useMutation({
    mutationFn: async (data: CategoryFormValues) => {
      return await apiRequest("POST", "/api/template-categories", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/template-categories"] });
      toast({
        title: "Success",
        description: "Category created successfully",
      });
      setIsCategoryDialogOpen(false);
      categoryForm.reset();
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to create category",
      });
    },
  });

  // Delete template mutation
  const deleteTemplateMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/inspection-templates/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/inspection-templates"] });
      toast({
        title: "Success",
        description: "Template deleted successfully",
      });
      if (selectedTemplate) {
        setSelectedTemplate(null);
      }
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to delete template",
      });
    },
  });

  // Clone template mutation
  const cloneTemplateMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("POST", `/api/inspection-templates/${id}/clone`, {
        name: undefined, // Let backend auto-generate name
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/inspection-templates"] });
      toast({
        title: "Success",
        description: "Template cloned successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to clone template",
      });
    },
  });

  // Toggle active mutation
  const toggleActiveMutation = useMutation({
    mutationFn: async ({ id, isActive }: { id: string; isActive: boolean }) => {
      return await apiRequest("PUT", `/api/inspection-templates/${id}`, { isActive });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/inspection-templates"] });
      toast({
        title: "Success",
        description: "Template status updated",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to update template",
      });
    },
  });

  const handleCreateTemplate = () => {
    setEditingTemplate(null);
    setIsBuilderOpen(true);
  };

  const handleEditTemplate = (template: InspectionTemplate) => {
    setEditingTemplate(template);
    setIsBuilderOpen(true);
  };

  const handleCloseBuilder = () => {
    setIsBuilderOpen(false);
    setEditingTemplate(null);
  };

  const handleBuilderSave = () => {
    // Cache invalidation is handled by TemplateBuilder
    handleCloseBuilder();
  };

  const clearAllFilters = () => {
    setSearchQuery("");
    setFilterCategory("all");
    setFilterScope("all");
    setFilterActive("all");
    setSortBy("name-asc");
  };

  const hasActiveFilters = searchQuery.trim() || filterCategory !== "all" || filterScope !== "all" || filterActive !== "all" || sortBy !== "name-asc";

  return (
    <div className="container mx-auto p-8 space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-4xl font-semibold tracking-tight">Inspection Templates</h1>
          <p className="text-muted-foreground mt-2">
            Create reusable inspection templates with flexible JSON-based structures
          </p>
        </div>
        <div className="flex items-center gap-3">
          <Button
            variant="outline"
            onClick={() => setIsCategoryDialogOpen(true)}
            data-testid="button-manage-categories"
          >
            <Layers className="w-4 h-4 mr-2" />
            Categories
          </Button>
          <Button
            onClick={handleCreateTemplate}
            size="lg"
            data-testid="button-create-template"
          >
            <Plus className="w-5 h-5 mr-2" />
            New Template
          </Button>
        </div>
      </div>

      {/* Filters */}
      <Card className="shadow-sm rounded-xl">
        <CardContent className="p-6">
          <div className="space-y-4">
            {/* Search Bar */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                placeholder="Search templates by name or description..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-9 pr-9"
                data-testid="input-search-templates"
              />
              {searchQuery && (
                <Button
                  variant="ghost"
                  size="icon"
                  className="absolute right-1 top-1/2 -translate-y-1/2 h-7 w-7"
                  onClick={() => setSearchQuery("")}
                  data-testid="button-clear-search"
                >
                  <X className="w-4 h-4" />
                </Button>
              )}
            </div>

            {/* Filter Controls Row */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div>
                <label className="text-sm font-medium mb-2 block">Category</label>
                <Select value={filterCategory} onValueChange={setFilterCategory}>
                  <SelectTrigger data-testid="select-filter-category">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Categories</SelectItem>
                    {categories?.map((cat) => (
                      <SelectItem key={cat.id} value={cat.id}>
                        {cat.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm font-medium mb-2 block">Scope</label>
                <Select value={filterScope} onValueChange={setFilterScope}>
                  <SelectTrigger data-testid="select-filter-scope">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Scopes</SelectItem>
                    <SelectItem value="property">Property</SelectItem>
                    <SelectItem value="block">Block</SelectItem>
                    <SelectItem value="both">Both</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm font-medium mb-2 block">Status</label>
                <Select value={filterActive} onValueChange={setFilterActive}>
                  <SelectTrigger data-testid="select-filter-status">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="true">Active Only</SelectItem>
                    <SelectItem value="false">Inactive Only</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm font-medium mb-2 block">Sort By</label>
                <Select value={sortBy} onValueChange={setSortBy}>
                  <SelectTrigger data-testid="select-sort-by">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="name-asc">Name (A-Z)</SelectItem>
                    <SelectItem value="name-desc">Name (Z-A)</SelectItem>
                    <SelectItem value="newest">Newest First</SelectItem>
                    <SelectItem value="oldest">Oldest First</SelectItem>
                    <SelectItem value="version">Highest Version</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            {/* Results Count and Clear Filters */}
            <div className="flex items-center justify-between pt-2">
              <div className="text-sm text-muted-foreground">
                {templatesLoading ? (
                  "Loading..."
                ) : (
                  <span data-testid="text-result-count">
                    Showing <span className="font-semibold text-foreground">{templates?.length || 0}</span> {templates?.length === 1 ? "template" : "templates"}
                    {rawTemplates && templates && rawTemplates.length !== templates.length && (
                      <span className="ml-1">(filtered from {rawTemplates.length})</span>
                    )}
                  </span>
                )}
              </div>
              {hasActiveFilters && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={clearAllFilters}
                  data-testid="button-clear-filters"
                >
                  <X className="w-4 h-4 mr-2" />
                  Clear Filters
                </Button>
              )}
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Templates Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {templatesLoading ? (
          // Loading skeletons
          Array.from({ length: 6 }).map((_, i) => (
            <Card key={i} className="shadow-sm rounded-xl">
              <CardHeader>
                <Skeleton className="h-6 w-3/4 mb-2" />
                <div className="flex gap-2 mt-2">
                  <Skeleton className="h-5 w-16" />
                  <Skeleton className="h-5 w-16" />
                  <Skeleton className="h-5 w-12" />
                </div>
                <Skeleton className="h-10 w-full mt-2" />
              </CardHeader>
              <CardContent>
                <Skeleton className="h-9 w-full" />
              </CardContent>
            </Card>
          ))
        ) : !templates || templates.length === 0 ? (
          <Card className="col-span-full shadow-sm rounded-xl">
            <CardContent className="text-center py-12">
              <FileText className="w-16 h-16 mx-auto text-muted-foreground mb-4" />
              {hasActiveFilters ? (
                <>
                  <h3 className="text-lg font-semibold mb-2">No templates match your filters</h3>
                  <p className="text-muted-foreground mb-6">
                    Try adjusting your search or filter criteria
                  </p>
                  <Button variant="outline" onClick={clearAllFilters} data-testid="button-clear-filters-empty">
                    <X className="w-4 h-4 mr-2" />
                    Clear All Filters
                  </Button>
                </>
              ) : (
                <>
                  <h3 className="text-lg font-semibold mb-2">No templates yet</h3>
                  <p className="text-muted-foreground mb-6">
                    Create your first inspection template to get started
                  </p>
                  <Button onClick={handleCreateTemplate} data-testid="button-create-first-template">
                    <Plus className="w-4 h-4 mr-2" />
                    Create Template
                  </Button>
                </>
              )}
            </CardContent>
          </Card>
        ) : (
          templates.map((template) => {
            const category = categories?.find((c) => c.id === template.categoryId);
            return (
              <Card
                key={template.id}
                className="shadow-sm rounded-xl hover-elevate transition-all"
                data-testid={`card-template-${template.id}`}
              >
                <CardHeader>
                  <div className="flex items-start justify-between gap-2">
                    <div className="flex-1 min-w-0">
                      <CardTitle className="text-lg truncate" data-testid={`text-template-name-${template.id}`}>
                        {template.name}
                      </CardTitle>
                      <div className="flex items-center gap-2 mt-2">
                        {category && (
                          <Badge variant="outline" style={{ borderColor: category.color || "#5AB5E8" }}>
                            {category.name}
                          </Badge>
                        )}
                        <Badge variant={template.scope === "both" ? "default" : "secondary"}>
                          {template.scope}
                        </Badge>
                        <Badge variant="outline">v{template.version}</Badge>
                      </div>
                    </div>
                    <Switch
                      checked={template.isActive ?? true}
                      onCheckedChange={(checked) => {
                        toggleActiveMutation.mutate({ id: template.id, isActive: checked });
                      }}
                      data-testid={`switch-active-${template.id}`}
                    />
                  </div>
                  {template.description && (
                    <CardDescription className="line-clamp-2 mt-2">
                      {template.description}
                    </CardDescription>
                  )}
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="flex items-center gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      className="flex-1"
                      onClick={() => handleEditTemplate(template)}
                      data-testid={`button-edit-template-${template.id}`}
                    >
                      <Edit2 className="w-4 h-4 mr-2" />
                      Edit
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => cloneTemplateMutation.mutate(template.id)}
                      data-testid={`button-clone-template-${template.id}`}
                    >
                      <Copy className="w-4 h-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        if (confirm("Are you sure you want to delete this template?")) {
                          deleteTemplateMutation.mutate(template.id);
                        }
                      }}
                      data-testid={`button-delete-template-${template.id}`}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            );
          })
        )}
      </div>

      {/* Template Builder Dialog */}
      {isBuilderOpen && (
        <TemplateBuilder
          template={editingTemplate}
          categories={categories || []}
          onClose={handleCloseBuilder}
          onSave={handleBuilderSave}
        />
      )}

      {/* Category Dialog */}
      <Dialog open={isCategoryDialogOpen} onOpenChange={setIsCategoryDialogOpen}>
        <DialogContent data-testid="dialog-category-form">
          <DialogHeader>
            <DialogTitle>Manage Categories</DialogTitle>
            <DialogDescription>
              Create and organize template categories
            </DialogDescription>
          </DialogHeader>
          <Form {...categoryForm}>
            <form onSubmit={categoryForm.handleSubmit((data) => createCategoryMutation.mutate(data))} className="space-y-4">
              <FormField
                control={categoryForm.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Category Name</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="e.g., Move-In, Move-Out, Routine"
                        {...field}
                        data-testid="input-category-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={categoryForm.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Description (Optional)</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Describe this category..."
                        {...field}
                        value={field.value || ""}
                        data-testid="input-category-description"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={categoryForm.control}
                name="color"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Color</FormLabel>
                    <FormControl>
                      <Input
                        type="color"
                        {...field}
                        value={field.value || "#5AB5E8"}
                        data-testid="input-category-color"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <DialogFooter>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsCategoryDialogOpen(false)}
                  data-testid="button-cancel-category"
                >
                  Close
                </Button>
                <Button
                  type="submit"
                  disabled={createCategoryMutation.isPending}
                  data-testid="button-save-category"
                >
                  {createCategoryMutation.isPending ? "Creating..." : "Create Category"}
                </Button>
              </DialogFooter>
            </form>
          </Form>

          {/* Existing Categories List */}
          {categories && categories.length > 0 && (
            <div className="mt-6 space-y-2">
              <h4 className="font-medium text-sm">Existing Categories</h4>
              <div className="space-y-2">
                {categories.map((cat) => (
                  <div
                    key={cat.id}
                    className="flex items-center gap-3 p-3 rounded-lg bg-muted"
                  >
                    <div
                      className="w-4 h-4 rounded-full"
                      style={{ backgroundColor: cat.color || "#5AB5E8" }}
                    />
                    <div className="flex-1 min-w-0">
                      <p className="font-medium text-sm">{cat.name}</p>
                      {cat.description && (
                        <p className="text-xs text-muted-foreground truncate">
                          {cat.description}
                        </p>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/Contacts.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Users, Plus, Search, Mail, Phone, Building2, Briefcase, Globe, MapPin, Trash2, Edit, Tag, X } from "lucide-react";
import type { Contact, Tag as TagType } from "@shared/schema";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";

type ContactWithTags = Omit<Contact, 'tags'> & { tags?: TagType[] };

const contactTypeLabels: Record<string, string> = {
  internal: "Internal",
  contractor: "Contractor",
  lead: "Lead",
  company: "Company",
  partner: "Partner",
  vendor: "Vendor",
  other: "Other",
};

const contactTypeBadgeVariants: Record<string, "default" | "secondary" | "outline"> = {
  internal: "default",
  contractor: "secondary",
  lead: "outline",
  company: "default",
  partner: "default",
  vendor: "secondary",
  other: "outline",
};

export default function Contacts() {
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [filterType, setFilterType] = useState<string>("all");
  const [filterTag, setFilterTag] = useState<string>("all");
  const [dialogOpen, setDialogOpen] = useState(false);
  const [editingContact, setEditingContact] = useState<ContactWithTags | null>(null);
  const [selectedTags, setSelectedTags] = useState<string[]>([]);
  const [newTagName, setNewTagName] = useState("");

  const { data: contacts, isLoading } = useQuery<ContactWithTags[]>({
    queryKey: ["/api/contacts"],
  });

  const { data: allTags } = useQuery<TagType[]>({
    queryKey: ["/api/tags"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      const res = await apiRequest("POST", "/api/contacts", data);
      return await res.json();
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to save contact",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const res = await apiRequest("PATCH", `/api/contacts/${id}`, data);
      return await res.json();
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to update contact",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/contacts/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
      toast({
        title: "Success",
        description: "Contact deleted successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to delete contact",
      });
    },
  });

  const addTagMutation = useMutation({
    mutationFn: async ({ contactId, tagId }: { contactId: string; tagId: string }) => {
      return await apiRequest("POST", `/api/contacts/${contactId}/tags/${tagId}`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
    },
  });

  const removeTagMutation = useMutation({
    mutationFn: async ({ contactId, tagId }: { contactId: string; tagId: string }) => {
      return await apiRequest("DELETE", `/api/contacts/${contactId}/tags/${tagId}`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
    },
  });

  const createTagMutation = useMutation({
    mutationFn: async (data: { name: string; color?: string }) => {
      const res = await apiRequest("POST", "/api/tags", data);
      return await res.json() as TagType;
    },
    onSuccess: async (newTag: TagType) => {
      await queryClient.refetchQueries({ queryKey: ["/api/tags"] });
      setSelectedTags(prev => [...prev, newTag.id]);
      setNewTagName("");
      toast({
        title: "Success",
        description: "Tag created successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to create tag",
      });
    },
  });

  const handleCreateTag = () => {
    if (!newTagName.trim()) {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Please enter a tag name",
      });
      return;
    }

    // Generate a random color for the new tag
    const colors = ["#00D5CC", "#3B7A8C", "#10b981", "#f59e0b", "#ef4444", "#8b5cf6", "#ec4899"];
    const randomColor = colors[Math.floor(Math.random() * colors.length)];

    createTagMutation.mutate({ name: newTagName.trim(), color: randomColor });
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);

    const data = {
      type: formData.get("type") as string,
      firstName: formData.get("firstName") as string,
      lastName: formData.get("lastName") as string,
      email: formData.get("email") as string || undefined,
      phone: formData.get("phone") as string || undefined,
      countryCode: formData.get("countryCode") as string || "+1",
      companyName: formData.get("companyName") as string || undefined,
      jobTitle: formData.get("jobTitle") as string || undefined,
      address: formData.get("address") as string || undefined,
      city: formData.get("city") as string || undefined,
      state: formData.get("state") as string || undefined,
      postalCode: formData.get("postalCode") as string || undefined,
      country: formData.get("country") as string || undefined,
      website: formData.get("website") as string || undefined,
      notes: formData.get("notes") as string || undefined,
    };

    try {
      let contactId: string;

      if (editingContact) {
        await updateMutation.mutateAsync({ id: editingContact.id, data });
        contactId = editingContact.id;

        // Update tags: remove old tags, add new tags
        const oldTagIds = editingContact.tags?.map(t => t.id) || [];
        const tagsToRemove = oldTagIds.filter(id => !selectedTags.includes(id));
        const tagsToAdd = selectedTags.filter(id => !oldTagIds.includes(id));

        for (const tagId of tagsToRemove) {
          await removeTagMutation.mutateAsync({ contactId, tagId });
        }
        for (const tagId of tagsToAdd) {
          await addTagMutation.mutateAsync({ contactId, tagId });
        }
      } else {
        const result = await createMutation.mutateAsync(data);
        contactId = result.id;

        // Add tags to new contact
        for (const tagId of selectedTags) {
          await addTagMutation.mutateAsync({ contactId, tagId });
        }
      }

      // Invalidate and refetch contacts to ensure fresh data
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
      await queryClient.refetchQueries({ queryKey: ["/api/contacts"] });

      setDialogOpen(false);
      setEditingContact(null);
      setSelectedTags([]);

      toast({
        title: "Success",
        description: editingContact ? "Contact updated successfully" : "Contact created successfully",
      });
    } catch (error) {
      // Error handling is done in mutations
    }
  };

  const filteredContacts = contacts?.filter((contact) => {
    const matchesSearch =
      searchTerm === "" ||
      `${contact.firstName} ${contact.lastName}`.toLowerCase().includes(searchTerm.toLowerCase()) ||
      contact.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      contact.companyName?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesType = filterType === "all" || contact.type === filterType;

    const matchesTag = filterTag === "all" || contact.tags?.some(tag => tag.id === filterTag);

    return matchesSearch && matchesType && matchesTag;
  });

  const getInitials = (firstName: string, lastName: string) => {
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase();
  };

  if (isLoading) {
    return (
      <div className="p-8">
        <div className="max-w-6xl mx-auto">
          <div className="flex items-center gap-3 mb-6">
            <Users className="w-8 h-8 text-primary" />
            <div>
              <h1 className="text-3xl font-semibold">Contacts</h1>
              <p className="text-muted-foreground">Loading contacts...</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-8">
      <div className="max-w-6xl mx-auto space-y-6">
        <div className="flex items-center justify-between gap-3">
          <div className="flex items-center gap-3">
            <Users className="w-8 h-8 text-primary" />
            <div>
              <h1 className="text-3xl font-semibold">Contacts</h1>
              <p className="text-muted-foreground">
                Manage internal team members and external contacts
              </p>
            </div>
          </div>

          <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
            <DialogTrigger asChild>
              <Button
                onClick={() => setEditingContact(null)}
                data-testid="button-add-contact"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Contact
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>
                  {editingContact ? "Edit Contact" : "Add New Contact"}
                </DialogTitle>
                <DialogDescription>
                  {editingContact
                    ? "Update contact information"
                    : "Create a new contact entry"}
                </DialogDescription>
              </DialogHeader>

              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="type">Contact Type *</Label>
                    <Select
                      name="type"
                      defaultValue={editingContact?.type || "other"}
                      required
                    >
                      <SelectTrigger data-testid="select-contact-type">
                        <SelectValue placeholder="Select type" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="internal">Internal</SelectItem>
                        <SelectItem value="contractor">Contractor</SelectItem>
                        <SelectItem value="lead">Lead</SelectItem>
                        <SelectItem value="company">Company</SelectItem>
                        <SelectItem value="partner">Partner</SelectItem>
                        <SelectItem value="vendor">Vendor</SelectItem>
                        <SelectItem value="other">Other</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <Label htmlFor="companyName">Company Name</Label>
                    <Input
                      id="companyName"
                      name="companyName"
                      defaultValue={editingContact?.companyName || ""}
                      placeholder="Acme Corporation"
                      data-testid="input-company-name"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="firstName">First Name *</Label>
                    <Input
                      id="firstName"
                      name="firstName"
                      defaultValue={editingContact?.firstName || ""}
                      required
                      placeholder="John"
                      data-testid="input-first-name"
                    />
                  </div>

                  <div>
                    <Label htmlFor="lastName">Last Name *</Label>
                    <Input
                      id="lastName"
                      name="lastName"
                      defaultValue={editingContact?.lastName || ""}
                      required
                      placeholder="Doe"
                      data-testid="input-last-name"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="email">Email</Label>
                    <Input
                      id="email"
                      name="email"
                      type="email"
                      defaultValue={editingContact?.email || ""}
                      placeholder="john@example.com"
                      data-testid="input-email"
                    />
                  </div>

                  <div>
                    <Label htmlFor="jobTitle">Job Title</Label>
                    <Input
                      id="jobTitle"
                      name="jobTitle"
                      defaultValue={editingContact?.jobTitle || ""}
                      placeholder="Property Manager"
                      data-testid="input-job-title"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-3 gap-4">
                  <div>
                    <Label htmlFor="countryCode">Country Code</Label>
                    <Input
                      id="countryCode"
                      name="countryCode"
                      defaultValue={editingContact?.countryCode || "+1"}
                      placeholder="+1"
                      data-testid="input-country-code"
                    />
                  </div>

                  <div className="col-span-2">
                    <Label htmlFor="phone">Phone Number</Label>
                    <Input
                      id="phone"
                      name="phone"
                      type="tel"
                      defaultValue={editingContact?.phone || ""}
                      placeholder="(555) 123-4567"
                      data-testid="input-phone"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="address">Address</Label>
                  <Input
                    id="address"
                    name="address"
                    defaultValue={editingContact?.address || ""}
                    placeholder="123 Main Street"
                    data-testid="input-address"
                  />
                </div>

                <div className="grid grid-cols-3 gap-4">
                  <div>
                    <Label htmlFor="city">City</Label>
                    <Input
                      id="city"
                      name="city"
                      defaultValue={editingContact?.city || ""}
                      placeholder="New York"
                      data-testid="input-city"
                    />
                  </div>

                  <div>
                    <Label htmlFor="state">State</Label>
                    <Input
                      id="state"
                      name="state"
                      defaultValue={editingContact?.state || ""}
                      placeholder="NY"
                      data-testid="input-state"
                    />
                  </div>

                  <div>
                    <Label htmlFor="postalCode">Postal Code</Label>
                    <Input
                      id="postalCode"
                      name="postalCode"
                      defaultValue={editingContact?.postalCode || ""}
                      placeholder="10001"
                      data-testid="input-postal-code"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="country">Country</Label>
                    <Input
                      id="country"
                      name="country"
                      defaultValue={editingContact?.country || ""}
                      placeholder="United States"
                      data-testid="input-country"
                    />
                  </div>

                  <div>
                    <Label htmlFor="website">Website</Label>
                    <Input
                      id="website"
                      name="website"
                      type="url"
                      defaultValue={editingContact?.website || ""}
                      placeholder="https://example.com"
                      data-testid="input-website"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="notes">Notes</Label>
                  <Textarea
                    id="notes"
                    name="notes"
                    defaultValue={editingContact?.notes || ""}
                    placeholder="Additional notes or information"
                    data-testid="input-notes"
                    rows={3}
                  />
                </div>

                <div className="space-y-2">
                  <Label>Tags</Label>
                  <div className="flex flex-wrap gap-2 mb-2">
                    {selectedTags.map((tagId) => {
                      const tag = allTags?.find(t => t.id === tagId);
                      if (!tag) return null;
                      return (
                        <Badge
                          key={tagId}
                          variant="outline"
                          className="gap-1"
                          style={{ borderColor: tag.color || undefined }}
                          data-testid={`badge-selected-tag-${tagId}`}
                        >
                          {tag.name}
                          <X
                            className="w-3 h-3 cursor-pointer hover:text-destructive"
                            onClick={() => setSelectedTags(prev => prev.filter(id => id !== tagId))}
                          />
                        </Badge>
                      );
                    })}
                  </div>
                  <Select
                    value=""
                    onValueChange={(value) => {
                      if (value && !selectedTags.includes(value)) {
                        setSelectedTags(prev => [...prev, value]);
                      }
                    }}
                  >
                    <SelectTrigger data-testid="select-add-tag">
                      <SelectValue placeholder="Add existing tag..." />
                    </SelectTrigger>
                    <SelectContent>
                      {allTags?.filter(tag => !selectedTags.includes(tag.id)).map((tag) => (
                        <SelectItem key={tag.id} value={tag.id}>
                          {tag.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>

                  <div className="flex gap-2 items-end pt-2">
                    <div className="flex-1">
                      <Label htmlFor="new-tag-name" className="text-sm text-muted-foreground">
                        Or create new tag
                      </Label>
                      <Input
                        id="new-tag-name"
                        value={newTagName}
                        onChange={(e) => setNewTagName(e.target.value)}
                        placeholder="Enter new tag name..."
                        onKeyDown={(e) => {
                          if (e.key === "Enter") {
                            e.preventDefault();
                            handleCreateTag();
                          }
                        }}
                        data-testid="input-new-tag-name"
                      />
                    </div>
                    <Button
                      type="button"
                      onClick={handleCreateTag}
                      disabled={createTagMutation.isPending || !newTagName.trim()}
                      data-testid="button-create-tag"
                    >
                      <Plus className="w-4 h-4 mr-2" />
                      Create Tag
                    </Button>
                  </div>
                </div>

                <DialogFooter>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      setDialogOpen(false);
                      setEditingContact(null);
                    }}
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    disabled={createMutation.isPending || updateMutation.isPending}
                    data-testid="button-submit-contact"
                  >
                    {editingContact ? "Update Contact" : "Create Contact"}
                  </Button>
                </DialogFooter>
              </form>
            </DialogContent>
          </Dialog>
        </div>

        <div className="flex gap-4 items-center">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
            <Input
              placeholder="Search contacts by name, email, or company..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-9"
              data-testid="input-search-contacts"
            />
          </div>

          <Select value={filterType} onValueChange={setFilterType}>
            <SelectTrigger className="w-48" data-testid="select-filter-type">
              <SelectValue placeholder="Filter by type" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Types</SelectItem>
              <SelectItem value="internal">Internal</SelectItem>
              <SelectItem value="contractor">Contractor</SelectItem>
              <SelectItem value="lead">Lead</SelectItem>
              <SelectItem value="company">Company</SelectItem>
              <SelectItem value="partner">Partner</SelectItem>
              <SelectItem value="vendor">Vendor</SelectItem>
              <SelectItem value="other">Other</SelectItem>
            </SelectContent>
          </Select>

          <Select value={filterTag} onValueChange={setFilterTag}>
            <SelectTrigger className="w-48" data-testid="select-filter-tag">
              <SelectValue placeholder="Filter by tag" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Tags</SelectItem>
              {allTags?.map((tag) => (
                <SelectItem key={tag.id} value={tag.id}>
                  {tag.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        {!filteredContacts || filteredContacts.length === 0 ? (
          <Card>
            <CardContent className="p-12 text-center">
              <Users className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-semibold mb-2">
                {searchTerm || filterType !== "all" ? "No Contacts Found" : "No Contacts Yet"}
              </h3>
              <p className="text-muted-foreground mb-4">
                {searchTerm || filterType !== "all"
                  ? "Try adjusting your search or filters"
                  : "Get started by adding your first contact"}
              </p>
              {!searchTerm && filterType === "all" && (
                <Button onClick={() => setDialogOpen(true)} data-testid="button-add-first-contact">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Your First Contact
                </Button>
              )}
            </CardContent>
          </Card>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredContacts.map((contact) => (
              <Card key={contact.id} className="hover-elevate" data-testid={`card-contact-${contact.id}`}>
                <CardContent className="p-6 space-y-4">
                  <div className="flex items-start justify-between gap-3">
                    <div className="flex items-center gap-3 flex-1 min-w-0">
                      <Avatar>
                        <AvatarImage src={contact.profileImageUrl || undefined} />
                        <AvatarFallback className="bg-primary/10 text-primary">
                          {getInitials(contact.firstName, contact.lastName)}
                        </AvatarFallback>
                      </Avatar>
                      <div className="flex-1 min-w-0">
                        <h3 className="font-semibold truncate" data-testid={`text-name-${contact.id}`}>
                          {contact.firstName} {contact.lastName}
                        </h3>
                        <Badge
                          variant={contactTypeBadgeVariants[contact.type]}
                          className="mt-1"
                          data-testid={`badge-type-${contact.id}`}
                        >
                          {contactTypeLabels[contact.type]}
                        </Badge>
                      </div>
                    </div>

                    <div className="flex gap-1">
                      <Button
                        size="icon"
                        variant="ghost"
                        onClick={() => {
                          setEditingContact(contact);
                          setSelectedTags(contact.tags?.map(t => t.id) || []);
                          setDialogOpen(true);
                        }}
                        data-testid={`button-edit-${contact.id}`}
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        size="icon"
                        variant="ghost"
                        onClick={() => {
                          if (confirm("Are you sure you want to delete this contact?")) {
                            deleteMutation.mutate(contact.id);
                          }
                        }}
                        data-testid={`button-delete-${contact.id}`}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>

                  <div className="space-y-2 text-sm">
                    {contact.companyName && (
                      <div className="flex items-center gap-2 text-muted-foreground">
                        <Building2 className="w-4 h-4 shrink-0" />
                        <span className="truncate">{contact.companyName}</span>
                      </div>
                    )}

                    {contact.jobTitle && (
                      <div className="flex items-center gap-2 text-muted-foreground">
                        <Briefcase className="w-4 h-4 shrink-0" />
                        <span className="truncate">{contact.jobTitle}</span>
                      </div>
                    )}

                    {contact.email && (
                      <div className="flex items-center gap-2 text-muted-foreground">
                        <Mail className="w-4 h-4 shrink-0" />
                        <span className="truncate" data-testid={`text-email-${contact.id}`}>
                          {contact.email}
                        </span>
                      </div>
                    )}

                    {contact.phone && (
                      <div className="flex items-center gap-2 text-muted-foreground">
                        <Phone className="w-4 h-4 shrink-0" />
                        <span className="truncate" data-testid={`text-phone-${contact.id}`}>
                          {contact.countryCode} {contact.phone}
                        </span>
                      </div>
                    )}

                    {contact.city && contact.state && (
                      <div className="flex items-center gap-2 text-muted-foreground">
                        <MapPin className="w-4 h-4 shrink-0" />
                        <span className="truncate">
                          {contact.city}, {contact.state}
                        </span>
                      </div>
                    )}

                    {contact.website && (
                      <div className="flex items-center gap-2 text-muted-foreground">
                        <Globe className="w-4 h-4 shrink-0" />
                        <a
                          href={contact.website}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="truncate hover:text-primary transition-colors"
                        >
                          {contact.website}
                        </a>
                      </div>
                    )}
                  </div>

                  {contact.tags && contact.tags.length > 0 && (
                    <div className="flex flex-wrap gap-1.5 pt-2 border-t">
                      {contact.tags.map((tag) => (
                        <Badge
                          key={tag.id}
                          variant="secondary"
                          className="text-xs"
                          style={{
                            borderColor: tag.color || undefined,
                            backgroundColor: tag.color ? `${tag.color}15` : undefined,
                          }}
                          data-testid={`badge-tag-${tag.id}`}
                        >
                          <Tag className="w-3 h-3 mr-1" />
                          {tag.name}
                        </Badge>
                      ))}
                    </div>
                  )}
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

