import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Search, Building, Home, Users, FileText, Package, Wrench, Tag as TagIcon, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { ScrollArea } from "@/components/ui/scroll-area";
import { apiRequest } from "@/lib/queryClient";
import { useLocation } from "wouter";
import type { Tag } from "@shared/schema";

interface TagSearchProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

interface SearchResults {
  blocks: any[];
  properties: any[];
  users: any[];
  complianceDocuments: any[];
  assetInventory: any[];
  maintenanceRequests: any[];
}

export function TagSearch({ open, onOpenChange }: TagSearchProps) {
  const [, setLocation] = useLocation();
  const [selectedTags, setSelectedTags] = useState<Tag[]>([]);
  const [searchResults, setSearchResults] = useState<SearchResults | null>(null);

  const { data: allTags = [] } = useQuery<Tag[]>({
    queryKey: ["/api/tags"],
  });

  const searchMutation = useMutation({
    mutationFn: async (tagIds: string[]) => {
      const res = await apiRequest("POST", "/api/tags/search", { tagIds });
      return res.json();
    },
    onSuccess: (data) => {
      setSearchResults(data);
    },
  });

  const handleTagSelect = (tag: Tag) => {
    if (selectedTags.find(t => t.id === tag.id)) {
      const newTags = selectedTags.filter(t => t.id !== tag.id);
      setSelectedTags(newTags);
      if (newTags.length > 0) {
        searchMutation.mutate(newTags.map(t => t.id));
      } else {
        setSearchResults(null);
      }
    } else {
      const newTags = [...selectedTags, tag];
      setSelectedTags(newTags);
      searchMutation.mutate(newTags.map(t => t.id));
    }
  };

  const handleClear = () => {
    setSelectedTags([]);
    setSearchResults(null);
  };

  const handleNavigate = (path: string) => {
    setLocation(path);
    onOpenChange(false);
    handleClear();
  };

  const getTotalResults = () => {
    if (!searchResults) return 0;
    return (
      searchResults.blocks.length +
      searchResults.properties.length +
      searchResults.users.length +
      searchResults.complianceDocuments.length +
      searchResults.assetInventory.length +
      searchResults.maintenanceRequests.length
    );
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-3xl max-h-[80vh]">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Search className="h-5 w-5" />
            Search by Tags
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Tag Selection */}
          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <label className="text-sm font-medium">Select Tags:</label>
              {selectedTags.length > 0 && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleClear}
                  data-testid="button-clear-tags"
                >
                  Clear
                </Button>
              )}
            </div>
            <div className="flex flex-wrap gap-2 p-4 border rounded-lg min-h-[60px]">
              {allTags.map(tag => (
                <Badge
                  key={tag.id}
                  variant={selectedTags.find(t => t.id === tag.id) ? "default" : "outline"}
                  className="cursor-pointer gap-1"
                  style={{
                    backgroundColor: selectedTags.find(t => t.id === tag.id) ? tag.color || undefined : undefined,
                    borderColor: tag.color || undefined,
                  }}
                  onClick={() => handleTagSelect(tag)}
                  data-testid={`tag-filter-${tag.id}`}
                >
                  <TagIcon className="h-3 w-3" />
                  {tag.name}
                </Badge>
              ))}
              {allTags.length === 0 && (
                <p className="text-sm text-muted-foreground">No tags available. Create tags in the Properties, Blocks, or other sections.</p>
              )}
            </div>
          </div>

          {/* Search Results */}
          {searchMutation.isPending && (
            <div className="text-center py-8 text-muted-foreground">
              Searching...
            </div>
          )}

          {searchResults && (
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <h3 className="font-medium">
                  Results ({getTotalResults()})
                </h3>
              </div>

              <ScrollArea className="h-[400px] border rounded-lg p-4">
                <div className="space-y-6">
                  {/* Blocks */}
                  {searchResults.blocks.length > 0 && (
                    <div className="space-y-2">
                      <div className="flex items-center gap-2 text-sm font-medium text-muted-foreground">
                        <Building className="h-4 w-4" />
                        Blocks ({searchResults.blocks.length})
                      </div>
                      <div className="space-y-1">
                        {searchResults.blocks.map(block => (
                          <button
                            key={block.id}
                            onClick={() => handleNavigate(`/blocks`)}
                            className="w-full flex items-center justify-between p-2 hover-elevate rounded-md text-left"
                            data-testid={`result-block-${block.id}`}
                          >
                            <div>
                              <p className="font-medium">{block.name}</p>
                              <p className="text-sm text-muted-foreground">{block.address}</p>
                            </div>
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Properties */}
                  {searchResults.properties.length > 0 && (
                    <div className="space-y-2">
                      <div className="flex items-center gap-2 text-sm font-medium text-muted-foreground">
                        <Home className="h-4 w-4" />
                        Properties ({searchResults.properties.length})
                      </div>
                      <div className="space-y-1">
                        {searchResults.properties.map(property => (
                          <button
                            key={property.id}
                            onClick={() => handleNavigate(`/properties`)}
                            className="w-full flex items-center justify-between p-2 hover-elevate rounded-md text-left"
                            data-testid={`result-property-${property.id}`}
                          >
                            <div>
                              <p className="font-medium">{property.name}</p>
                              <p className="text-sm text-muted-foreground">{property.address}</p>
                            </div>
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Users/Tenants */}
                  {searchResults.users.length > 0 && (
                    <div className="space-y-2">
                      <div className="flex items-center gap-2 text-sm font-medium text-muted-foreground">
                        <Users className="h-4 w-4" />
                        Team Members ({searchResults.users.length})
                      </div>
                      <div className="space-y-1">
                        {searchResults.users.map(user => (
                          <button
                            key={user.id}
                            onClick={() => handleNavigate(`/team`)}
                            className="w-full flex items-center justify-between p-2 hover-elevate rounded-md text-left"
                            data-testid={`result-user-${user.id}`}
                          >
                            <div>
                              <p className="font-medium">{user.firstName} {user.lastName}</p>
                              <p className="text-sm text-muted-foreground">{user.email}  {user.role}</p>
                            </div>
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Compliance Documents */}
                  {searchResults.complianceDocuments.length > 0 && (
                    <div className="space-y-2">
                      <div className="flex items-center gap-2 text-sm font-medium text-muted-foreground">
                        <FileText className="h-4 w-4" />
                        Compliance Documents ({searchResults.complianceDocuments.length})
                      </div>
                      <div className="space-y-1">
                        {searchResults.complianceDocuments.map(doc => (
                          <button
                            key={doc.id}
                            onClick={() => handleNavigate(`/compliance`)}
                            className="w-full flex items-center justify-between p-2 hover-elevate rounded-md text-left"
                            data-testid={`result-compliance-${doc.id}`}
                          >
                            <div>
                              <p className="font-medium">{doc.documentType}</p>
                              <p className="text-sm text-muted-foreground">
                                {doc.issueDate && new Date(doc.issueDate).toLocaleDateString()}
                              </p>
                            </div>
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Asset Inventory */}
                  {searchResults.assetInventory.length > 0 && (
                    <div className="space-y-2">
                      <div className="flex items-center gap-2 text-sm font-medium text-muted-foreground">
                        <Package className="h-4 w-4" />
                        Asset Inventory ({searchResults.assetInventory.length})
                      </div>
                      <div className="space-y-1">
                        {searchResults.assetInventory.map(asset => (
                          <button
                            key={asset.id}
                            onClick={() => handleNavigate(`/inventory`)}
                            className="w-full flex items-center justify-between p-2 hover-elevate rounded-md text-left"
                            data-testid={`result-asset-${asset.id}`}
                          >
                            <div>
                              <p className="font-medium">{asset.name}</p>
                              <p className="text-sm text-muted-foreground">{asset.category}</p>
                            </div>
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Maintenance Requests */}
                  {searchResults.maintenanceRequests.length > 0 && (
                    <div className="space-y-2">
                      <div className="flex items-center gap-2 text-sm font-medium text-muted-foreground">
                        <Wrench className="h-4 w-4" />
                        Maintenance Requests ({searchResults.maintenanceRequests.length})
                      </div>
                      <div className="space-y-1">
                        {searchResults.maintenanceRequests.map(request => (
                          <button
                            key={request.id}
                            onClick={() => handleNavigate(`/maintenance`)}
                            className="w-full flex items-center justify-between p-2 hover-elevate rounded-md text-left"
                            data-testid={`result-maintenance-${request.id}`}
                          >
                            <div>
                              <p className="font-medium">{request.title}</p>
                              <p className="text-sm text-muted-foreground">{request.status}</p>
                            </div>
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  {getTotalResults() === 0 && (
                    <div className="text-center py-8 text-muted-foreground">
                      No results found for the selected tags
                    </div>
                  )}
                </div>
              </ScrollArea>
            </div>
          )}

          {!searchResults && !searchMutation.isPending && selectedTags.length === 0 && (
            <div className="text-center py-8 text-muted-foreground">
              Select one or more tags to search
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/TemplateBuilder.ts.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage, FormDescription } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Plus, Trash2, GripVertical, Save, X, Eye, Code, ChevronDown, ChevronRight } from "lucide-react";
import { type InspectionTemplate, type TemplateCategory } from "@shared/schema";
import { z } from "zod";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";

// UI-specific schema - only includes fields user can edit
// Backend will populate organizationId, createdBy, createdAt, updatedAt
const templateMetaSchema = z.object({
  name: z.string().min(1, "Name is required"),
  description: z.string().optional(),
  scope: z.enum(["property", "block", "both"]),
  categoryId: z.number().optional(),
  isActive: z.boolean().default(true),
  version: z.number().default(1),
  structureJson: z.any(),
});

type TemplateMetaForm = z.infer<typeof templateMetaSchema>;

interface TemplateField {
  id: string; // Primary identifier for the field
  key: string; // Legacy property kept for compatibility
  label: string;
  type: string;
  required?: boolean;
  placeholder?: string;
  options?: string[];
  validation?: any;
  depends_on?: string;
  includeCondition?: boolean;
  includeCleanliness?: boolean;
}

interface TemplateSection {
  id: string;
  title: string;
  description?: string;
  repeatable?: boolean;
  fields: TemplateField[];
}

interface TemplateStructure {
  sections: TemplateSection[];
}

const FIELD_TYPES = [
  { value: "short_text", label: "Short Text" },
  { value: "long_text", label: "Long Text" },
  { value: "number", label: "Number" },
  { value: "rating", label: "Rating (1-5)" },
  { value: "select", label: "Dropdown" },
  { value: "multiselect", label: "Multiple Select" },
  { value: "boolean", label: "Yes/No" },
  { value: "date", label: "Date" },
  { value: "time", label: "Time" },
  { value: "datetime", label: "Date & Time" },
  { value: "photo", label: "Single Photo" },
  { value: "photo_array", label: "Multiple Photos" },
  { value: "video", label: "Video" },
  { value: "gps", label: "GPS Location" },
  { value: "signature", label: "Signature" },
];

interface TemplateBuilderProps {
  template: InspectionTemplate | null;
  categories: TemplateCategory[];
  onClose: () => void;
  onSave: () => void;
}

export function TemplateBuilder({ template, categories, onClose, onSave }: TemplateBuilderProps) {
  const { toast } = useToast();
  const [previewMode, setPreviewMode] = useState(false);

  // Parse existing structure or create default
  const rawStructure: TemplateStructure = template?.structureJson 
    ? (typeof template.structureJson === 'string' ? JSON.parse(template.structureJson) : template.structureJson as TemplateStructure)
    : { sections: [] };

  // Migrate old templates: ensure all fields have both id and key
  const initialStructure: TemplateStructure = {
    sections: rawStructure.sections.map(section => ({
      ...section,
      fields: section.fields.map(field => ({
        ...field,
        id: field.id || field.key, // Use existing id or fall back to key
        key: field.key || field.id, // Ensure key exists too
      })),
    })),
  };

  const [structure, setStructure] = useState<TemplateStructure>(initialStructure);
  const [expandedSections, setExpandedSections] = useState<Set<string>>(new Set());

  const form = useForm<TemplateMetaForm>({
    resolver: zodResolver(templateMetaSchema),
    defaultValues: {
      name: template?.name || "",
      description: template?.description || "",
      scope: template?.scope || "property",
      categoryId: template?.categoryId !== null && template?.categoryId !== undefined ? template.categoryId : undefined,
      isActive: template?.isActive ?? true,
      version: template?.version || 1,
      structureJson: initialStructure,
    },
  });

  // Save mutation
  const saveMutation = useMutation({
    mutationFn: async (data: TemplateMetaForm) => {
      const payload = {
        ...data,
        structureJson: structure,
      };
      if (template) {
        return await apiRequest("PUT", `/api/inspection-templates/${template.id}`, payload);
      } else {
        return await apiRequest("POST", "/api/inspection-templates", payload);
      }
    },
    onSuccess: async () => {
      // Refetch queries and wait for fresh data before closing dialog
      await queryClient.refetchQueries({ queryKey: ["/api/inspection-templates"] });
      if (template) {
        await queryClient.refetchQueries({ queryKey: ["/api/inspection-templates", template.id] });
      }
      toast({
        title: "Success",
        description: template ? "Template updated successfully" : "Template created successfully",
      });
      onSave();
      onClose(); // Close the modal after successful save
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to save template",
      });
    },
  });

  const handleSave = () => {
    form.handleSubmit((data) => {
      saveMutation.mutate(data);
    })();
  };

  const addSection = () => {
    const newSection: TemplateSection = {
      id: `section_${Date.now()}`,
      title: "New Section",
      fields: [],
    };
    setStructure({ sections: [...structure.sections, newSection] });
    setExpandedSections(new Set([...Array.from(expandedSections), newSection.id]));
  };

  const updateSection = (sectionId: string, updates: Partial<TemplateSection>) => {
    setStructure({
      sections: structure.sections.map((s) =>
        s.id === sectionId ? { ...s, ...updates } : s
      ),
    });
  };

  const deleteSection = (sectionId: string) => {
    setStructure({
      sections: structure.sections.filter((s) => s.id !== sectionId),
    });
    const newExpanded = new Set(expandedSections);
    newExpanded.delete(sectionId);
    setExpandedSections(newExpanded);
  };

  const addField = (sectionId: string) => {
    const section = structure.sections.find((s) => s.id === sectionId);
    if (!section) return;

    const fieldId = `field_${Date.now()}`;
    const newField: TemplateField = {
      id: fieldId,
      key: fieldId, // Keep key same as id for compatibility
      label: "New Field",
      type: "short_text",
      required: false,
    };

    updateSection(sectionId, {
      fields: [...section.fields, newField],
    });
  };

  const updateField = (sectionId: string, fieldKey: string, updates: Partial<TemplateField>) => {
    const section = structure.sections.find((s) => s.id === sectionId);
    if (!section) return;

    updateSection(sectionId, {
      fields: section.fields.map((f) =>
        f.key === fieldKey ? { ...f, ...updates } : f
      ),
    });
  };

  const deleteField = (sectionId: string, fieldKey: string) => {
    const section = structure.sections.find((s) => s.id === sectionId);
    if (!section) return;

    updateSection(sectionId, {
      fields: section.fields.filter((f) => f.key !== fieldKey),
    });
  };

  const toggleSection = (sectionId: string) => {
    const newExpanded = new Set(expandedSections);
    if (newExpanded.has(sectionId)) {
      newExpanded.delete(sectionId);
    } else {
      newExpanded.add(sectionId);
    }
    setExpandedSections(newExpanded);
  };

  return (
    <Dialog open={true} onOpenChange={onClose}>
      <DialogContent className="max-w-6xl h-[90vh] p-0 gap-0 flex flex-col">
        <DialogHeader className="p-6 pb-4 border-b">
          <div className="flex items-center justify-between">
            <DialogTitle className="text-2xl">
              {template ? "Edit Template" : "Create Template"}
            </DialogTitle>
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setPreviewMode(!previewMode)}
                data-testid="button-toggle-preview"
              >
                {previewMode ? <Code className="w-4 h-4 mr-2" /> : <Eye className="w-4 h-4 mr-2" />}
                {previewMode ? "Edit" : "Preview"}
              </Button>
              <Button
                onClick={handleSave}
                disabled={saveMutation.isPending}
                data-testid="button-save-template"
              >
                <Save className="w-4 h-4 mr-2" />
                {saveMutation.isPending ? "Saving..." : "Save Template"}
              </Button>
              <Button variant="ghost" size="icon" onClick={onClose} data-testid="button-close-builder">
                <X className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </DialogHeader>

        <div className="flex-1 overflow-auto p-6">
          {previewMode ? (
            <div className="space-y-6">
              <Card className="shadow-sm">
                <CardHeader>
                  <CardTitle>Preview Mode</CardTitle>
                </CardHeader>
                <CardContent>
                  <pre className="text-xs bg-muted p-4 rounded-lg overflow-auto">
                    {JSON.stringify({ ...form.getValues(), structureJson: structure }, null, 2)}
                  </pre>
                </CardContent>
              </Card>
            </div>
          ) : (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              {/* Template Metadata */}
              <div className="lg:col-span-1 space-y-6">
                <Card className="shadow-sm">
                  <CardHeader>
                    <CardTitle className="text-lg">Template Settings</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <Form {...form}>
                      <FormField
                        control={form.control}
                        name="name"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Name</FormLabel>
                            <FormControl>
                              <Input {...field} placeholder="Move-In Inspection" data-testid="input-template-name" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="description"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Description</FormLabel>
                            <FormControl>
                              <Textarea
                                {...field}
                                value={field.value || ""}
                                placeholder="Template description..."
                                data-testid="input-template-description"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="scope"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Scope</FormLabel>
                            <Select value={field.value} onValueChange={field.onChange}>
                              <FormControl>
                                <SelectTrigger data-testid="select-template-scope">
                                  <SelectValue />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="property">Property</SelectItem>
                                <SelectItem value="block">Block</SelectItem>
                                <SelectItem value="both">Both</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="categoryId"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Category</FormLabel>
                            <Select 
                              value={field.value?.toString() || "none"} 
                              onValueChange={(v) => field.onChange(v === "none" ? undefined : parseInt(v))}
                            >
                              <FormControl>
                                <SelectTrigger data-testid="select-template-category">
                                  <SelectValue />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="none">No Category</SelectItem>
                                {categories.map((cat) => (
                                  <SelectItem key={cat.id} value={cat.id.toString()}>
                                    {cat.name}
                                  </SelectItem>
                                ))}
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="isActive"
                        render={({ field }) => (
                          <FormItem className="flex items-center justify-between gap-2 space-y-0">
                            <FormLabel>Active Template</FormLabel>
                            <FormControl>
                              <Switch
                                checked={field.value ?? true}
                                onCheckedChange={field.onChange}
                                data-testid="switch-template-active"
                              />
                            </FormControl>
                          </FormItem>
                        )}
                      />
                    </Form>
                  </CardContent>
                </Card>
              </div>

              {/* Structure Editor */}
              <div className="lg:col-span-2 space-y-4">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-semibold">Template Structure</h3>
                  <Button onClick={addSection} size="sm" data-testid="button-add-section">
                    <Plus className="w-4 h-4 mr-2" />
                    Add Section
                  </Button>
                </div>

                {structure.sections.length === 0 ? (
                  <Card className="shadow-sm">
                    <CardContent className="text-center py-12">
                      <p className="text-muted-foreground mb-4">No sections yet</p>
                      <Button onClick={addSection} data-testid="button-add-first-section">
                        <Plus className="w-4 h-4 mr-2" />
                        Add First Section
                      </Button>
                    </CardContent>
                  </Card>
                ) : (
                  <div className="space-y-4">
                    {structure.sections.map((section, sectionIndex) => (
                      <Card key={section.id} className="shadow-sm">
                        <Collapsible
                          open={expandedSections.has(section.id)}
                          onOpenChange={() => toggleSection(section.id)}
                        >
                          <CardHeader className="pb-3">
                            <div className="flex items-center gap-3">
                              <CollapsibleTrigger asChild>
                                <Button variant="ghost" size="icon" className="h-8 w-8">
                                  {expandedSections.has(section.id) ? (
                                    <ChevronDown className="w-4 h-4" />
                                  ) : (
                                    <ChevronRight className="w-4 h-4" />
                                  )}
                                </Button>
                              </CollapsibleTrigger>
                              <GripVertical className="w-4 h-4 text-muted-foreground" />
                              <Input
                                value={section.title}
                                onChange={(e) => updateSection(section.id, { title: e.target.value })}
                                className="font-semibold flex-1"
                                placeholder="Section Title"
                                data-testid={`input-section-title-${sectionIndex}`}
                              />
                              <Switch
                                checked={section.repeatable || false}
                                onCheckedChange={(checked) => updateSection(section.id, { repeatable: checked })}
                                data-testid={`switch-section-repeatable-${sectionIndex}`}
                              />
                              <span className="text-xs text-muted-foreground">Repeatable</span>
                              <Button
                                variant="ghost"
                                size="icon"
                                onClick={() => deleteSection(section.id)}
                                data-testid={`button-delete-section-${sectionIndex}`}
                              >
                                <Trash2 className="w-4 h-4 text-destructive" />
                              </Button>
                            </div>
                          </CardHeader>
                          <CollapsibleContent>
                            <CardContent className="space-y-3">
                              <Textarea
                                value={section.description || ""}
                                onChange={(e) => updateSection(section.id, { description: e.target.value })}
                                placeholder="Section description (optional)"
                                rows={2}
                                data-testid={`input-section-description-${sectionIndex}`}
                              />

                              {/* Fields */}
                              <div className="space-y-2">
                                {section.fields.map((field, fieldIndex) => (
                                  <div key={field.key} className="space-y-2">
                                    <div className="space-y-2">
                                      <div className="flex items-center gap-2 p-3 bg-muted rounded-lg">
                                        <GripVertical className="w-4 h-4 text-muted-foreground" />
                                        <Input
                                          value={field.label}
                                          onChange={(e) => updateField(section.id, field.key, { label: e.target.value })}
                                          placeholder="Field Label"
                                          className="flex-1"
                                          data-testid={`input-field-label-${sectionIndex}-${fieldIndex}`}
                                        />
                                        <Select
                                          value={field.type}
                                          onValueChange={(type) => updateField(section.id, field.key, { type })}
                                        >
                                          <SelectTrigger className="w-40" data-testid={`select-field-type-${sectionIndex}-${fieldIndex}`}>
                                            <SelectValue />
                                          </SelectTrigger>
                                          <SelectContent>
                                            {FIELD_TYPES.map((ft) => (
                                              <SelectItem key={ft.value} value={ft.value}>
                                                {ft.label}
                                              </SelectItem>
                                            ))}
                                          </SelectContent>
                                        </Select>
                                        <Switch
                                          checked={field.required || false}
                                          onCheckedChange={(checked) =>
                                            updateField(section.id, field.key, { required: checked })
                                          }
                                          data-testid={`switch-field-required-${sectionIndex}-${fieldIndex}`}
                                        />
                                        <span className="text-xs text-muted-foreground w-16">Required</span>
                                        <Button
                                          variant="ghost"
                                          size="icon"
                                          onClick={() => deleteField(section.id, field.key)}
                                          data-testid={`button-delete-field-${sectionIndex}-${fieldIndex}`}
                                        >
                                          <Trash2 className="w-4 h-4 text-destructive" />
                                        </Button>
                                      </div>

                                      {/* Additional Options Row */}
                                      <div className="flex items-center gap-4 ml-8 px-3">
                                        <div className="flex items-center gap-2">
                                          <Switch
                                            checked={field.includeCondition || false}
                                            onCheckedChange={(checked) =>
                                              updateField(section.id, field.key, { includeCondition: checked })
                                            }
                                            data-testid={`switch-field-condition-${sectionIndex}-${fieldIndex}`}
                                          />
                                          <span className="text-xs text-muted-foreground">Include Condition</span>
                                        </div>
                                        <div className="flex items-center gap-2">
                                          <Switch
                                            checked={field.includeCleanliness || false}
                                            onCheckedChange={(checked) =>
                                              updateField(section.id, field.key, { includeCleanliness: checked })
                                            }
                                            data-testid={`switch-field-cleanliness-${sectionIndex}-${fieldIndex}`}
                                          />
                                          <span className="text-xs text-muted-foreground">Include Cleanliness</span>
                                        </div>
                                      </div>
                                    </div>

                                    {/* Options editor for select/multiselect fields */}
                                    {(field.type === "select" || field.type === "multiselect") && (
                                      <div className="ml-8 p-3 bg-background border rounded-lg space-y-2">
                                        <div className="flex items-center justify-between">
                                          <label className="text-sm font-medium">Dropdown Options</label>
                                          <Button
                                            variant="outline"
                                            size="sm"
                                            onClick={() => {
                                              const options = field.options || [];
                                              updateField(section.id, field.key, { options: [...options, ""] });
                                            }}
                                            data-testid={`button-add-option-${sectionIndex}-${fieldIndex}`}
                                          >
                                            <Plus className="w-3 h-3 mr-1" />
                                            Add Option
                                          </Button>
                                        </div>

                                        {(!field.options || field.options.length === 0) ? (
                                          <p className="text-xs text-muted-foreground">No options yet. Click "Add Option" to create dropdown choices.</p>
                                        ) : (
                                          <div className="space-y-2">
                                            {field.options.map((option, optionIndex) => (
                                              <div key={optionIndex} className="flex items-center gap-2">
                                                <Input
                                                  value={option}
                                                  onChange={(e) => {
                                                    const newOptions = [...(field.options || [])];
                                                    newOptions[optionIndex] = e.target.value;
                                                    updateField(section.id, field.key, { options: newOptions });
                                                  }}
                                                  placeholder={`Option ${optionIndex + 1}`}
                                                  className="flex-1"
                                                  data-testid={`input-option-${sectionIndex}-${fieldIndex}-${optionIndex}`}
                                                />
                                                <Button
                                                  variant="ghost"
                                                  size="icon"
                                                  onClick={() => {
                                                    const newOptions = (field.options || []).filter((_, i) => i !== optionIndex);
                                                    updateField(section.id, field.key, { options: newOptions });
                                                  }}
                                                  data-testid={`button-delete-option-${sectionIndex}-${fieldIndex}-${optionIndex}`}
                                                >
                                                  <X className="w-4 h-4 text-destructive" />
                                                </Button>
                                              </div>
                                            ))}
                                          </div>
                                        )}
                                      </div>
                                    )}
                                  </div>
                                ))}
                              </div>

                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => addField(section.id)}
                                className="w-full"
                                data-testid={`button-add-field-${sectionIndex}`}
                              >
                                <Plus className="w-4 h-4 mr-2" />
                                Add Field
                              </Button>
                            </CardContent>
                          </CollapsibleContent>
                        </Collapsible>
                      </Card>
                    ))}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

