import { useQuery, useMutation } from "@tanstack/react-query";
import { useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { Shield, Plus, Trash2, Edit, ArrowLeft } from "lucide-react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";

export default function AdminTeam() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [createDialog, setCreateDialog] = useState(false);
  const [editDialog, setEditDialog] = useState(false);
  const [deleteDialog, setDeleteDialog] = useState(false);
  const [selectedAdmin, setSelectedAdmin] = useState<any>(null);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    firstName: "",
    lastName: "",
  });

  // Fetch admin team
  const { data: admins = [], isLoading } = useQuery<any[]>({
    queryKey: ["/api/admin/team"],
    retry: false,
  });

  // Fetch current admin user
  const { data: currentAdmin, isLoading: isLoadingAdmin } = useQuery({
    queryKey: ["/api/admin/me"],
    retry: false,
  });

  // Create admin mutation
  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await fetch("/api/admin/team", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
        credentials: "include",
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to create admin");
      }
      return response.json();
    },
    onSuccess: async () => {
      await queryClient.invalidateQueries({ queryKey: ["/api/admin/team"] });
      setCreateDialog(false);
      setFormData({ email: "", password: "", firstName: "", lastName: "" });
      toast({
        title: "Admin Created",
        description: "New admin user has been created successfully",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Creation Failed",
        description: error.message,
      });
    },
  });

  // Update admin mutation
  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await fetch(`/api/admin/team/${id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to update admin");
      return response.json();
    },
    onSuccess: async () => {
      await queryClient.invalidateQueries({ queryKey: ["/api/admin/team"] });
      setEditDialog(false);
      toast({
        title: "Admin Updated",
        description: "Admin user has been updated successfully",
      });
    },
  });

  // Delete admin mutation
  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/admin/team/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to delete admin");
      }
    },
    onSuccess: async () => {
      await queryClient.invalidateQueries({ queryKey: ["/api/admin/team"] });
      setDeleteDialog(false);
      setSelectedAdmin(null);
      toast({
        title: "Admin Deleted",
        description: "Admin user has been removed successfully",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Deletion Failed",
        description: error.message,
      });
    },
  });

  const handleCreateClick = () => {
    setFormData({ email: "", password: "", firstName: "", lastName: "" });
    setCreateDialog(true);
  };

  const handleEditClick = (admin: any) => {
    setSelectedAdmin(admin);
    setFormData({
      email: admin.email,
      password: "",
      firstName: admin.firstName,
      lastName: admin.lastName,
    });
    setEditDialog(true);
  };

  const handleDeleteClick = (admin: any) => {
    setSelectedAdmin(admin);
    setDeleteDialog(true);
  };

  const handleCreateSubmit = () => {
    createMutation.mutate(formData);
  };

  const handleEditSubmit = () => {
    if (selectedAdmin) {
      const updateData: any = {
        email: formData.email,
        firstName: formData.firstName,
        lastName: formData.lastName,
      };
      if (formData.password) {
        updateData.password = formData.password;
      }
      updateMutation.mutate({ id: selectedAdmin.id, data: updateData });
    }
  };

  const handleDeleteConfirm = () => {
    if (selectedAdmin) {
      deleteMutation.mutate(selectedAdmin.id);
    }
  };

  // Show loading state while checking authentication
  if (isLoadingAdmin) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  // Redirect to login if not authenticated
  if (!currentAdmin) {
    navigate("/admin/login");
    return null;
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="border-b bg-card">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => navigate("/admin/dashboard")}
              data-testid="button-back-to-dashboard"
            >
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Dashboard
            </Button>
          </div>
          <div className="flex items-center gap-3">
            <Shield className="w-8 h-8 text-primary" />
            <div>
              <h1 className="text-2xl font-bold" data-testid="heading-admin-team">Admin Team</h1>
              <p className="text-sm text-muted-foreground">Manage admin users</p>
            </div>
          </div>
          <div />
        </div>
      </header>

      {/* Main Content */}
      <div className="container mx-auto px-4 py-8 space-y-6">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
            <div>
              <CardTitle>Admin Users</CardTitle>
              <CardDescription>
                Manage admin accounts with access to this dashboard
              </CardDescription>
            </div>
            <Button onClick={handleCreateClick} data-testid="button-create-admin">
              <Plus className="w-4 h-4 mr-2" />
              Add Admin
            </Button>
          </CardHeader>
          <CardContent className="p-0">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Email</TableHead>
                  <TableHead>Created</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {isLoading ? (
                  <TableRow>
                    <TableCell colSpan={4} className="text-center py-8 text-muted-foreground">
                      Loading admin users...
                    </TableCell>
                  </TableRow>
                ) : admins.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={4} className="text-center py-8 text-muted-foreground">
                      No admin users found
                    </TableCell>
                  </TableRow>
                ) : (
                  admins.map((admin) => (
                    <TableRow key={admin.id} data-testid={`row-admin-${admin.id}`}>
                      <TableCell className="font-medium">
                        {admin.firstName} {admin.lastName}
                        {currentAdmin.id === admin.id && (
                          <span className="ml-2 text-xs text-muted-foreground">(You)</span>
                        )}
                      </TableCell>
                      <TableCell>{admin.email}</TableCell>
                      <TableCell className="text-sm text-muted-foreground">
                        {new Date(admin.createdAt).toLocaleDateString()}
                      </TableCell>
                      <TableCell className="text-right space-x-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEditClick(admin)}
                          data-testid={`button-edit-admin-${admin.id}`}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          variant="destructive"
                          size="sm"
                          onClick={() => handleDeleteClick(admin)}
                          disabled={currentAdmin.id === admin.id}
                          data-testid={`button-delete-admin-${admin.id}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      </div>

      {/* Create Dialog */}
      <Dialog open={createDialog} onOpenChange={setCreateDialog}>
        <DialogContent data-testid="dialog-create-admin">
          <DialogHeader>
            <DialogTitle>Create Admin User</DialogTitle>
            <DialogDescription>
              Add a new admin user with access to this dashboard
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label>First Name</Label>
              <Input
                value={formData.firstName}
                onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
                placeholder="John"
                data-testid="input-first-name"
              />
            </div>
            <div className="space-y-2">
              <Label>Last Name</Label>
              <Input
                value={formData.lastName}
                onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
                placeholder="Doe"
                data-testid="input-last-name"
              />
            </div>
            <div className="space-y-2">
              <Label>Email</Label>
              <Input
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                placeholder="admin@inspect360.com"
                data-testid="input-email"
              />
            </div>
            <div className="space-y-2">
              <Label>Password</Label>
              <Input
                type="password"
                value={formData.password}
                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                placeholder="Enter password"
                data-testid="input-password"
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setCreateDialog(false)}>
              Cancel
            </Button>
            <Button
              onClick={handleCreateSubmit}
              disabled={createMutation.isPending}
              data-testid="button-submit-create"
            >
              {createMutation.isPending ? "Creating..." : "Create Admin"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Edit Dialog */}
      <Dialog open={editDialog} onOpenChange={setEditDialog}>
        <DialogContent data-testid="dialog-edit-admin">
          <DialogHeader>
            <DialogTitle>Edit Admin User</DialogTitle>
            <DialogDescription>
              Update admin user information
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label>First Name</Label>
              <Input
                value={formData.firstName}
                onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
                data-testid="input-edit-first-name"
              />
            </div>
            <div className="space-y-2">
              <Label>Last Name</Label>
              <Input
                value={formData.lastName}
                onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
                data-testid="input-edit-last-name"
              />
            </div>
            <div className="space-y-2">
              <Label>Email</Label>
              <Input
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                data-testid="input-edit-email"
              />
            </div>
            <div className="space-y-2">
              <Label>New Password (leave blank to keep current)</Label>
              <Input
                type="password"
                value={formData.password}
                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                placeholder="Enter new password"
                data-testid="input-edit-password"
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setEditDialog(false)}>
              Cancel
            </Button>
            <Button
              onClick={handleEditSubmit}
              disabled={updateMutation.isPending}
              data-testid="button-submit-edit"
            >
              {updateMutation.isPending ? "Saving..." : "Save Changes"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={deleteDialog} onOpenChange={setDeleteDialog}>
        <DialogContent data-testid="dialog-delete-admin">
          <DialogHeader>
            <DialogTitle>Delete Admin User</DialogTitle>
            <DialogDescription>
              Are you sure you want to delete{" "}
              <span className="font-semibold">
                {selectedAdmin?.firstName} {selectedAdmin?.lastName}
              </span>
              ? This action cannot be undone.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button variant="outline" onClick={() => setDeleteDialog(false)}>
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={handleDeleteConfirm}
              disabled={deleteMutation.isPending}
              data-testid="button-confirm-delete"
            >
              {deleteMutation.isPending ? "Deleting..." : "Delete Admin"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/AdminDashboard.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { Shield, Search, Building2, LogOut, Users, CreditCard, AlertCircle, CheckCircle, XCircle, BookOpen } from "lucide-react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";

export default function AdminDashboard() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedInstance, setSelectedInstance] = useState<any>(null);
  const [editDialog, setEditDialog] = useState(false);
  const [editFormData, setEditFormData] = useState({
    subscriptionLevel: "",
    creditsRemaining: 0,
    isActive: true,
  });

  // Fetch admin user
  const { data: adminUser, isLoading: isLoadingAdmin } = useQuery({
    queryKey: ["/api/admin/me"],
    retry: false,
  });

  // Fetch instances
  const { data: instances = [], isLoading } = useQuery<any[]>({
    queryKey: ["/api/admin/instances"],
    retry: false,
  });

  // Logout mutation
  const logoutMutation = useMutation({
    mutationFn: async () => {
      await fetch("/api/admin/logout", {
        method: "POST",
        credentials: "include",
      });
    },
    onSuccess: () => {
      navigate("/admin/login");
    },
  });

  // Update instance mutation
  const updateInstanceMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await fetch(`/api/admin/instances/${id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to update instance");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/instances"] });
      setEditDialog(false);
      toast({
        title: "Instance Updated",
        description: "Instance settings have been updated successfully",
      });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Update Failed",
        description: "Failed to update instance settings",
      });
    },
  });

  // Toggle status mutation
  const toggleStatusMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/admin/instances/${id}/toggle-status`, {
        method: "POST",
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to toggle status");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/instances"] });
      toast({
        title: "Status Updated",
        description: "Instance status has been toggled successfully",
      });
    },
  });

  // Filter instances based on search
  const filteredInstances = instances.filter(
    (instance) =>
      instance.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      instance.owner?.email?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      instance.owner?.firstName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      instance.owner?.lastName?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleEditClick = (instance: any) => {
    setSelectedInstance(instance);
    setEditFormData({
      subscriptionLevel: instance.subscriptionLevel || "free",
      creditsRemaining: instance.creditsRemaining || 0,
      isActive: instance.isActive !== false,
    });
    setEditDialog(true);
  };

  const handleEditSubmit = () => {
    if (selectedInstance) {
      updateInstanceMutation.mutate({
        id: selectedInstance.id,
        data: editFormData,
      });
    }
  };

  // Show loading state while checking authentication
  if (isLoadingAdmin) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  // Redirect to login if not authenticated
  if (!adminUser) {
    navigate("/admin/login");
    return null;
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="border-b bg-card">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Shield className="w-8 h-8 text-primary" />
            <div>
              <h1 className="text-2xl font-bold" data-testid="heading-admin-dashboard">Admin Dashboard</h1>
              <p className="text-sm text-muted-foreground">Inspect360 Platform Administration</p>
            </div>
          </div>
          <div className="flex items-center gap-4">
            <Button
              variant="outline"
              onClick={() => navigate("/admin/knowledge-base")}
              data-testid="button-knowledge-base"
            >
              <BookOpen className="h-4 w-4 mr-2" />
              Knowledge Base
            </Button>
            <span className="text-sm text-muted-foreground">
              {adminUser.firstName} {adminUser.lastName}
            </span>
            <Button
              variant="outline"
              size="sm"
              onClick={() => navigate("/admin/team")}
              data-testid="button-admin-team"
            >
              <Users className="w-4 h-4 mr-2" />
              Admin Team
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => logoutMutation.mutate()}
              data-testid="button-admin-logout"
            >
              <LogOut className="w-4 h-4 mr-2" />
              Logout
            </Button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="container mx-auto px-4 py-8 space-y-6">
        {/* Search Bar */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Building2 className="w-5 h-5" />
              Registered Instances
            </CardTitle>
            <CardDescription>
              Monitor and manage all registered organizations
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="relative">
              <Search className="absolute left-3 top-3 w-4 h-4 text-muted-foreground" />
              <Input
                placeholder="Search by organization name, owner name, or email..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search-instances"
              />
            </div>
          </CardContent>
        </Card>

        {/* Instances Table */}
        <Card>
          <CardContent className="p-0">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Organization</TableHead>
                  <TableHead>Owner</TableHead>
                  <TableHead>Subscription</TableHead>
                  <TableHead>Credits</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Created</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {isLoading ? (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center py-8 text-muted-foreground">
                      Loading instances...
                    </TableCell>
                  </TableRow>
                ) : filteredInstances.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center py-8 text-muted-foreground">
                      No instances found
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredInstances.map((instance) => (
                    <TableRow key={instance.id} data-testid={`row-instance-${instance.id}`}>
                      <TableCell>
                        <div>
                          <div className="font-medium">{instance.name}</div>
                          <div className="text-xs text-muted-foreground">{instance.id}</div>
                        </div>
                      </TableCell>
                      <TableCell>
                        <div>
                          <div className="font-medium">
                            {instance.owner?.firstName} {instance.owner?.lastName}
                          </div>
                          <div className="text-xs text-muted-foreground">{instance.owner?.email}</div>
                        </div>
                      </TableCell>
                      <TableCell>
                        <Badge variant="outline" className="capitalize">
                          {instance.subscriptionLevel || "free"}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <CreditCard className="w-4 h-4 text-muted-foreground" />
                          {instance.creditsRemaining || 0}
                        </div>
                      </TableCell>
                      <TableCell>
                        {instance.isActive !== false ? (
                          <Badge variant="default" className="flex items-center gap-1 w-fit">
                            <CheckCircle className="w-3 h-3" />
                            Active
                          </Badge>
                        ) : (
                          <Badge variant="destructive" className="flex items-center gap-1 w-fit">
                            <XCircle className="w-3 h-3" />
                            Disabled
                          </Badge>
                        )}
                      </TableCell>
                      <TableCell className="text-sm text-muted-foreground">
                        {new Date(instance.createdAt).toLocaleDateString()}
                      </TableCell>
                      <TableCell className="text-right space-x-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEditClick(instance)}
                          data-testid={`button-edit-${instance.id}`}
                        >
                          Edit
                        </Button>
                        <Button
                          variant={instance.isActive !== false ? "destructive" : "default"}
                          size="sm"
                          onClick={() => toggleStatusMutation.mutate(instance.id)}
                          disabled={toggleStatusMutation.isPending}
                          data-testid={`button-toggle-${instance.id}`}
                        >
                          {instance.isActive !== false ? "Disable" : "Enable"}
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      </div>

      {/* Edit Dialog */}
      <Dialog open={editDialog} onOpenChange={setEditDialog}>
        <DialogContent data-testid="dialog-edit-instance">
          <DialogHeader>
            <DialogTitle>Edit Instance Settings</DialogTitle>
            <DialogDescription>
              Update subscription level, credits, and status for {selectedInstance?.name}
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label>Subscription Level</Label>
              <Select
                value={editFormData.subscriptionLevel}
                onValueChange={(value) =>
                  setEditFormData({ ...editFormData, subscriptionLevel: value })
                }
              >
                <SelectTrigger data-testid="select-subscription-level">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="free">Free</SelectItem>
                  <SelectItem value="starter">Starter</SelectItem>
                  <SelectItem value="professional">Professional</SelectItem>
                  <SelectItem value="enterprise">Enterprise</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label>Credits Remaining</Label>
              <Input
                type="number"
                value={editFormData.creditsRemaining}
                onChange={(e) =>
                  setEditFormData({
                    ...editFormData,
                    creditsRemaining: parseInt(e.target.value) || 0,
                  })
                }
                data-testid="input-credits"
              />
            </div>
            <div className="flex items-center space-x-2">
              <input
                type="checkbox"
                id="isActive"
                checked={editFormData.isActive}
                onChange={(e) =>
                  setEditFormData({ ...editFormData, isActive: e.target.checked })
                }
                className="w-4 h-4"
                data-testid="checkbox-is-active"
              />
              <Label htmlFor="isActive">Instance Active</Label>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setEditDialog(false)}>
              Cancel
            </Button>
            <Button
              onClick={handleEditSubmit}
              disabled={updateInstanceMutation.isPending}
              data-testid="button-save-instance"
            >
              {updateInstanceMutation.isPending ? "Saving..." : "Save Changes"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/AdminLogin.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import logoUrl from "@assets/Inspect360 Logo_1761302629835.png";

export default function AdminLogin() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch("/api/admin/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
        credentials: "include",
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Login failed");
      }

      const admin = await response.json();

      toast({
        title: "Login Successful",
        description: `Welcome back, ${admin.firstName}!`,
      });

      navigate("/admin/dashboard");
    } catch (error: any) {
      toast({
        variant: "destructive",
        title: "Login Failed",
        description: error.message || "Invalid credentials",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-sky-100 dark:from-gray-900 dark:to-gray-800 p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-1 text-center">
          <div className="flex justify-center mb-4">
            <img src={logoUrl} alt="Inspect360" className="h-16" />
          </div>
          <CardTitle className="text-3xl font-bold" data-testid="heading-admin-login">Admin Portal</CardTitle>
          <CardDescription>
            Sign in to access the Inspect360 Admin Dashboard
          </CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="email">Email Address</Label>
              <Input
                id="email"
                type="email"
                placeholder="admin@inspect360.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                disabled={isLoading}
                data-testid="input-admin-email"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                disabled={isLoading}
                data-testid="input-admin-password"
              />
            </div>
            <Button
              type="submit"
              className="w-full"
              disabled={isLoading}
              data-testid="button-admin-login"
            >
              {isLoading ? "Signing in..." : "Sign In"}
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/InspectionReport.tsx.content:
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { 
  ArrowLeft, 
  Printer, 
  Download, 
  Edit2, 
  Save, 
  X, 
  AlertTriangle, 
  FileText,
  Calendar,
  User,
  MapPin,
  Building,
  Wrench,
  GitCompare,
  ExternalLink
} from "lucide-react";
import { format } from "date-fns";
import { useState } from "react";
import { queryClient, apiRequest } from "@/lib/queryClient";

interface TemplateField {
  id: string;
  key?: string;
  label: string;
  type: string;
  description?: string;
  required?: boolean;
  placeholder?: string;
  options?: string[];
  validation?: Record<string, any>;
  dependsOn?: Record<string, any>;
  includeCondition?: boolean;
  includeCleanliness?: boolean;
}

interface TemplateSection {
  id: string;
  title: string;
  description?: string;
  repeatable?: boolean;
  fields: TemplateField[];
}

interface Inspection {
  id: string;
  organizationId: string;
  templateId?: string;
  templateSnapshotJson?: { sections: TemplateSection[] };
  propertyId?: string;
  blockId?: string;
  inspectorId: string;
  type: string;
  status: string;
  scheduledDate?: string;
  startedAt?: string;
  completedDate?: string;
  submittedAt?: string;
  notes?: string;
  property?: {
    id: string;
    name: string;
    address: string;
    propertyType?: string;
  };
  block?: {
    id: string;
    name: string;
    address: string;
  };
  inspector?: {
    id: string;
    email: string;
    firstName?: string;
    lastName?: string;
  };
}

export default function InspectionReport() {
  const { id } = useParams<{ id: string }>();
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [editMode, setEditMode] = useState(false);
  const [editedNotes, setEditedNotes] = useState<Record<string, string>>({});
  const [maintenanceDialogOpen, setMaintenanceDialogOpen] = useState(false);
  const [selectedEntryForMaintenance, setSelectedEntryForMaintenance] = useState<{
    entryId: string;
    fieldLabel: string;
    sectionTitle: string;
  } | null>(null);
  const [maintenanceForm, setMaintenanceForm] = useState({
    title: "",
    description: "",
    priority: "medium" as "low" | "medium" | "high" | "urgent",
  });

  // Fetch inspection data
  const { data: inspection, isLoading: inspectionLoading } = useQuery<Inspection>({
    queryKey: ["/api/inspections", id],
  });

  // Fetch all inspection entries
  const { data: entries = [], isLoading: entriesLoading } = useQuery<any[]>({
    queryKey: [`/api/inspections/${id}/entries`],
    enabled: !!id,
  });

  // Fetch user role for edit permissions
  const { data: currentUser } = useQuery<any>({
    queryKey: ["/api/auth/user"],
  });

  // Fetch maintenance requests linked to this inspection
  const { data: maintenanceRequests = [] } = useQuery<any[]>({
    queryKey: [`/api/maintenance?inspectionId=${id}`],
    enabled: !!id,
  });

  // Create maintenance request mutation
  const createMaintenanceMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await fetch(`/api/maintenance`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to create maintenance request");
      }
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/maintenance?inspectionId=${id}`] });
      toast({
        title: "Success",
        description: "Maintenance request created successfully",
      });
      setMaintenanceDialogOpen(false);
      setMaintenanceForm({ title: "", description: "", priority: "medium" });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create maintenance request",
        variant: "destructive",
      });
    },
  });

  // Update inspection status mutation
  const updateStatusMutation = useMutation({
    mutationFn: async (newStatus: string) => {
      return await apiRequest("PATCH", `/api/inspections/${id}/status`, {
        status: newStatus,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/inspections", id] });
      queryClient.invalidateQueries({ queryKey: ["/api/inspections/my"] });
      toast({
        title: "Success",
        description: "Inspection status updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update inspection status",
        variant: "destructive",
      });
    },
  });

  // Auto-create comparison report mutation
  const autoCreateComparisonMutation = useMutation({
    mutationFn: async (context: { inspectionId: string; fieldKey: string }) => {
      if (!inspection?.propertyId) {
        throw new Error("This inspection must be assigned to a property before creating a comparison report");
      }

      const response = await fetch(`/api/comparison-reports/auto`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ 
          propertyId: inspection.propertyId,
          checkOutInspectionId: context.inspectionId,
          fieldKey: context.fieldKey
        }),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to create comparison report");
      }
      return await response.json();
    },
    onSuccess: (data: any) => {
      const { report, created } = data;
      if (created) {
        toast({
          title: "Success",
          description: "Comparison report created successfully using the latest check-in and check-out inspections",
        });
      } else {
        toast({
          title: "Success",
          description: "Navigating to existing comparison report",
        });
      }
      // Navigate to the comparison report detail page
      navigate(`/comparisons/${report.id}`);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create comparison report",
        variant: "destructive",
      });
    },
  });

  const templateStructure = inspection?.templateSnapshotJson as { sections: TemplateSection[] } | null;
  const sections = templateStructure?.sections || [];

  // Initialize edited notes from entries
  useState(() => {
    const initialNotes: Record<string, string> = {};
    entries.forEach((entry: any) => {
      const key = `${entry.sectionRef}-${entry.fieldKey}`;
      if (entry.note) {
        initialNotes[key] = entry.note;
      }
    });
    setEditedNotes(initialNotes);
  });

  // Save edited notes mutation
  const saveNotesMutation = useMutation({
    mutationFn: async (updates: { entryId: string; note: string }[]) => {
      const promises = updates.map(({ entryId, note }) =>
        apiRequest("PATCH", `/api/inspection-entries/${entryId}`, { note })
      );
      return Promise.all(promises);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/inspections/${id}/entries`] });
      setEditMode(false);
      toast({
        title: "Report updated",
        description: "Changes saved successfully",
      });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to save changes",
      });
    },
  });

  const handleSaveEdits = () => {
    const updates: { entryId: string; note: string }[] = [];
    entries.forEach((entry: any) => {
      const key = `${entry.sectionRef}-${entry.fieldKey}`;
      if (editedNotes[key] !== entry.note) {
        updates.push({ entryId: entry.id, note: editedNotes[key] || "" });
      }
    });
    if (updates.length > 0) {
      saveNotesMutation.mutate(updates);
    } else {
      setEditMode(false);
    }
  };

  const handleCancelEdit = () => {
    setEditMode(false);
    const initialNotes: Record<string, string> = {};
    entries.forEach((entry: any) => {
      const key = `${entry.sectionRef}-${entry.fieldKey}`;
      if (entry.note) {
        initialNotes[key] = entry.note;
      }
    });
    setEditedNotes(initialNotes);
  };

  const handleOpenMaintenanceDialog = (entryId: string, fieldLabel: string, sectionTitle: string) => {
    setSelectedEntryForMaintenance({ entryId, fieldLabel, sectionTitle });
    setMaintenanceForm({
      title: `${sectionTitle} - ${fieldLabel}`,
      description: "",
      priority: "medium",
    });
    setMaintenanceDialogOpen(true);
  };

  const handleSubmitMaintenance = () => {
    if (!inspection || !selectedEntryForMaintenance) return;

    createMaintenanceMutation.mutate({
      title: maintenanceForm.title,
      description: maintenanceForm.description,
      priority: maintenanceForm.priority,
      status: "open",
      propertyId: inspection.propertyId,
      organizationId: inspection.organizationId,
      reportedBy: currentUser?.id,
      source: "inspection",
      inspectionId: inspection.id,
      inspectionEntryId: selectedEntryForMaintenance.entryId,
    });
  };

  const handlePrint = async () => {
    if (!inspection) return;

    try {
      toast({
        title: "Generating PDF",
        description: "Please wait while we create your inspection report...",
      });

      const response = await fetch(`/api/inspections/${inspection.id}/pdf`, {
        method: "GET",
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to generate PDF");
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${inspection.property?.name?.replace(/[^a-zA-Z0-9]/g, "_") || "inspection"}_report.pdf`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "PDF Generated",
        description: "Your inspection report has been downloaded.",
      });
    } catch (error) {
      console.error("Error generating PDF:", error);
      toast({
        title: "Error",
        description: "Failed to generate PDF report. Please try again.",
        variant: "destructive",
      });
    }
  };

  const getEntryValue = (sectionId: string, fieldKey: string) => {
    return entries.find(e => e.sectionRef === sectionId && e.fieldKey === fieldKey);
  };

  const renderFieldValue = (value: any, field: TemplateField) => {
    if (value === null || value === undefined) {
      return <span className="text-muted-foreground italic">Not recorded</span>;
    }

    // Handle composite values (with condition/cleanliness)
    let actualValue = value;
    let condition = null;
    let cleanliness = null;

    if (typeof value === 'object' && !Array.isArray(value) && value.value !== undefined) {
      actualValue = value.value;
      condition = value.condition;
      cleanliness = value.cleanliness;
    }

    let displayValue: React.ReactNode = null;

    switch (field.type) {
      case "text":
      case "textarea":
      case "email":
      case "phone":
        displayValue = actualValue || <span className="text-muted-foreground italic">Not filled</span>;
        break;
      case "number":
        displayValue = actualValue !== null && actualValue !== undefined ? actualValue.toString() : <span className="text-muted-foreground italic">Not filled</span>;
        break;
      case "boolean":
        displayValue = actualValue ? "Yes" : "No";
        break;
      case "select":
      case "radio":
        displayValue = actualValue || <span className="text-muted-foreground italic">Not selected</span>;
        break;
      case "checkbox":
        displayValue = Array.isArray(actualValue) ? actualValue.join(", ") : <span className="text-muted-foreground italic">None selected</span>;
        break;
      case "date":
        displayValue = actualValue ? format(new Date(actualValue), "MMM dd, yyyy") : <span className="text-muted-foreground italic">Not set</span>;
        break;
      case "signature":
        displayValue = actualValue ? (
          <img 
            src={actualValue} 
            alt="Signature" 
            className="max-w-md h-32 object-contain border rounded bg-background p-2"
          />
        ) : (
          <span className="text-muted-foreground italic">Not signed</span>
        );
        break;
      default:
        // Handle objects that don't match composite structure
        if (typeof actualValue === 'object' && actualValue !== null) {
          // If it's still an object, try to stringify it nicely or show a message
          if (Array.isArray(actualValue)) {
            displayValue = actualValue.length > 0 
              ? actualValue.join(", ") 
              : <span className="text-muted-foreground italic">Empty list</span>;
          } else {
            // Try to extract meaningful data from the object with error handling
            try {
              // Check if object has a meaningful toString or specific properties to display
              const jsonStr = JSON.stringify(actualValue, null, 2);
              if (jsonStr && jsonStr !== '{}' && jsonStr !== 'null') {
                displayValue = (
                  <pre className="text-sm bg-muted p-2 rounded whitespace-pre-wrap break-words">
                    {jsonStr}
                  </pre>
                );
              } else {
                displayValue = <span className="text-muted-foreground italic">No data</span>;
              }
            } catch (error) {
              // Handle circular references or other JSON.stringify errors
              console.warn('Failed to stringify field value:', error);
              displayValue = <span className="text-muted-foreground italic">Complex data structure</span>;
            }
          }
        } else if (typeof actualValue === 'string' && actualValue.length > 0) {
          displayValue = actualValue;
        } else if (typeof actualValue === 'number') {
          displayValue = actualValue.toString();
        } else {
          displayValue = <span className="text-muted-foreground italic">Not filled</span>;
        }
    }

    return (
      <div className="space-y-2">
        <div className="text-base">{displayValue}</div>
        {(condition || cleanliness) && (
          <div className="flex gap-2 flex-wrap">
            {condition && (
              <Badge variant={condition === 'Excellent' ? 'default' : condition === 'Good' ? 'secondary' : 'destructive'}>
                Condition: {condition}
              </Badge>
            )}
            {cleanliness && (
              <Badge variant={cleanliness === 'Clean' ? 'default' : cleanliness === 'Needs a Clean' ? 'secondary' : 'destructive'}>
                Cleanliness: {cleanliness}
              </Badge>
            )}
          </div>
        )}
      </div>
    );
  };

  const canEdit = currentUser?.role === 'owner' || currentUser?.role === 'compliance';

  if (inspectionLoading || entriesLoading) {
    return (
      <div className="p-6 max-w-6xl mx-auto space-y-6">
        <Skeleton className="h-8 w-64" />
        <Skeleton className="h-40 w-full" />
        <Skeleton className="h-60 w-full" />
      </div>
    );
  }

  if (!inspection) {
    return (
      <div className="p-6 max-w-6xl mx-auto">
        <Card>
          <CardContent className="pt-6">
            <p className="text-center text-muted-foreground">Inspection not found</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  const inspectorName = inspection.inspector
    ? `${inspection.inspector.firstName || ''} ${inspection.inspector.lastName || ''}`.trim() || inspection.inspector.email
    : "Unknown Inspector";

  const propertyName = inspection.property?.name || inspection.block?.name || "Unknown Property";
  const propertyAddress = inspection.property?.address || inspection.block?.address || "No address";

  return (
    <div className="min-h-screen bg-background">
      {/* Print styles */}
      <style>{`
        @media print {
          .no-print {
            display: none !important;
          }
          .print-break-inside-avoid {
            break-inside: avoid;
            page-break-inside: avoid;
          }
          .print-only {
            display: block !important;
          }
          .print-cover-page {
            display: flex !important;
            page-break-after: always;
            min-height: 100%;
            padding: 3rem 2rem;
            margin: 0;
            box-sizing: border-box;
          }
          body {
            background: white !important;
            print-color-adjust: exact;
            -webkit-print-color-adjust: exact;
          }
          .bg-card {
            background: white !important;
            border: 1px solid #e5e7eb !important;
          }
          img {
            max-width: 100%;
            page-break-inside: avoid;
          }
          h1, h2, h3, h4, h5, h6 {
            page-break-after: avoid;
            page-break-inside: avoid;
          }
          .space-y-8 > * + * {
            margin-top: 2rem !important;
          }
          @page {
            margin: 0.75in;
            size: letter;
          }
          @page :first {
            margin: 0;
          }
          /* Ensure colors print correctly */
          * {
            print-color-adjust: exact;
            -webkit-print-color-adjust: exact;
          }
        }
        @media screen {
          .print-only {
            position: absolute !important;
            left: -9999px !important;
            top: -9999px !important;
            width: 0 !important;
            height: 0 !important;
            overflow: hidden !important;
            visibility: hidden !important;
          }
        }
      `}</style>

      {/* PDF Cover Page - Only visible when printing */}
      <div className="print-only print-cover-page hidden flex-col items-center justify-center text-center p-12" aria-hidden="true">
        <div className="space-y-12 max-w-2xl mx-auto">
          {/* Logo */}
          <div className="flex justify-center mb-16">
            <img 
              src={new URL("@assets/Inspect360 Logo_1761302629835.png", import.meta.url).href}
              alt="Inspect360 Logo" 
              className="h-24 w-auto"
            />
          </div>

          {/* Report Title */}
          <div className="space-y-4 mb-12">
            <h1 className="text-5xl font-bold text-gray-900" style={{ color: '#000' }}>
              Inspection Report
            </h1>
            <div className="h-1 w-32 mx-auto" style={{ backgroundColor: '#00D5CC' }}></div>
          </div>

          {/* Inspection Type */}
          {inspection.type && (
            <div className="text-2xl font-semibold text-gray-700 mb-12" style={{ color: '#333' }}>
              {inspection.type.replace(/_/g, ' ').toUpperCase()}
            </div>
          )}

          {/* Property Information */}
          <div className="space-y-6 pt-8 border-t-2" style={{ borderColor: '#E5E7EB' }}>
            <div className="space-y-2">
              <div className="text-sm font-medium text-gray-500 uppercase tracking-wide">Property</div>
              <div className="text-2xl font-bold text-gray-900" style={{ color: '#000' }}>
                {propertyName || "Property Not Specified"}
              </div>
              {propertyAddress && propertyAddress !== "No address" && (
                <div className="text-lg text-gray-600" style={{ color: '#666' }}>
                  {propertyAddress}
                </div>
              )}
            </div>

            {/* Inspector Information */}
            <div className="space-y-2 pt-6">
              <div className="text-sm font-medium text-gray-500 uppercase tracking-wide">Inspector</div>
              <div className="text-xl font-semibold text-gray-900" style={{ color: '#000' }}>
                {inspectorName || "Inspector Not Assigned"}
              </div>
              {inspection.inspector?.email && (
                <div className="text-base text-gray-600" style={{ color: '#666' }}>
                  {inspection.inspector.email}
                </div>
              )}
            </div>

            {/* Inspection Date */}
            <div className="space-y-2 pt-6">
              <div className="text-sm font-medium text-gray-500 uppercase tracking-wide">Inspection Date</div>
              <div className="text-xl font-semibold text-gray-900" style={{ color: '#000' }}>
                {inspection.completedDate 
                  ? format(new Date(inspection.completedDate), "MMMM dd, yyyy")
                  : inspection.scheduledDate 
                    ? format(new Date(inspection.scheduledDate), "MMMM dd, yyyy")
                    : "Not Scheduled"}
              </div>
            </div>

            {/* Status Badge */}
            {inspection.status && (
              <div className="pt-6">
                <div className="inline-block px-6 py-2 text-sm font-semibold uppercase tracking-wide border-2 rounded-lg"
                     style={{ 
                       borderColor: inspection.status === 'completed' ? '#00D5CC' : '#9CA3AF',
                       color: inspection.status === 'completed' ? '#00D5CC' : '#9CA3AF'
                     }}>
                  {inspection.status.replace(/_/g, ' ')}
                </div>
              </div>
            )}
          </div>

          {/* Report ID */}
          {inspection.id && (
            <div className="pt-8 text-sm text-gray-500">
              Report ID: {inspection.id}
            </div>
          )}
        </div>
      </div>

      {/* Header Actions - Hidden in print */}
      <div className="no-print sticky top-0 z-10 bg-background border-b">
        <div className="max-w-6xl mx-auto p-4 flex items-center justify-between gap-4">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => navigate("/inspections")}
            data-testid="button-back"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Inspections
          </Button>

          <div className="flex gap-2">
            {canEdit && !editMode && (
              <Button
                variant="outline"
                size="sm"
                onClick={() => setEditMode(true)}
                data-testid="button-edit-report"
              >
                <Edit2 className="w-4 h-4 mr-2" />
                Edit Report
              </Button>
            )}
            {editMode && (
              <>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleCancelEdit}
                  data-testid="button-cancel-edit"
                >
                  <X className="w-4 h-4 mr-2" />
                  Cancel
                </Button>
                <Button
                  size="sm"
                  onClick={handleSaveEdits}
                  disabled={saveNotesMutation.isPending}
                  data-testid="button-save-edit"
                >
                  <Save className="w-4 h-4 mr-2" />
                  {saveNotesMutation.isPending ? "Saving..." : "Save Changes"}
                </Button>
              </>
            )}
            <Button
              variant="outline"
              size="sm"
              onClick={handlePrint}
              data-testid="button-print-report"
            >
              <Printer className="w-4 h-4 mr-2" />
              Print
            </Button>
          </div>
        </div>
      </div>

      {/* Report Content */}
      <div className="max-w-6xl mx-auto p-6 space-y-8">
        {/* Report Header */}
        <Card className="print-break-inside-avoid">
          <CardHeader className="space-y-4">
            <div className="flex items-start justify-between gap-4">
              <div className="space-y-2">
                <CardTitle className="text-3xl">Inspection Report</CardTitle>
                <CardDescription className="text-lg">
                  {propertyName}
                </CardDescription>
              </div>
              {canEdit ? (
                <div className="flex flex-col gap-1 min-w-[180px]">
                  <Label className="text-xs text-muted-foreground">Status</Label>
                  <Select
                    value={inspection.status}
                    onValueChange={(value) => updateStatusMutation.mutate(value)}
                    disabled={updateStatusMutation.isPending}
                  >
                    <SelectTrigger data-testid="select-inspection-status" className="h-8">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="scheduled">Scheduled</SelectItem>
                      <SelectItem value="in_progress">In Progress</SelectItem>
                      <SelectItem value="completed">Completed</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              ) : (
                inspection.status && (
                  <Badge variant={inspection.status === 'completed' ? 'default' : 'secondary'} className="text-sm">
                    {inspection.status.replace(/_/g, ' ').toUpperCase()}
                  </Badge>
                )
              )}
            </div>

            {/* Inspection Metadata Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 pt-4 border-t">
              <div className="flex items-start gap-3">
                <MapPin className="w-5 h-5 text-muted-foreground mt-0.5" />
                <div>
                  <div className="text-sm font-medium">Property Address</div>
                  <div className="text-sm text-muted-foreground">{propertyAddress}</div>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <FileText className="w-5 h-5 text-muted-foreground mt-0.5" />
                <div>
                  <div className="text-sm font-medium">Inspection Type</div>
                  <div className="text-sm text-muted-foreground capitalize">
                    {inspection.type ? inspection.type.replace(/_/g, ' ') : 'Not specified'}
                  </div>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <User className="w-5 h-5 text-muted-foreground mt-0.5" />
                <div>
                  <div className="text-sm font-medium">Inspector</div>
                  <div className="text-sm text-muted-foreground">{inspectorName}</div>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <Calendar className="w-5 h-5 text-muted-foreground mt-0.5" />
                <div>
                  <div className="text-sm font-medium">Inspection Date</div>
                  <div className="text-sm text-muted-foreground">
                    {inspection.completedDate 
                      ? format(new Date(inspection.completedDate), "MMMM dd, yyyy")
                      : inspection.scheduledDate 
                        ? format(new Date(inspection.scheduledDate), "MMMM dd, yyyy")
                        : "Not scheduled"}
                  </div>
                </div>
              </div>
            </div>

            {inspection.notes && (
              <div className="pt-4 border-t">
                <div className="text-sm font-medium mb-2">General Notes</div>
                <div className="text-sm text-muted-foreground whitespace-pre-wrap">{inspection.notes}</div>
              </div>
            )}
          </CardHeader>
        </Card>

        {/* Inspection Points */}
        {sections.length === 0 ? (
          <Card>
            <CardContent className="pt-6 text-center text-muted-foreground">
              No inspection template structure available
            </CardContent>
          </Card>
        ) : (
          sections.map((section) => {
            const sectionEntries = entries.filter(e => e.sectionRef === section.id);

            return (
              <Card key={section.id} className="print-break-inside-avoid" data-testid={`section-${section.id}`}>
                <CardHeader>
                  <CardTitle className="text-2xl">{section.title}</CardTitle>
                  {section.description && (
                    <CardDescription>{section.description}</CardDescription>
                  )}
                </CardHeader>
                <CardContent className="space-y-6">
                  {section.fields.map((field) => {
                    const entry = getEntryValue(section.id, field.id || field.key || field.label);
                    const entryKey = `${section.id}-${field.id || field.key || field.label}`;

                    if (!entry && !editMode) return null; // Hide unfilled fields in view mode

                    return (
                      <div 
                        key={field.id || field.key || field.label}
                        className="border-b pb-6 last:border-b-0 last:pb-0"
                        data-testid={`field-${field.id || field.key}`}
                      >
                        <div className="space-y-3">
                          {/* Field Label and Description */}
                          <div>
                            <h4 className="text-base font-semibold">
                              {field.label}
                              {field.required && <span className="text-destructive ml-1">*</span>}
                            </h4>
                            {field.description && (
                              <p className="text-sm text-muted-foreground mt-1">{field.description}</p>
                            )}
                          </div>

                          {/* Field Value */}
                          <div className="pl-4 border-l-2 border-primary/20">
                            {renderFieldValue(entry?.valueJson, field)}
                          </div>

                          {/* Photos */}
                          {entry?.photos && entry.photos.length > 0 && (
                            <div className="space-y-2">
                              <div className="text-sm font-medium">Photos</div>
                              <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                                {entry.photos.map((photo: string, idx: number) => (
                                  <img
                                    key={idx}
                                    src={photo}
                                    alt={`${field.label} - Photo ${idx + 1}`}
                                    className="w-full h-40 object-cover rounded-lg border"
                                    data-testid={`photo-${field.id || field.key}-${idx}`}
                                  />
                                ))}
                              </div>
                            </div>
                          )}

                          {/* Notes */}
                          <div className="space-y-2">
                            <div className="text-sm font-medium">Notes</div>
                            {editMode ? (
                              <Textarea
                                value={editedNotes[entryKey] || ""}
                                onChange={(e) => setEditedNotes({ ...editedNotes, [entryKey]: e.target.value })}
                                placeholder="Add inspection notes..."
                                className="min-h-[80px]"
                                data-testid={`textarea-note-${field.id || field.key}`}
                              />
                            ) : entry?.note ? (
                              <div className="text-sm text-muted-foreground whitespace-pre-wrap bg-muted/50 p-3 rounded-lg">
                                {entry.note}
                              </div>
                            ) : (
                              <p className="text-sm text-muted-foreground italic">No notes</p>
                            )}
                          </div>

                          {/* Action Buttons - Hidden in print and edit mode */}
                          {!editMode && entry && (
                            <div className="flex gap-2 flex-wrap no-print">
                              {/* Raise Maintenance Request - only if property exists */}
                              {inspection.propertyId && (
                                <Button
                                  variant="outline"
                                  size="sm"
                                  onClick={() => handleOpenMaintenanceDialog(entry.id, field.label, section.title)}
                                  data-testid={`button-maintenance-${field.id || field.key}`}
                                >
                                  <Wrench className="w-4 h-4 mr-2" />
                                  Raise Maintenance Request
                                </Button>
                              )}
                              {/* Add to Comparison - only on check-out inspections with photos and property */}
                              {inspection.type === 'check_out' && entry.photos && entry.photos.length > 0 && inspection.propertyId && (
                                <Button
                                  variant="outline"
                                  size="sm"
                                  onClick={() => autoCreateComparisonMutation.mutate({ 
                                    inspectionId: id!, 
                                    fieldKey: field.id || field.key 
                                  })}
                                  disabled={autoCreateComparisonMutation.isPending}
                                  data-testid={`button-comparison-${field.id || field.key}`}
                                >
                                  <GitCompare className="w-4 h-4 mr-2" />
                                  {autoCreateComparisonMutation.isPending ? "Creating..." : "Add to Comparison"}
                                </Button>
                              )}
                            </div>
                          )}

                          {/* Maintenance Flag Indicator */}
                          {entry?.maintenanceFlag && (
                            <div className="flex items-center gap-2 text-sm text-amber-600 dark:text-amber-500">
                              <AlertTriangle className="w-4 h-4" />
                              <span>Flagged for maintenance</span>
                            </div>
                          )}

                          {/* Linked Maintenance Requests */}
                          {entry && (() => {
                            const linkedRequests = maintenanceRequests.filter(
                              (req: any) => req.inspectionEntryId === entry.id
                            );
                            if (linkedRequests.length === 0) return null;

                            return (
                              <div className="space-y-2 mt-4 border-t pt-4">
                                <div className="text-sm font-medium text-muted-foreground">
                                  Related Maintenance Requests ({linkedRequests.length})
                                </div>
                                {linkedRequests.map((request: any) => (
                                  <Card key={request.id} className="bg-muted/30">
                                    <CardContent className="p-3">
                                      <div className="flex items-start justify-between gap-2">
                                        <div className="flex-1">
                                          <div className="font-medium text-sm">{request.title}</div>
                                          {request.description && (
                                            <div className="text-xs text-muted-foreground mt-1">
                                              {request.description}
                                            </div>
                                          )}
                                          <div className="flex items-center gap-2 mt-2">
                                            <Badge variant={
                                              request.status === 'open' ? 'default' :
                                              request.status === 'in_progress' ? 'secondary' :
                                              request.status === 'resolved' ? 'outline' : 'default'
                                            }>
                                              {request.status}
                                            </Badge>
                                            <Badge variant="outline" className="text-xs">
                                              {request.priority}
                                            </Badge>
                                          </div>
                                        </div>
                                        <Button
                                          variant="ghost"
                                          size="sm"
                                          onClick={() => navigate(`/maintenance`)}
                                          data-testid={`link-maintenance-${request.id}`}
                                        >
                                          <ExternalLink className="w-4 h-4" />
                                        </Button>
                                      </div>
                                    </CardContent>
                                  </Card>
                                ))}
                              </div>
                            );
                          })()}
                        </div>
                      </div>
                    );
                  })}
                </CardContent>
              </Card>
            );
          })
        )}
      </div>

      {/* Maintenance Request Dialog */}
      <Dialog open={maintenanceDialogOpen} onOpenChange={setMaintenanceDialogOpen}>
        <DialogContent className="sm:max-w-[500px]">
          <DialogHeader>
            <DialogTitle>Raise Maintenance Request</DialogTitle>
            <DialogDescription>
              Create a maintenance request for this inspection item
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="maintenance-title">Title</Label>
              <Input
                id="maintenance-title"
                value={maintenanceForm.title}
                onChange={(e) => setMaintenanceForm({ ...maintenanceForm, title: e.target.value })}
                placeholder="Brief description of the issue"
                data-testid="input-maintenance-title"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="maintenance-description">Description</Label>
              <Textarea
                id="maintenance-description"
                value={maintenanceForm.description}
                onChange={(e) => setMaintenanceForm({ ...maintenanceForm, description: e.target.value })}
                placeholder="Detailed description of the maintenance issue..."
                className="min-h-[100px]"
                data-testid="textarea-maintenance-description"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="maintenance-priority">Priority</Label>
              <Select
                value={maintenanceForm.priority}
                onValueChange={(value: any) => setMaintenanceForm({ ...maintenanceForm, priority: value })}
              >
                <SelectTrigger id="maintenance-priority" data-testid="select-maintenance-priority">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="low">Low</SelectItem>
                  <SelectItem value="medium">Medium</SelectItem>
                  <SelectItem value="high">High</SelectItem>
                  <SelectItem value="urgent">Urgent</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setMaintenanceDialogOpen(false)}
              data-testid="button-cancel-maintenance"
            >
              Cancel
            </Button>
            <Button
              onClick={handleSubmitMaintenance}
              disabled={!maintenanceForm.title || createMaintenanceMutation.isPending}
              data-testid="button-submit-maintenance"
            >
              {createMaintenanceMutation.isPending ? "Creating..." : "Create Request"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

