import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { AlertCircle, CheckCircle2, Clock, Circle } from "lucide-react";

interface MonthData {
  month: string;
  status: 'completed' | 'overdue' | 'due' | 'scheduled' | 'not_scheduled';
  count: number;
  completed?: number;
  overdue?: number;
}

interface TemplateCompliance {
  templateId: string;
  templateName: string;
  monthData: MonthData[];
  complianceRate: number;
  totalScheduled: number;
  totalCompleted: number;
}

interface ComplianceReport {
  year: number;
  months: string[];
  templates: TemplateCompliance[];
  overallCompliance: number;
  totalScheduled: number;
  totalCompleted: number;
}

interface ComplianceCalendarProps {
  report: ComplianceReport | null;
  isLoading: boolean;
  entityType: 'property' | 'block';
}

function StatusCell({ data }: { data: MonthData }) {
  const getStatusIcon = () => {
    switch (data.status) {
      case 'completed':
        return <CheckCircle2 className="h-4 w-4 text-green-600" data-testid={`icon-completed-${data.month}`} />;
      case 'overdue':
        return <AlertCircle className="h-4 w-4 text-destructive" data-testid={`icon-overdue-${data.month}`} />;
      case 'due':
        return <Clock className="h-4 w-4 text-yellow-600" data-testid={`icon-due-${data.month}`} />;
      case 'scheduled':
        return <Circle className="h-4 w-4 text-blue-600" data-testid={`icon-scheduled-${data.month}`} />;
      case 'not_scheduled':
      default:
        return <Circle className="h-4 w-4 text-muted-foreground/30" data-testid={`icon-not-scheduled-${data.month}`} />;
    }
  };

  const getStatusColor = () => {
    switch (data.status) {
      case 'completed':
        return 'bg-green-100 dark:bg-green-950 border-green-300 dark:border-green-800';
      case 'overdue':
        return 'bg-destructive/10 border-destructive/30';
      case 'due':
        return 'bg-yellow-100 dark:bg-yellow-950 border-yellow-300 dark:border-yellow-800';
      case 'scheduled':
        return 'bg-blue-100 dark:bg-blue-950 border-blue-300 dark:border-blue-800';
      case 'not_scheduled':
      default:
        return 'bg-muted/30 border-border/30';
    }
  };

  const getStatusLabel = () => {
    switch (data.status) {
      case 'completed':
        return `${data.month}: Completed - ${data.completed || 0} of ${data.count} inspections completed`;
      case 'overdue':
        return `${data.month}: Overdue - ${data.overdue || 0} of ${data.count} inspections overdue`;
      case 'due':
        return `${data.month}: Due Soon - ${data.count} inspections due within 30 days`;
      case 'scheduled':
        return `${data.month}: Scheduled - ${data.count} inspections scheduled`;
      case 'not_scheduled':
      default:
        return `${data.month}: Not Scheduled - No inspections scheduled`;
    }
  };

  const statusLabel = getStatusLabel();

  return (
    <div 
      className={`flex items-center justify-center p-2 rounded-md border ${getStatusColor()} hover-elevate transition-all`}
      title={statusLabel}
      aria-label={statusLabel}
      role="status"
      data-testid={`cell-${data.status}-${data.month}`}
    >
      {getStatusIcon()}
    </div>
  );
}

export default function ComplianceCalendar({ report, isLoading, entityType }: ComplianceCalendarProps) {
  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Annual Inspection Compliance</CardTitle>
          <CardDescription>Loading compliance report...</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="h-64 flex items-center justify-center">
            <div className="text-muted-foreground">Loading...</div>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!report || report.templates.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Annual Inspection Compliance</CardTitle>
          <CardDescription>
            Track inspection compliance across all months for {entityType === 'property' ? 'this property' : 'this block'}
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="py-12 text-center">
            <AlertCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">No Inspection Templates</h3>
            <p className="text-muted-foreground">
              Create inspection templates and schedule inspections to track compliance.
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  // Abbreviate month names for better mobile display
  const monthAbbreviations = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between gap-4">
          <div>
            <CardTitle className="flex items-center gap-2">
              Annual Inspection Compliance
              <Badge variant="outline" className="ml-2" data-testid="badge-overall-compliance">
                {report.overallCompliance}% Overall
              </Badge>
            </CardTitle>
            <CardDescription className="mt-1">
              {report.year}  {report.totalCompleted} of {report.totalScheduled} inspections completed
            </CardDescription>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {/* Legend */}
        <div className="mb-6 flex flex-wrap gap-4 text-sm">
          <div className="flex items-center gap-2">
            <CheckCircle2 className="h-4 w-4 text-green-600" />
            <span className="text-muted-foreground">Completed</span>
          </div>
          <div className="flex items-center gap-2">
            <AlertCircle className="h-4 w-4 text-destructive" />
            <span className="text-muted-foreground">Overdue</span>
          </div>
          <div className="flex items-center gap-2">
            <Clock className="h-4 w-4 text-yellow-600" />
            <span className="text-muted-foreground">Due Soon</span>
          </div>
          <div className="flex items-center gap-2">
            <Circle className="h-4 w-4 text-blue-600" />
            <span className="text-muted-foreground">Scheduled</span>
          </div>
          <div className="flex items-center gap-2">
            <Circle className="h-4 w-4 text-muted-foreground/30" />
            <span className="text-muted-foreground">Not Scheduled</span>
          </div>
        </div>

        {/* Compliance Grid */}
        <div className="overflow-x-auto">
          <div className="min-w-[800px]">
            {/* Header Row */}
            <div className="grid gap-2 mb-2" style={{ gridTemplateColumns: '200px repeat(12, 1fr) 80px' }}>
              <div className="font-semibold text-sm p-2">Template</div>
              {monthAbbreviations.map((month, idx) => (
                <div key={month} className="font-semibold text-sm text-center p-2" data-testid={`header-${report.months[idx]}`}>
                  {month}
                </div>
              ))}
              <div className="font-semibold text-sm text-center p-2">Rate</div>
            </div>

            {/* Template Rows */}
            {report.templates.map((template) => (
              <div 
                key={template.templateId} 
                className="grid gap-2 mb-2 items-center"
                style={{ gridTemplateColumns: '200px repeat(12, 1fr) 80px' }}
                data-testid={`row-template-${template.templateId}`}
              >
                {/* Template Name */}
                <div className="text-sm font-medium p-2 truncate" title={template.templateName}>
                  {template.templateName}
                </div>

                {/* Month Cells */}
                {template.monthData.map((monthData) => (
                  <StatusCell key={monthData.month} data={monthData} />
                ))}

                {/* Compliance Rate */}
                <div className="text-center p-2">
                  <Badge 
                    variant={template.complianceRate >= 80 ? "default" : template.complianceRate >= 60 ? "secondary" : "destructive"}
                    data-testid={`badge-rate-${template.templateId}`}
                  >
                    {template.complianceRate}%
                  </Badge>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Summary Stats */}
        <div className="mt-6 pt-6 border-t grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <div className="text-sm text-muted-foreground">Total Inspections</div>
            <div className="text-2xl font-bold" data-testid="stat-total-scheduled">{report.totalScheduled}</div>
          </div>
          <div>
            <div className="text-sm text-muted-foreground">Completed</div>
            <div className="text-2xl font-bold text-green-600" data-testid="stat-total-completed">{report.totalCompleted}</div>
          </div>
          <div>
            <div className="text-sm text-muted-foreground">Pending</div>
            <div className="text-2xl font-bold text-yellow-600" data-testid="stat-total-pending">
              {report.totalScheduled - report.totalCompleted}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/pages/BlockTenants.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useRoute, Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, Users, Home, DollarSign, Percent, Mail, Phone, Building2, Calendar, MessageSquare } from "lucide-react";
import { format } from "date-fns";
import { BroadcastDialog } from "@/components/BroadcastDialog";

interface TenantAssignment {
  user: {
    id: string;
    firstName?: string;
    lastName?: string;
    email: string;
    phone?: string;
    profileImageUrl?: string;
  };
  property: {
    id: string;
    name: string;
    address: string;
  };
  assignment: {
    leaseStartDate?: Date | string;
    leaseEndDate?: Date | string;
    monthlyRent?: string;
    depositAmount?: string;
    isActive: boolean;
  };
}

interface BlockTenantsData {
  stats: {
    totalUnits: number;
    occupiedUnits: number;
    occupancyRate: number;
    totalMonthlyRent: number;
  };
  tenants: TenantAssignment[];
}

interface Block {
  id: string;
  name: string;
  address: string;
}

export default function BlockTenants() {
  const [, params] = useRoute("/blocks/:id/tenants");
  const blockId = params?.id;
  const [broadcastDialogOpen, setBroadcastDialogOpen] = useState(false);

  const { data: block, isLoading: blockLoading } = useQuery<Block>({
    queryKey: ["/api/blocks", blockId],
    queryFn: async () => {
      const res = await fetch(`/api/blocks/${blockId}`, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch block");
      return res.json();
    },
    enabled: !!blockId,
  });

  const { data: tenantsData, isLoading: tenantsLoading } = useQuery<BlockTenantsData>({
    queryKey: ["/api/blocks", blockId, "tenants"],
    queryFn: async () => {
      const res = await fetch(`/api/blocks/${blockId}/tenants`, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch tenants");
      return res.json();
    },
    enabled: !!blockId,
  });

  if (blockLoading || tenantsLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="text-center py-12">Loading...</div>
      </div>
    );
  }

  if (!block) {
    return (
      <div className="container mx-auto p-6">
        <div className="text-center py-12">
          <p className="text-muted-foreground">Block not found</p>
          <Link href="/blocks">
            <Button variant="outline" className="mt-4">
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Blocks
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  const stats = tenantsData?.stats || { totalUnits: 0, occupiedUnits: 0, occupancyRate: 0, totalMonthlyRent: 0 };
  const tenants = tenantsData?.tenants || [];

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div>
        <Link href={`/blocks/${block.id}`}>
          <Button variant="ghost" className="mb-4" data-testid="button-back-to-block">
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to {block.name}
          </Button>
        </Link>

        <div className="flex items-start justify-between">
          <div>
            <h1 className="text-3xl font-bold flex items-center gap-2">
              <Users className="h-8 w-8 text-primary" />
              {block.name} - Tenants
            </h1>
            <p className="text-muted-foreground mt-2">
              Tenant occupancy and property assignments for {block.name}
            </p>
          </div>

          <Button
            onClick={() => setBroadcastDialogOpen(true)}
            disabled={stats.occupiedUnits === 0}
            data-testid="button-broadcast-message"
          >
            <MessageSquare className="h-4 w-4 mr-2" />
            Broadcast Message
          </Button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card data-testid="card-total-units">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Units</CardTitle>
            <Home className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.totalUnits}</div>
            <p className="text-xs text-muted-foreground">
              Properties in this block
            </p>
          </CardContent>
        </Card>

        <Card data-testid="card-occupied-units">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Occupied Units</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.occupiedUnits}</div>
            <p className="text-xs text-muted-foreground">
              Active tenant assignments
            </p>
          </CardContent>
        </Card>

        <Card data-testid="card-occupancy-rate">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Occupancy Rate</CardTitle>
            <Percent className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.occupancyRate.toFixed(1)}%</div>
            <p className="text-xs text-muted-foreground">
              {stats.occupiedUnits} of {stats.totalUnits} units
            </p>
          </CardContent>
        </Card>

        <Card data-testid="card-total-rent">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Monthly Rent</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              ${stats.totalMonthlyRent.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
            </div>
            <p className="text-xs text-muted-foreground">
              Combined monthly revenue
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Tenants List */}
      <div>
        <h2 className="text-2xl font-semibold mb-4">Tenants</h2>

        {tenants.length === 0 ? (
          <Card>
            <CardContent className="flex flex-col items-center justify-center py-12">
              <Users className="h-12 w-12 text-muted-foreground mb-4" />
              <h3 className="text-lg font-semibold mb-2">No tenants assigned</h3>
              <p className="text-muted-foreground text-center">
                Tenants assigned to properties in this block will appear here
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="grid gap-4">
            {tenants.map((tenant, index) => {
              const fullName = [tenant.user.firstName, tenant.user.lastName].filter(Boolean).join(' ') || 'Unnamed Tenant';
              const initials = fullName
                .split(' ')
                .map(n => n[0])
                .join('')
                .toUpperCase()
                .slice(0, 2);

              return (
                <Card key={index} data-testid={`card-tenant-${index}`} className="hover-elevate">
                  <CardContent className="p-6">
                    <div className="flex flex-col md:flex-row gap-6">
                      {/* Tenant Info */}
                      <div className="flex items-start gap-4 flex-1">
                        <Avatar className="h-12 w-12">
                          <AvatarImage src={tenant.user.profileImageUrl} alt={fullName} />
                          <AvatarFallback>{initials}</AvatarFallback>
                        </Avatar>
                        <div className="flex-1 min-w-0">
                          <h3 className="text-lg font-semibold">{fullName}</h3>
                          <div className="space-y-1 mt-2">
                            {tenant.user.email && (
                              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                                <Mail className="h-4 w-4 shrink-0" />
                                <span className="truncate">{tenant.user.email}</span>
                              </div>
                            )}
                            {tenant.user.phone && (
                              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                                <Phone className="h-4 w-4 shrink-0" />
                                <span>{tenant.user.phone}</span>
                              </div>
                            )}
                          </div>
                        </div>
                        <Badge variant={tenant.assignment.isActive ? "default" : "secondary"}>
                          {tenant.assignment.isActive ? "Active" : "Inactive"}
                        </Badge>
                      </div>

                      {/* Property Info */}
                      <div className="border-t md:border-t-0 md:border-l pt-4 md:pt-0 md:pl-6 flex-1">
                        <Link href={`/properties/${tenant.property.id}`}>
                          <div className="flex items-start gap-3 hover-elevate p-2 -m-2 rounded-md cursor-pointer">
                            <Building2 className="h-5 w-5 text-primary shrink-0 mt-0.5" />
                            <div className="flex-1 min-w-0">
                              <h4 className="font-semibold text-primary">{tenant.property.name}</h4>
                              <p className="text-sm text-muted-foreground truncate">{tenant.property.address}</p>
                            </div>
                          </div>
                        </Link>

                        <div className="mt-3 space-y-2">
                          {tenant.assignment.leaseStartDate && (
                            <div className="flex items-center gap-2 text-sm text-muted-foreground">
                              <Calendar className="h-4 w-4 shrink-0" />
                              <span>
                                Lease: {format(new Date(tenant.assignment.leaseStartDate), 'MMM d, yyyy')}
                                {tenant.assignment.leaseEndDate && (
                                  <> - {format(new Date(tenant.assignment.leaseEndDate), 'MMM d, yyyy')}</>
                                )}
                              </span>
                            </div>
                          )}
                          {tenant.assignment.monthlyRent && (
                            <div className="flex items-center gap-2 text-sm text-muted-foreground">
                              <DollarSign className="h-4 w-4 shrink-0" />
                              <span>Rent: ${parseFloat(tenant.assignment.monthlyRent).toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}/month</span>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        )}
      </div>

      <BroadcastDialog
        blockId={blockId!}
        blockName={block.name}
        blockAddress={block.address}
        tenantCount={stats.occupiedUnits}
        open={broadcastDialogOpen}
        onOpenChange={setBroadcastDialogOpen}
      />
    </div>
  );
}

