import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Mail, Send, FileText, Plus, AlertCircle } from "lucide-react";

interface MessageTemplate {
  id: string;
  name: string;
  subject: string;
  body: string;
  variables: string[];
}

interface BroadcastDialogProps {
  blockId: string;
  blockName: string;
  blockAddress: string;
  tenantCount: number;
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function BroadcastDialog({
  blockId,
  blockName,
  blockAddress,
  tenantCount,
  open,
  onOpenChange,
}: BroadcastDialogProps) {
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState<"template" | "custom">("template");
  const [selectedTemplateId, setSelectedTemplateId] = useState<string>("");
  const [customSubject, setCustomSubject] = useState("");
  const [customBody, setCustomBody] = useState("");
  const [previewSubject, setPreviewSubject] = useState("");
  const [previewBody, setPreviewBody] = useState("");

  const { data: templates = [], isLoading: templatesLoading } = useQuery<MessageTemplate[]>({
    queryKey: ["/api/message-templates"],
    enabled: open,
  });

  const broadcastMutation = useMutation({
    mutationFn: async (data: { templateId?: string; subject?: string; body?: string }) => {
      return apiRequest(`/api/blocks/${blockId}/broadcast`, "POST", data);
    },
    onSuccess: (result: any) => {
      const successCount = result.results?.filter((r: any) => r.success).length || 0;
      const failureCount = result.results?.filter((r: any) => !r.success).length || 0;

      toast({
        title: "Broadcast sent",
        description: `Successfully sent to ${successCount} tenant${successCount !== 1 ? 's' : ''}${failureCount > 0 ? `. Failed to send to ${failureCount}.` : ''}`,
      });
      onOpenChange(false);
      resetForm();
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to send broadcast",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setActiveTab("template");
    setSelectedTemplateId("");
    setCustomSubject("");
    setCustomBody("");
    setPreviewSubject("");
    setPreviewBody("");
  };

  const handleTemplateChange = (templateId: string) => {
    setSelectedTemplateId(templateId);
    const template = templates.find(t => t.id === templateId);
    if (template) {
      updatePreview(template.subject, template.body);
    }
  };

  const updatePreview = (subject: string, body: string) => {
    const variables: Record<string, string> = {
      '{block_name}': blockName,
      '{block_address}': blockAddress,
      '{tenant_name}': '[Tenant Name]',
      '{tenant_email}': '[tenant@example.com]',
      '{property_name}': '[Property Name]',
      '{sender_name}': '[Your Name]',
    };

    let previewSubj = subject;
    let previewBod = body;

    Object.entries(variables).forEach(([key, value]) => {
      previewSubj = previewSubj.replaceAll(key, value);
      previewBod = previewBod.replaceAll(key, value);
    });

    setPreviewSubject(previewSubj);
    setPreviewBody(previewBod);
  };

  const handleCustomSubjectChange = (value: string) => {
    setCustomSubject(value);
    updatePreview(value, customBody);
  };

  const handleCustomBodyChange = (value: string) => {
    setCustomBody(value);
    updatePreview(customSubject, value);
  };

  const handleSend = () => {
    if (activeTab === "template") {
      if (!selectedTemplateId) {
        toast({
          title: "Template required",
          description: "Please select a template to send",
          variant: "destructive",
        });
        return;
      }
      broadcastMutation.mutate({ templateId: selectedTemplateId });
    } else {
      if (!customSubject.trim() || !customBody.trim()) {
        toast({
          title: "Message required",
          description: "Please provide both subject and message body",
          variant: "destructive",
        });
        return;
      }
      broadcastMutation.mutate({ subject: customSubject, body: customBody });
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Mail className="h-5 w-5 text-primary" />
            Broadcast Message to Tenants
          </DialogTitle>
          <DialogDescription>
            Send a message to all {tenantCount} active tenant{tenantCount !== 1 ? 's' : ''} in {blockName}
          </DialogDescription>
        </DialogHeader>

        <Tabs value={activeTab} onValueChange={(v) => setActiveTab(v as "template" | "custom")}>
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="template" data-testid="tab-template">
              <FileText className="h-4 w-4 mr-2" />
              Use Template
            </TabsTrigger>
            <TabsTrigger value="custom" data-testid="tab-custom">
              <Plus className="h-4 w-4 mr-2" />
              Custom Message
            </TabsTrigger>
          </TabsList>

          <TabsContent value="template" className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="template-select">Select Message Template</Label>
              {templatesLoading ? (
                <div className="text-sm text-muted-foreground">Loading templates...</div>
              ) : templates.length === 0 ? (
                <Card className="border-dashed">
                  <CardContent className="flex flex-col items-center justify-center py-8 text-center">
                    <FileText className="h-8 w-8 text-muted-foreground mb-2" />
                    <p className="text-sm text-muted-foreground">
                      No templates available. Switch to custom message or create templates from the settings.
                    </p>
                  </CardContent>
                </Card>
              ) : (
                <Select value={selectedTemplateId} onValueChange={handleTemplateChange}>
                  <SelectTrigger id="template-select" data-testid="select-template">
                    <SelectValue placeholder="Choose a template..." />
                  </SelectTrigger>
                  <SelectContent>
                    {templates.map((template) => (
                      <SelectItem key={template.id} value={template.id}>
                        {template.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              )}
            </div>

            {selectedTemplateId && (
              <Card className="bg-muted/50">
                <CardContent className="pt-6 space-y-3">
                  <div>
                    <Label className="text-xs text-muted-foreground">Subject</Label>
                    <p className="text-sm font-medium" data-testid="text-template-subject">
                      {templates.find(t => t.id === selectedTemplateId)?.subject}
                    </p>
                  </div>
                  <div>
                    <Label className="text-xs text-muted-foreground">Message</Label>
                    <p className="text-sm whitespace-pre-wrap" data-testid="text-template-body">
                      {templates.find(t => t.id === selectedTemplateId)?.body}
                    </p>
                  </div>
                </CardContent>
              </Card>
            )}
          </TabsContent>

          <TabsContent value="custom" className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="custom-subject">Subject</Label>
              <Input
                id="custom-subject"
                placeholder="Enter message subject..."
                value={customSubject}
                onChange={(e) => handleCustomSubjectChange(e.target.value)}
                data-testid="input-custom-subject"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="custom-body">Message</Label>
              <Textarea
                id="custom-body"
                placeholder="Enter your message here..."
                value={customBody}
                onChange={(e) => handleCustomBodyChange(e.target.value)}
                rows={8}
                data-testid="textarea-custom-body"
              />
            </div>

            <Card className="bg-muted/50 border-dashed">
              <CardContent className="pt-6">
                <div className="flex items-start gap-2">
                  <AlertCircle className="h-4 w-4 text-muted-foreground mt-0.5 shrink-0" />
                  <div className="space-y-1 text-xs text-muted-foreground">
                    <p className="font-medium">Available Variables:</p>
                    <p>{'{tenant_name}'}, {'{tenant_email}'}, {'{block_name}'}, {'{block_address}'}, {'{property_name}'}, {'{sender_name}'}</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>

        {(previewSubject || previewBody) && (
          <div className="space-y-2">
            <Label className="text-sm font-semibold">Preview</Label>
            <Card className="border-primary/20">
              <CardContent className="pt-6 space-y-3">
                <div>
                  <Label className="text-xs text-muted-foreground">Subject</Label>
                  <p className="text-sm font-medium" data-testid="text-preview-subject">{previewSubject}</p>
                </div>
                <div>
                  <Label className="text-xs text-muted-foreground">Message</Label>
                  <p className="text-sm whitespace-pre-wrap" data-testid="text-preview-body">{previewBody}</p>
                </div>
              </CardContent>
            </Card>
          </div>
        )}

        <DialogFooter>
          <Button variant="outline" onClick={() => onOpenChange(false)} data-testid="button-cancel">
            Cancel
          </Button>
          <Button
            onClick={handleSend}
            disabled={broadcastMutation.isPending || tenantCount === 0}
            data-testid="button-send-broadcast"
          >
            {broadcastMutation.isPending ? (
              <>Sending...</>
            ) : (
              <>
                <Send className="h-4 w-4 mr-2" />
                Send to {tenantCount} Tenant{tenantCount !== 1 ? 's' : ''}
              </>
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/EditTenantDialog.t.content:
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { z } from "zod";
import { updateTenantAssignmentSchema } from "@shared/schema";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  FormDescription,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Loader2 } from "lucide-react";

const formSchema = z.object({
  leaseStartDate: z.string().optional(),
  leaseEndDate: z.string().optional(),
  monthlyRent: z.string().optional(),
  depositAmount: z.string().optional(),
  isActive: z.boolean(),
});

type FormData = z.infer<typeof formSchema>;

interface TenantAssignment {
  id: string;
  firstName?: string;
  lastName?: string;
  email: string;
  assignment: {
    id: string;
    leaseStartDate?: Date | string;
    leaseEndDate?: Date | string;
    monthlyRent?: string;
    depositAmount?: string;
    isActive: boolean;
  };
}

interface EditTenantDialogProps {
  propertyId: string;
  tenant: TenantAssignment;
  children: React.ReactNode;
  onSuccess?: () => void;
}

export default function EditTenantDialog({
  propertyId,
  tenant,
  children,
  onSuccess,
}: EditTenantDialogProps) {
  const [open, setOpen] = useState(false);
  const { toast } = useToast();

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      leaseStartDate: "",
      leaseEndDate: "",
      monthlyRent: "",
      depositAmount: "",
      isActive: true,
    },
  });

  useEffect(() => {
    if (open && tenant) {
      const leaseStartDate = tenant.assignment.leaseStartDate
        ? new Date(tenant.assignment.leaseStartDate).toISOString().split("T")[0]
        : "";
      const leaseEndDate = tenant.assignment.leaseEndDate
        ? new Date(tenant.assignment.leaseEndDate).toISOString().split("T")[0]
        : "";

      form.reset({
        leaseStartDate,
        leaseEndDate,
        monthlyRent: tenant.assignment.monthlyRent || "",
        depositAmount: tenant.assignment.depositAmount || "",
        isActive: tenant.assignment.isActive,
      });
    }
  }, [open, tenant, form]);

  const updateMutation = useMutation({
    mutationFn: async (data: FormData) => {
      // Transform form data to match API expectations
      const payload = updateTenantAssignmentSchema.parse({
        leaseStartDate: data.leaseStartDate ? new Date(data.leaseStartDate) : null,
        leaseEndDate: data.leaseEndDate ? new Date(data.leaseEndDate) : null,
        monthlyRent: data.monthlyRent && data.monthlyRent.trim() !== "" ? data.monthlyRent : null,
        depositAmount: data.depositAmount && data.depositAmount.trim() !== "" ? data.depositAmount : null,
        isActive: data.isActive,
      });
      return apiRequest(`/api/tenant-assignments/${tenant.assignment.id}`, "PUT", payload);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/properties", propertyId, "tenants"] });
      toast({
        title: "Assignment updated",
        description: "The tenant assignment has been successfully updated",
      });
      setOpen(false);
      onSuccess?.();
    },
    onError: () => {
      toast({
        title: "Failed to update assignment",
        description: "An error occurred while updating the tenant assignment",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: FormData) => {
    updateMutation.mutate(data);
  };

  const fullName = [tenant.firstName, tenant.lastName].filter(Boolean).join(" ") || tenant.email;

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>{children}</DialogTrigger>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>Edit Tenant Assignment</DialogTitle>
          <DialogDescription>Update lease details for {fullName}</DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="leaseStartDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Lease Start Date</FormLabel>
                    <FormControl>
                      <Input
                        type="date"
                        {...field}
                        data-testid="input-lease-start"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="leaseEndDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Lease End Date</FormLabel>
                    <FormControl>
                      <Input
                        type="date"
                        {...field}
                        data-testid="input-lease-end"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="monthlyRent"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Monthly Rent</FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        step="0.01"
                        placeholder="0.00"
                        {...field}
                        data-testid="input-monthly-rent"
                      />
                    </FormControl>
                    <FormDescription>In dollars</FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="depositAmount"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Deposit Amount</FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        step="0.01"
                        placeholder="0.00"
                        {...field}
                        data-testid="input-deposit"
                      />
                    </FormControl>
                    <FormDescription>In dollars</FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="isActive"
              render={({ field }) => (
                <FormItem className="flex flex-row items-center justify-between rounded-lg border p-4">
                  <div className="space-y-0.5">
                    <FormLabel className="text-base">Active Status</FormLabel>
                    <FormDescription>
                      Mark this tenant assignment as active or inactive
                    </FormDescription>
                  </div>
                  <FormControl>
                    <Switch
                      checked={field.value}
                      onCheckedChange={field.onChange}
                      data-testid="switch-is-active"
                    />
                  </FormControl>
                </FormItem>
              )}
            />

            <div className="flex justify-end gap-2 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setOpen(false)}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={updateMutation.isPending}
                data-testid="button-submit"
              >
                {updateMutation.isPending ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Updating...
                  </>
                ) : (
                  "Update Assignment"
                )}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/AddTenantDialog.ts.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { z } from "zod";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  FormDescription,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Loader2, UserPlus, Users } from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

interface User {
  id: string;
  firstName?: string;
  lastName?: string;
  email: string;
  role: string;
}

const selectTenantFormSchema = z.object({
  tenantId: z.string().min(1, "Please select a tenant"),
  leaseStartDate: z.string().optional(),
  leaseEndDate: z.string().optional(),
  monthlyRent: z.string().optional(),
  depositAmount: z.string().optional(),
  isActive: z.boolean().default(true),
});

const createTenantFormSchema = z.object({
  email: z.string().email("Invalid email address"),
  firstName: z.string().min(1, "First name is required").max(255),
  lastName: z.string().min(1, "Last name is required").max(255),
  username: z.string().min(3, "Username must be at least 3 characters").max(100),
  password: z.string().min(6, "Password must be at least 6 characters"),
  leaseStartDate: z.string().optional(),
  leaseEndDate: z.string().optional(),
  monthlyRent: z.string().optional(),
  depositAmount: z.string().optional(),
  isActive: z.boolean().default(true),
});

type SelectTenantFormData = z.infer<typeof selectTenantFormSchema>;
type CreateTenantFormData = z.infer<typeof createTenantFormSchema>;

interface AddTenantDialogProps {
  propertyId: string;
  children: React.ReactNode;
  onSuccess?: () => void;
}

export default function AddTenantDialog({ propertyId, children, onSuccess }: AddTenantDialogProps) {
  const [open, setOpen] = useState(false);
  const [mode, setMode] = useState<"select" | "create">("select");
  const { toast } = useToast();

  const { data: tenantUsers = [], isLoading: loadingTenants } = useQuery<User[]>({
    queryKey: ["/api/users/role/tenant"],
    queryFn: async () => {
      const res = await fetch("/api/users/role/tenant", { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch tenant users");
      return res.json();
    },
    enabled: open,
  });

  const selectForm = useForm<SelectTenantFormData>({
    resolver: zodResolver(selectTenantFormSchema),
    defaultValues: {
      tenantId: "",
      leaseStartDate: "",
      leaseEndDate: "",
      monthlyRent: "",
      depositAmount: "",
      isActive: true,
    },
  });

  const createForm = useForm<CreateTenantFormData>({
    resolver: zodResolver(createTenantFormSchema),
    defaultValues: {
      email: "",
      firstName: "",
      lastName: "",
      username: "",
      password: "",
      leaseStartDate: "",
      leaseEndDate: "",
      monthlyRent: "",
      depositAmount: "",
      isActive: true,
    },
  });

  const createUserMutation = useMutation<User, Error, CreateTenantFormData>({
    mutationFn: async (data: CreateTenantFormData) => {
      // Create the user first via team member API
      const userPayload = {
        email: data.email,
        firstName: data.firstName,
        lastName: data.lastName,
        username: data.username,
        password: data.password,
        role: "tenant" as const,
      };
      const res = await apiRequest("POST", "/api/team", userPayload);

      if (!res.ok) {
        const error = await res.json().catch(() => ({ message: "Failed to create tenant user" }));

        // Handle specific error cases
        if (res.status === 401) {
          throw new Error("Your session has expired. Please refresh the page and log in again.");
        } else if (res.status === 403) {
          throw new Error("Only organization owners can create tenant users. Please contact your administrator.");
        } else if (error.message === "Email or username already exists") {
          throw new Error("A user with this email or username already exists in your organization.");
        } else {
          throw new Error(error.message || "Failed to create tenant user");
        }
      }

      return await res.json() as User;
    },
  });

  const assignTenantMutation = useMutation({
    mutationFn: async (data: { tenantId: string; leaseData: any }) => {
      // Build payload - convert string numbers to actual numbers for schema validation
      const payload = {
        propertyId,
        tenantId: data.tenantId,
        leaseStartDate: data.leaseData.leaseStartDate || undefined,
        leaseEndDate: data.leaseData.leaseEndDate || undefined,
        monthlyRent: data.leaseData.monthlyRent ? parseFloat(data.leaseData.monthlyRent) : undefined,
        depositAmount: data.leaseData.depositAmount ? parseFloat(data.leaseData.depositAmount) : undefined,
        isActive: data.leaseData.isActive,
      };
      return apiRequest("POST", "/api/tenant-assignments", payload);
    },
    onSuccess: async () => {
      // Force active refetch to update the UI immediately
      await queryClient.invalidateQueries({ 
        queryKey: ["/api/properties", propertyId, "tenants"],
        refetchType: "active"
      });
      await queryClient.invalidateQueries({ 
        queryKey: ["/api/users/role/tenant"],
        refetchType: "active"
      });
      toast({
        title: "Tenant assigned",
        description: "The tenant has been successfully assigned to this property",
      });
      selectForm.reset();
      createForm.reset();
      setOpen(false);
      setMode("select");
      onSuccess?.();
    },
    onError: async (error: any) => {
      // Try to get detailed error message from response
      let errorMessage = "An error occurred while assigning the tenant";
      try {
        if (error.response) {
          const errorData = await error.response.json().catch(() => null);
          if (errorData?.details) {
            errorMessage = JSON.stringify(errorData.details);
          } else if (errorData?.error) {
            errorMessage = errorData.error;
          }
        }
      } catch (e) {
        // Use default error message
      }

      console.error("Tenant assignment error:", error, errorMessage);
      toast({
        title: "Failed to assign tenant",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const onSelectSubmit = async (data: SelectTenantFormData) => {
    assignTenantMutation.mutate({
      tenantId: data.tenantId,
      leaseData: data,
    });
  };

  const onCreateSubmit = async (data: CreateTenantFormData) => {
    try {
      // First create the user
      const newUser = await createUserMutation.mutateAsync(data);

      if (!newUser || !newUser.id) {
        throw new Error("User creation failed - no user ID returned");
      }

      console.log("User created successfully:", newUser.id);

      // Extract only lease-related fields for the assignment
      const leaseData = {
        leaseStartDate: data.leaseStartDate,
        leaseEndDate: data.leaseEndDate,
        monthlyRent: data.monthlyRent,
        depositAmount: data.depositAmount,
        isActive: data.isActive,
      };

      console.log("Assigning tenant to property:", { tenantId: newUser.id, leaseData });

      // Then assign them to the property
      assignTenantMutation.mutate({
        tenantId: newUser.id,
        leaseData,
      });
    } catch (error: any) {
      console.error("Failed to create tenant user:", error);
      toast({
        title: "Failed to create tenant",
        description: error.message || "An error occurred while creating the tenant user",
        variant: "destructive",
      });
      // Return early to prevent assignment attempt
      return;
    }
  };

  const getTenantDisplayName = (user: User) => {
    const fullName = [user.firstName, user.lastName].filter(Boolean).join(" ");
    return fullName || user.email;
  };

  const isSubmitting = createUserMutation.isPending || assignTenantMutation.isPending;

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>{children}</DialogTrigger>
      <DialogContent className="sm:max-w-[550px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Add Tenant Assignment</DialogTitle>
          <DialogDescription>
            {mode === "select" 
              ? "Assign an existing tenant to this property with lease details"
              : "Create a new tenant user and assign them to this property"
            }
          </DialogDescription>
        </DialogHeader>

        <Tabs value={mode} onValueChange={(v) => setMode(v as "select" | "create")} className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="select" data-testid="tab-select-tenant">
              <Users className="h-4 w-4 mr-2" />
              Select Existing
            </TabsTrigger>
            <TabsTrigger value="create" data-testid="tab-create-tenant">
              <UserPlus className="h-4 w-4 mr-2" />
              Create New
            </TabsTrigger>
          </TabsList>

          <TabsContent value="select" className="space-y-4 mt-4">
            <Form {...selectForm}>
              <form onSubmit={selectForm.handleSubmit(onSelectSubmit)} className="space-y-4">
                <FormField
                  control={selectForm.control}
                  name="tenantId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Tenant *</FormLabel>
                      <Select
                        onValueChange={field.onChange}
                        value={field.value}
                        disabled={loadingTenants}
                      >
                        <FormControl>
                          <SelectTrigger data-testid="select-tenant">
                            <SelectValue placeholder="Select a tenant" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {loadingTenants ? (
                            <div className="flex items-center justify-center p-4">
                              <Loader2 className="h-4 w-4 animate-spin" />
                            </div>
                          ) : tenantUsers.length === 0 ? (
                            <div className="p-4 text-sm text-muted-foreground text-center">
                              No tenant users found. Create a new tenant using the "Create New" tab.
                            </div>
                          ) : (
                            tenantUsers.map((user) => (
                              <SelectItem key={user.id} value={user.id}>
                                {getTenantDisplayName(user)}
                              </SelectItem>
                            ))
                          )}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={selectForm.control}
                    name="leaseStartDate"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Lease Start Date</FormLabel>
                        <FormControl>
                          <Input
                            type="date"
                            {...field}
                            data-testid="input-lease-start"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={selectForm.control}
                    name="leaseEndDate"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Lease End Date</FormLabel>
                        <FormControl>
                          <Input
                            type="date"
                            {...field}
                            data-testid="input-lease-end"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <FormField
                    control={selectForm.control}
                    name="monthlyRent"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Monthly Rent</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            step="0.01"
                            placeholder="0.00"
                            {...field}
                            data-testid="input-monthly-rent"
                          />
                        </FormControl>
                        <FormDescription>In dollars</FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={selectForm.control}
                    name="depositAmount"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Deposit Amount</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            step="0.01"
                            placeholder="0.00"
                            {...field}
                            data-testid="input-deposit"
                          />
                        </FormControl>
                        <FormDescription>In dollars</FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={selectForm.control}
                  name="isActive"
                  render={({ field }) => (
                    <FormItem className="flex flex-row items-center justify-between rounded-lg border p-4">
                      <div className="space-y-0.5">
                        <FormLabel className="text-base">Active Status</FormLabel>
                        <FormDescription>
                          Mark this tenant assignment as active or inactive
                        </FormDescription>
                      </div>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                          data-testid="switch-is-active"
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <div className="flex justify-end gap-2 pt-4">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setOpen(false)}
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    disabled={isSubmitting}
                    data-testid="button-submit"
                  >
                    {isSubmitting ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Assigning...
                      </>
                    ) : (
                      "Assign Tenant"
                    )}
                  </Button>
                </div>
              </form>
            </Form>
          </TabsContent>

          <TabsContent value="create" className="space-y-4 mt-4">
            <Form {...createForm}>
              <form onSubmit={createForm.handleSubmit(onCreateSubmit)} className="space-y-4">
                <div className="space-y-4 p-4 border rounded-lg bg-muted/50">
                  <h4 className="text-sm font-medium">Tenant User Details</h4>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={createForm.control}
                      name="firstName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>First Name *</FormLabel>
                          <FormControl>
                            <Input
                              placeholder="John"
                              {...field}
                              data-testid="input-first-name"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={createForm.control}
                      name="lastName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Last Name *</FormLabel>
                          <FormControl>
                            <Input
                              placeholder="Doe"
                              {...field}
                              data-testid="input-last-name"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={createForm.control}
                    name="email"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email *</FormLabel>
                        <FormControl>
                          <Input
                            type="email"
                            placeholder="john.doe@example.com"
                            {...field}
                            data-testid="input-email"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={createForm.control}
                      name="username"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Username *</FormLabel>
                          <FormControl>
                            <Input
                              placeholder="johndoe"
                              {...field}
                              data-testid="input-username"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={createForm.control}
                      name="password"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Password *</FormLabel>
                          <FormControl>
                            <Input
                              type="password"
                              placeholder=""
                              {...field}
                              data-testid="input-password"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>
                </div>

                <div className="space-y-4">
                  <h4 className="text-sm font-medium">Lease Details</h4>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={createForm.control}
                      name="leaseStartDate"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Lease Start Date</FormLabel>
                          <FormControl>
                            <Input
                              type="date"
                              {...field}
                              data-testid="input-create-lease-start"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={createForm.control}
                      name="leaseEndDate"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Lease End Date</FormLabel>
                          <FormControl>
                            <Input
                              type="date"
                              {...field}
                              data-testid="input-create-lease-end"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={createForm.control}
                      name="monthlyRent"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Monthly Rent</FormLabel>
                          <FormControl>
                            <Input
                              type="number"
                              step="0.01"
                              placeholder="0.00"
                              {...field}
                              data-testid="input-create-monthly-rent"
                            />
                          </FormControl>
                          <FormDescription>In dollars</FormDescription>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={createForm.control}
                      name="depositAmount"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Deposit Amount</FormLabel>
                          <FormControl>
                            <Input
                              type="number"
                              step="0.01"
                              placeholder="0.00"
                              {...field}
                              data-testid="input-create-deposit"
                            />
                          </FormControl>
                          <FormDescription>In dollars</FormDescription>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={createForm.control}
                    name="isActive"
                    render={({ field }) => (
                      <FormItem className="flex flex-row items-center justify-between rounded-lg border p-4">
                        <div className="space-y-0.5">
                          <FormLabel className="text-base">Active Status</FormLabel>
                          <FormDescription>
                            Mark this tenant assignment as active or inactive
                          </FormDescription>
                        </div>
                        <FormControl>
                          <Switch
                            checked={field.value}
                            onCheckedChange={field.onChange}
                            data-testid="switch-create-is-active"
                          />
                        </FormControl>
                      </FormItem>
                    )}
                  />
                </div>

                <div className="flex justify-end gap-2 pt-4">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setOpen(false)}
                    data-testid="button-create-cancel"
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    disabled={isSubmitting}
                    data-testid="button-create-submit"
                  >
                    {isSubmitting ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Creating...
                      </>
                    ) : (
                      "Create & Assign Tenant"
                    )}
                  </Button>
                </div>
              </form>
            </Form>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

