import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { offlineQueue, useOnlineStatus } from "@/lib/offlineQueue";
import type { QuickAddAsset } from "@shared/schema";
import { quickAddAssetSchema } from "@shared/schema";
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
} from "@/components/ui/sheet";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Loader2, WifiOff } from "lucide-react";

interface QuickAddAssetSheetProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  propertyId?: string;
  blockId?: string;
  inspectionId?: string;
  inspectionEntryId?: string;
}

const ASSET_CATEGORIES = [
  "HVAC",
  "Appliances",
  "Furniture",
  "Plumbing",
  "Electrical",
  "Flooring",
  "Windows & Doors",
  "Kitchen",
  "Bathroom",
  "Security",
  "Lighting",
  "Other",
];

export function QuickAddAssetSheet({
  open,
  onOpenChange,
  propertyId,
  blockId,
  inspectionId,
  inspectionEntryId,
}: QuickAddAssetSheetProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const isOnline = useOnlineStatus();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const form = useForm<QuickAddAsset>({
    resolver: zodResolver(quickAddAssetSchema),
    defaultValues: {
      name: "",
      category: "",
      condition: "good",
      cleanliness: undefined,
      location: "",
      description: "",
      propertyId: propertyId || undefined,
      blockId: blockId || undefined,
      photos: [],
      inspectionId: inspectionId || undefined,
      inspectionEntryId: inspectionEntryId || undefined,
    },
  });

  const createAssetMutation = useMutation({
    mutationFn: async (data: QuickAddAsset) => {
      return await apiRequest("POST", "/api/asset-inventory/quick", data);
    },
    onSuccess: () => {
      // Invalidate global asset inventory query
      queryClient.invalidateQueries({ queryKey: ["/api/asset-inventory"] });
      // Invalidate property-specific inventory query if propertyId exists
      if (propertyId) {
        queryClient.invalidateQueries({ queryKey: ["/api/properties", propertyId, "inventory"] });
      }
      // Invalidate block-specific inventory query if blockId exists
      if (blockId) {
        queryClient.invalidateQueries({ queryKey: ["/api/blocks", blockId, "inventory"] });
      }
      toast({
        title: "Asset Added",
        description: "Asset has been added to the inventory successfully",
      });
      form.reset();
      onOpenChange(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to Add Asset",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = async (data: QuickAddAsset) => {
    setIsSubmitting(true);

    try {
      if (isOnline) {
        // Online: submit directly
        await createAssetMutation.mutateAsync(data);
      } else {
        // Offline: queue for later sync
        offlineQueue.enqueueAsset(data);
        toast({
          title: "Asset Queued",
          description: "Asset will be added when you're back online",
        });
        form.reset();
        onOpenChange(false);
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent side="bottom" className="h-[85vh] overflow-y-auto">
        <SheetHeader>
          <SheetTitle>Quick Add Asset</SheetTitle>
          <SheetDescription>
            Add an asset to the inventory during inspection
          </SheetDescription>
        </SheetHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4 mt-6">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Asset Name *</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder="e.g., Refrigerator, HVAC Unit, Washing Machine"
                      data-testid="input-asset-name"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="category"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Category</FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-category">
                        <SelectValue placeholder="Select category" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {ASSET_CATEGORIES.map((category) => (
                        <SelectItem key={category} value={category}>
                          {category}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="condition"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Condition *</FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-condition">
                        <SelectValue />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="excellent">Excellent</SelectItem>
                      <SelectItem value="good">Good</SelectItem>
                      <SelectItem value="fair">Fair</SelectItem>
                      <SelectItem value="poor">Poor</SelectItem>
                      <SelectItem value="needs_replacement">
                        Needs Replacement
                      </SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="location"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Location</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder="e.g., Kitchen, Bedroom 1, Common Area"
                      data-testid="input-location"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Textarea
                      {...field}
                      placeholder="Additional notes about the asset..."
                      rows={3}
                      data-testid="textarea-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-2 pt-4">
              <Button
                type="button"
                variant="outline"
                className="flex-1"
                onClick={() => onOpenChange(false)}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                className="flex-1"
                disabled={isSubmitting}
                data-testid="button-add-asset"
              >
                {isSubmitting ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Adding...
                  </>
                ) : (
                  <>Add Asset{!isOnline && " (Offline)"}</>
                )}
              </Button>
            </div>
          </form>
        </Form>
      </SheetContent>
    </Sheet>
  );
}

client/src/components/InspectionQuickAct.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Plus, Package, Wrench, X, RefreshCw } from "lucide-react";

interface InspectionQuickActionsProps {
  onAddAsset: () => void;
  onUpdateAsset: () => void;
  onLogMaintenance: () => void;
}

export function InspectionQuickActions({
  onAddAsset,
  onUpdateAsset,
  onLogMaintenance,
}: InspectionQuickActionsProps) {
  const [open, setOpen] = useState(false);

  const handleAction = (action: () => void) => {
    action();
    setOpen(false);
  };

  return (
    <div className="fixed bottom-20 right-6 z-50">
      <Popover open={open} onOpenChange={setOpen}>
        <PopoverTrigger asChild>
          <Button
            size="icon"
            className="h-14 w-14 rounded-full shadow-lg hover-elevate active-elevate-2"
            data-testid="button-quick-actions"
          >
            {open ? <X className="h-6 w-6" /> : <Plus className="h-6 w-6" />}
          </Button>
        </PopoverTrigger>
        <PopoverContent
          side="top"
          align="end"
          className="w-56 p-2"
          data-testid="menu-quick-actions"
        >
          <div className="flex flex-col gap-1">
            <Button
              variant="ghost"
              className="w-full justify-start gap-3 h-12"
              onClick={() => handleAction(onAddAsset)}
              data-testid="button-quick-add-asset"
            >
              <Package className="h-5 w-5 text-primary" />
              <div className="flex flex-col items-start">
                <span className="font-medium">Add Asset</span>
                <span className="text-xs text-muted-foreground">
                  Add to inventory
                </span>
              </div>
            </Button>
            <Button
              variant="ghost"
              className="w-full justify-start gap-3 h-12"
              onClick={() => handleAction(onUpdateAsset)}
              data-testid="button-quick-update-asset"
            >
              <RefreshCw className="h-5 w-5 text-primary" />
              <div className="flex flex-col items-start">
                <span className="font-medium">Update Asset</span>
                <span className="text-xs text-muted-foreground">
                  Modify existing inventory
                </span>
              </div>
            </Button>
            <Button
              variant="ghost"
              className="w-full justify-start gap-3 h-12"
              onClick={() => handleAction(onLogMaintenance)}
              data-testid="button-quick-log-maintenance"
            >
              <Wrench className="h-5 w-5 text-primary" />
              <div className="flex flex-col items-start">
                <span className="font-medium">Log Maintenance</span>
                <span className="text-xs text-muted-foreground">
                  Report an issue
                </span>
              </div>
            </Button>
          </div>
        </PopoverContent>
      </Popover>
    </div>
  );
}

client/src/components/QuickAddMaintenanc.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { offlineQueue, useOnlineStatus } from "@/lib/offlineQueue";
import type { QuickAddMaintenance } from "@shared/schema";
import { quickAddMaintenanceSchema } from "@shared/schema";
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
} from "@/components/ui/sheet";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Loader2 } from "lucide-react";

interface QuickAddMaintenanceSheetProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  propertyId?: string;
  blockId?: string;
  inspectionId?: string;
  inspectionEntryId?: string;
}

export function QuickAddMaintenanceSheet({
  open,
  onOpenChange,
  propertyId,
  blockId,
  inspectionId,
  inspectionEntryId,
}: QuickAddMaintenanceSheetProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const isOnline = useOnlineStatus();
  const [isSubmitting, setIsSubmitting] = useState(false);

  // If we have a blockId but no propertyId, fetch properties for the block
  const { data: properties = [] } = useQuery<any[]>({
    queryKey: ["/api/properties"],
    enabled: !!blockId && !propertyId,
  });

  // Filter properties to only those in the current block
  const blockProperties = blockId && !propertyId 
    ? properties.filter(p => p.blockId === blockId)
    : [];

  const form = useForm<QuickAddMaintenance>({
    resolver: zodResolver(quickAddMaintenanceSchema),
    defaultValues: {
      title: "",
      description: "",
      propertyId: propertyId || (blockProperties.length === 1 ? blockProperties[0].id : ""),
      priority: "medium",
      photoUrls: [],
      inspectionId: inspectionId || undefined,
      inspectionEntryId: inspectionEntryId || undefined,
      source: "inspection",
    },
  });

  // Update propertyId when blockProperties change
  if (blockProperties.length === 1 && !form.getValues("propertyId")) {
    form.setValue("propertyId", blockProperties[0].id);
  }

  const createMaintenanceMutation = useMutation({
    mutationFn: async (data: QuickAddMaintenance) => {
      return await apiRequest("POST", "/api/maintenance/quick", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/maintenance"], refetchType: "active" });
      toast({
        title: "Maintenance Request Created",
        description: "The maintenance request has been logged successfully",
      });
      form.reset();
      onOpenChange(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to Log Maintenance",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = async (data: QuickAddMaintenance) => {
    setIsSubmitting(true);

    try {
      if (isOnline) {
        // Online: submit directly
        await createMaintenanceMutation.mutateAsync(data);
      } else {
        // Offline: queue for later sync
        offlineQueue.enqueueMaintenance(data);
        toast({
          title: "Maintenance Queued",
          description: "Request will be logged when you're back online",
        });
        form.reset();
        onOpenChange(false);
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent side="bottom" className="h-[75vh] overflow-y-auto">
        <SheetHeader>
          <SheetTitle>Quick Log Maintenance</SheetTitle>
          <SheetDescription>
            Log a maintenance issue found during inspection
          </SheetDescription>
        </SheetHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4 mt-6">
            <FormField
              control={form.control}
              name="title"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Issue Title *</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder="e.g., Leaking faucet, Damaged wall, Broken window"
                      data-testid="input-title"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Property selector for block-level inspections */}
            {blockId && !propertyId && blockProperties.length > 0 && (
              <FormField
                control={form.control}
                name="propertyId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Property *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-property">
                          <SelectValue placeholder="Select property" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {blockProperties.map((property) => (
                          <SelectItem key={property.id} value={property.id}>
                            {property.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            )}

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Textarea
                      {...field}
                      placeholder="Describe the issue in detail..."
                      rows={4}
                      data-testid="textarea-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="priority"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Priority</FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-priority">
                        <SelectValue />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="low">Low</SelectItem>
                      <SelectItem value="medium">Medium</SelectItem>
                      <SelectItem value="high">High</SelectItem>
                      <SelectItem value="urgent">Urgent</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-2 pt-4">
              <Button
                type="button"
                variant="outline"
                className="flex-1"
                onClick={() => onOpenChange(false)}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                className="flex-1"
                disabled={isSubmitting}
                data-testid="button-log-maintenance"
              >
                {isSubmitting ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Logging...
                  </>
                ) : (
                  <>Log Request{!isOnline && " (Offline)"}</>
                )}
              </Button>
            </div>
          </form>
        </Form>
      </SheetContent>
    </Sheet>
  );
}

client/src/pages/ComparisonReportDetail..content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useParams, Link } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Separator } from "@/components/ui/separator";
import { 
  FileText, 
  ArrowLeft, 
  MessageSquare, 
  Pen, 
  DollarSign, 
  TrendingDown, 
  Calculator,
  Image as ImageIcon,
  AlertCircle,
  Check,
  User
} from "lucide-react";
import { format } from "date-fns";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";

interface ComparisonReport {
  id: string;
  propertyId: string;
  tenantId: string;
  status: string;
  totalEstimatedCost: string;
  aiAnalysisJson: any;
  operatorSignature: string | null;
  operatorSignedAt: string | null;
  tenantSignature: string | null;
  tenantSignedAt: string | null;
  createdAt: string;
  items: ComparisonReportItem[];
}

interface ComparisonReportItem {
  id: string;
  sectionRef: string;
  fieldKey: string;
  itemRef: string | null;
  aiComparisonJson: any;
  estimatedCost: string;
  depreciation: string;
  finalCost: string;
  checkInEntryId: string | null;
  checkOutEntryId: string;
  checkInPhotos?: string[];
  checkOutPhotos?: string[];
}

interface Comment {
  id: string;
  userId: string;
  content: string;
  isInternal: boolean;
  createdAt: string;
}

const statusConfig = {
  draft: { label: "Draft", color: "bg-gray-500" },
  under_review: { label: "Under Review", color: "bg-blue-500" },
  awaiting_signatures: { label: "Awaiting Signatures", color: "bg-amber-500" },
  signed: { label: "Signed", color: "bg-green-500" },
  filed: { label: "Filed", color: "bg-slate-600" },
};

export default function ComparisonReportDetail() {
  const { id } = useParams();
  const { toast } = useToast();
  const { user } = useAuth();
  const [commentText, setCommentText] = useState("");
  const [signatureName, setSignatureName] = useState("");

  const { data: report, isLoading } = useQuery<ComparisonReport>({
    queryKey: ["/api/comparison-reports", id],
    enabled: !!id,
    select: (data) => {
      // Parse AI analysis to extract photos if available
      const items = data.items.map(item => {
        const aiAnalysis = item.aiComparisonJson || {};
        const checkInPhotos = aiAnalysis.checkInPhotos || [];
        const checkOutPhotos = aiAnalysis.checkOutPhotos || [];
        return {
          ...item,
          checkInPhotos,
          checkOutPhotos,
        };
      });
      return {
        ...data,
        items,
      };
    },
  });

  const { data: comments = [] } = useQuery<Comment[]>({
    queryKey: ["/api/comparison-reports", id, "comments"],
    enabled: !!id,
  });

  const addCommentMutation = useMutation({
    mutationFn: async (content: string) => {
      const response = await apiRequest("POST", `/api/comparison-reports/${id}/comments`, { content });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/comparison-reports", id, "comments"] });
      setCommentText("");
      toast({
        title: "Comment Added",
        description: "Your comment has been posted successfully.",
      });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to add comment.",
      });
    },
  });

  const signMutation = useMutation({
    mutationFn: async (signature: string) => {
      const response = await apiRequest("POST", `/api/comparison-reports/${id}/sign`, { signature });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/comparison-reports", id] });
      setSignatureName("");
      toast({
        title: "Report Signed",
        description: "Your electronic signature has been recorded.",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to sign report.",
      });
    },
  });

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-8 space-y-6">
        <Skeleton className="h-12 w-2/3" />
        <Skeleton className="h-64 w-full" />
        <Skeleton className="h-96 w-full" />
      </div>
    );
  }

  if (!report) {
    return (
      <div className="container mx-auto px-4 py-8">
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-16">
            <AlertCircle className="w-16 h-16 text-muted-foreground/50 mb-4" />
            <h3 className="text-xl font-semibold mb-2">Report Not Found</h3>
            <p className="text-muted-foreground mb-4">The comparison report you're looking for doesn't exist.</p>
            <Link href="/comparisons">
              <a>
                <Button variant="outline">
                  <ArrowLeft className="w-4 h-4 mr-2" />
                  Back to Reports
                </Button>
              </a>
            </Link>
          </CardContent>
        </Card>
      </div>
    );
  }

  const statusInfo = statusConfig[report.status as keyof typeof statusConfig];
  const totalCost = parseFloat(report.totalEstimatedCost);
  const isOperator = user?.role === "owner" || user?.role === "clerk";
  const isTenant = user?.role === "tenant";
  const canSign = (isOperator && !report.operatorSignature) || (isTenant && !report.tenantSignature);

  return (
    <div className="container mx-auto px-4 py-8 space-y-6" data-testid="page-comparison-detail">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="space-y-1">
          <Link href="/comparisons">
            <a>
              <Button variant="ghost" size="sm" data-testid="button-back">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Reports
              </Button>
            </a>
          </Link>
          <h1 className="text-4xl font-bold flex items-center gap-3">
            <FileText className="w-10 h-10 text-primary" />
            Comparison Report
          </h1>
          <p className="text-muted-foreground">
            Generated on {format(new Date(report.createdAt), "MMMM d, yyyy 'at' h:mm a")}
          </p>
        </div>
        <Badge className={`${statusInfo.color} text-white text-lg px-4 py-2`}>
          {statusInfo.label}
        </Badge>
      </div>

      {/* Summary Card */}
      <Card>
        <CardHeader>
          <CardTitle>Cost Summary</CardTitle>
          <CardDescription>Total estimated tenant liability after depreciation</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="text-4xl font-bold text-primary">
            ${totalCost.toFixed(2)}
          </div>
          <p className="text-sm text-muted-foreground mt-2">
            Based on {report.items.length} items marked for review
          </p>
        </CardContent>
      </Card>

      {/* Items Breakdown */}
      <Card>
        <CardHeader>
          <CardTitle>Item-by-Item Analysis</CardTitle>
          <CardDescription>Detailed comparison of each marked item</CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {report.items.map((item, index) => {
            const aiAnalysis = item.aiComparisonJson || {};
            const estimatedCost = parseFloat(item.estimatedCost);
            const depreciation = parseFloat(item.depreciation);
            const finalCost = parseFloat(item.finalCost);

            return (
              <div key={item.id} className="space-y-4 pb-6 border-b last:border-b-0">
                {/* Item Header */}
                <div className="flex items-start justify-between">
                  <div>
                    <h3 className="text-lg font-semibold">
                      {index + 1}. {item.sectionRef} - {item.fieldKey}
                    </h3>
                    {item.itemRef && (
                      <p className="text-sm text-muted-foreground">{item.itemRef}</p>
                    )}
                  </div>
                  {aiAnalysis.severity && (
                    <Badge 
                      variant={
                        aiAnalysis.severity === "high" ? "destructive" : 
                        aiAnalysis.severity === "medium" ? "default" : 
                        "secondary"
                      }
                    >
                      {aiAnalysis.severity} severity
                    </Badge>
                  )}
                </div>

                {/* Images Side-by-Side */}
                {((item.checkInPhotos && item.checkInPhotos.length > 0) || (item.checkOutPhotos && item.checkOutPhotos.length > 0)) && (
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <h4 className="text-sm font-medium text-muted-foreground">Check-In Photos</h4>
                      <div className="grid grid-cols-2 gap-2">
                        {item.checkInPhotos && item.checkInPhotos.length > 0 ? (
                          item.checkInPhotos.slice(0, 2).map((photo, idx) => (
                            <img
                              key={idx}
                              src={photo}
                              alt={`Check-in ${idx + 1}`}
                              className="w-full h-32 object-cover rounded-lg border"
                            />
                          ))
                        ) : (
                          <div className="col-span-2 flex items-center justify-center h-32 bg-muted rounded-lg border border-dashed">
                            <p className="text-sm text-muted-foreground">No check-in photos</p>
                          </div>
                        )}
                      </div>
                    </div>
                    <div className="space-y-2">
                      <h4 className="text-sm font-medium text-muted-foreground">Check-Out Photos</h4>
                      <div className="grid grid-cols-2 gap-2">
                        {item.checkOutPhotos && item.checkOutPhotos.length > 0 ? (
                          item.checkOutPhotos.slice(0, 2).map((photo, idx) => (
                            <img
                              key={idx}
                              src={photo}
                              alt={`Check-out ${idx + 1}`}
                              className="w-full h-32 object-cover rounded-lg border"
                            />
                          ))
                        ) : (
                          <div className="col-span-2 flex items-center justify-center h-32 bg-muted rounded-lg border border-dashed">
                            <p className="text-sm text-muted-foreground">No check-out photos</p>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                )}

                {/* AI Analysis */}
                {aiAnalysis.differences && (
                  <div className="bg-muted p-4 rounded-lg space-y-2">
                    <h4 className="font-medium text-sm flex items-center gap-2">
                      <ImageIcon className="w-4 h-4" />
                      Visual Differences
                    </h4>
                    <p className="text-sm">{aiAnalysis.differences}</p>
                  </div>
                )}

                {aiAnalysis.damage && (
                  <div className="bg-destructive/10 p-4 rounded-lg space-y-2">
                    <h4 className="font-medium text-sm flex items-center gap-2">
                      <AlertCircle className="w-4 h-4 text-destructive" />
                      Damage Assessment
                    </h4>
                    <p className="text-sm">{aiAnalysis.damage}</p>
                  </div>
                )}

                {aiAnalysis.repair_description && (
                  <div className="bg-blue-50 dark:bg-blue-950 p-4 rounded-lg space-y-2">
                    <h4 className="font-medium text-sm flex items-center gap-2">
                      <Calculator className="w-4 h-4" />
                      Recommended Repairs
                    </h4>
                    <p className="text-sm">{aiAnalysis.repair_description}</p>
                  </div>
                )}

                {/* Cost Breakdown */}
                <div className="grid grid-cols-3 gap-4 pt-2">
                  <div className="space-y-1">
                    <div className="flex items-center gap-2 text-sm text-muted-foreground">
                      <DollarSign className="w-4 h-4" />
                      Estimated Cost
                    </div>
                    <div className="text-lg font-semibold">${estimatedCost.toFixed(2)}</div>
                  </div>
                  <div className="space-y-1">
                    <div className="flex items-center gap-2 text-sm text-muted-foreground">
                      <TrendingDown className="w-4 h-4" />
                      Depreciation
                    </div>
                    <div className="text-lg font-semibold text-green-600">
                      -${depreciation.toFixed(2)}
                    </div>
                  </div>
                  <div className="space-y-1">
                    <div className="flex items-center gap-2 text-sm text-muted-foreground">
                      <Calculator className="w-4 h-4" />
                      Tenant Liability
                    </div>
                    <div className="text-lg font-semibold text-primary">
                      ${finalCost.toFixed(2)}
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </CardContent>
      </Card>

      {/* Comments Section */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <MessageSquare className="w-5 h-5" />
            Discussion Thread
          </CardTitle>
          <CardDescription>
            Internal notes and tenant communication
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Comments List */}
          <div className="space-y-3 max-h-96 overflow-y-auto">
            {comments.length === 0 ? (
              <p className="text-sm text-muted-foreground text-center py-8">
                No comments yet. Start the discussion below.
              </p>
            ) : (
              comments.map((comment) => (
                <div 
                  key={comment.id}
                  className={`p-4 rounded-lg ${
                    comment.isInternal 
                      ? "bg-amber-50 dark:bg-amber-950 border border-amber-200 dark:border-amber-800" 
                      : "bg-muted"
                  }`}
                >
                  <div className="flex items-start justify-between mb-2">
                    <div className="flex items-center gap-2">
                      <User className="w-4 h-4" />
                      <span className="text-sm font-medium">
                        {comment.isInternal ? "Internal Note" : "Comment"}
                      </span>
                    </div>
                    <span className="text-xs text-muted-foreground">
                      {format(new Date(comment.createdAt), "MMM d, h:mm a")}
                    </span>
                  </div>
                  <p className="text-sm whitespace-pre-wrap">{comment.content}</p>
                </div>
              ))
            )}
          </div>

          <Separator />

          {/* Add Comment Form */}
          <div className="space-y-3">
            <Textarea
              placeholder="Add a comment or internal note..."
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
              rows={3}
              data-testid="input-comment"
            />
            <Button
              onClick={() => addCommentMutation.mutate(commentText)}
              disabled={!commentText.trim() || addCommentMutation.isPending}
              data-testid="button-add-comment"
            >
              <MessageSquare className="w-4 h-4 mr-2" />
              {addCommentMutation.isPending ? "Adding..." : "Add Comment"}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Signature Section */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Pen className="w-5 h-5" />
            Electronic Signatures
          </CardTitle>
          <CardDescription>
            Both parties must sign to finalize the report
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Operator Signature */}
          <div className="flex items-center justify-between p-4 border rounded-lg">
            <div className="space-y-1">
              <div className="font-medium flex items-center gap-2">
                <User className="w-4 h-4" />
                Operator Signature
              </div>
              {report.operatorSignature ? (
                <div className="space-y-1">
                  <div className="flex items-center gap-2">
                    <Check className="w-4 h-4 text-green-600" />
                    <span className="text-sm">Signed: {report.operatorSignature}</span>
                  </div>
                  {report.operatorSignedAt && (
                    <p className="text-xs text-muted-foreground">
                      {format(new Date(report.operatorSignedAt), "MMM d, yyyy 'at' h:mm a")}
                    </p>
                  )}
                </div>
              ) : (
                <p className="text-sm text-muted-foreground">Pending signature</p>
              )}
            </div>
            {isOperator && !report.operatorSignature && (
              <Badge variant="outline" className="text-amber-600 border-amber-600">
                Action Required
              </Badge>
            )}
          </div>

          {/* Tenant Signature */}
          <div className="flex items-center justify-between p-4 border rounded-lg">
            <div className="space-y-1">
              <div className="font-medium flex items-center gap-2">
                <User className="w-4 h-4" />
                Tenant Signature
              </div>
              {report.tenantSignature ? (
                <div className="space-y-1">
                  <div className="flex items-center gap-2">
                    <Check className="w-4 h-4 text-green-600" />
                    <span className="text-sm">Signed: {report.tenantSignature}</span>
                  </div>
                  {report.tenantSignedAt && (
                    <p className="text-xs text-muted-foreground">
                      {format(new Date(report.tenantSignedAt), "MMM d, yyyy 'at' h:mm a")}
                    </p>
                  )}
                </div>
              ) : (
                <p className="text-sm text-muted-foreground">Pending signature</p>
              )}
            </div>
            {isTenant && !report.tenantSignature && (
              <Badge variant="outline" className="text-amber-600 border-amber-600">
                Action Required
              </Badge>
            )}
          </div>

          {/* Sign Form */}
          {canSign && (
            <div className="space-y-3 p-4 bg-muted rounded-lg">
              <label className="text-sm font-medium">Type your full name to sign</label>
              <Input
                placeholder="Your full name"
                value={signatureName}
                onChange={(e) => setSignatureName(e.target.value)}
                data-testid="input-signature"
              />
              <Button
                onClick={() => signMutation.mutate(signatureName)}
                disabled={!signatureName.trim() || signMutation.isPending}
                className="w-full"
                data-testid="button-sign"
              >
                <Pen className="w-4 h-4 mr-2" />
                {signMutation.isPending ? "Signing..." : "Sign Report"}
              </Button>
              <p className="text-xs text-muted-foreground">
                By signing, you acknowledge that you have reviewed and agree to the contents of this report.
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

server/services/fixflo-client.ts.content:
import type { FixfloConfig } from "@shared/schema";

export interface FixfloIssuePayload {
  propertyId: string;
  title: string;
  description: string;
  priority: "low" | "medium" | "high" | "urgent";
  category?: string;
  reporterId?: string;
  reporterName?: string;
  reporterEmail?: string;
  reporterPhone?: string;
  externalRef?: string;
}

export interface FixfloAttachment {
  url: string;
  filename: string;
  contentType?: string;
}

export interface FixfloIssueResponse {
  id: string;
  jobId?: string;
  status: string;
  assignedAgentId?: string;
  assignedAgentName?: string;
  createdAt: string;
  updatedAt: string;
}

export interface FixfloUpdatePayload {
  assignedAgentId?: string;
  priority?: string;
  status?: string;
  dueDate?: string;
  notes?: string;
}

export class FixfloClientError extends Error {
  constructor(
    message: string,
    public statusCode?: number,
    public response?: any
  ) {
    super(message);
    this.name = "FixfloClientError";
  }
}

export class FixfloClient {
  private baseUrl: string;
  private bearerToken: string;
  private maxRetries = 3;
  private retryDelayMs = 1000;

  constructor(config: FixfloConfig) {
    this.baseUrl = config.baseUrl;
    this.bearerToken = config.bearerToken || "";

    if (!this.bearerToken) {
      throw new FixfloClientError("Fixflo bearer token is required");
    }
  }

  private async request<T>(
    method: string,
    endpoint: string,
    body?: any,
    attempt = 1
  ): Promise<T> {
    const url = `${this.baseUrl}${endpoint}`;

    try {
      const response = await fetch(url, {
        method,
        headers: {
          "Authorization": `Bearer ${this.bearerToken}`,
          "Content-Type": "application/json",
          "Accept": "application/json",
        },
        body: body ? JSON.stringify(body) : undefined,
      });

      // Handle rate limiting with Retry-After
      if (response.status === 429) {
        const retryAfter = response.headers.get("Retry-After");
        const delayMs = retryAfter ? parseInt(retryAfter) * 1000 : this.retryDelayMs * attempt;

        if (attempt < this.maxRetries) {
          console.log(`[Fixflo] Rate limited. Retrying after ${delayMs}ms (attempt ${attempt}/${this.maxRetries})`);
          await this.delay(delayMs);
          return this.request<T>(method, endpoint, body, attempt + 1);
        }

        throw new FixfloClientError(
          "Rate limit exceeded",
          429,
          await response.text()
        );
      }

      // Retry on 5xx errors
      if (response.status >= 500) {
        if (attempt < this.maxRetries) {
          const delayMs = this.retryDelayMs * attempt;
          console.log(`[Fixflo] Server error ${response.status}. Retrying after ${delayMs}ms (attempt ${attempt}/${this.maxRetries})`);
          await this.delay(delayMs);
          return this.request<T>(method, endpoint, body, attempt + 1);
        }

        throw new FixfloClientError(
          `Server error: ${response.status}`,
          response.status,
          await response.text()
        );
      }

      // Handle client errors (4xx)
      if (!response.ok) {
        const errorText = await response.text();
        throw new FixfloClientError(
          `HTTP ${response.status}: ${errorText}`,
          response.status,
          errorText
        );
      }

      // Parse response
      const contentType = response.headers.get("Content-Type");
      if (contentType?.includes("application/json")) {
        return await response.json() as T;
      }

      return await response.text() as T;

    } catch (error) {
      if (error instanceof FixfloClientError) {
        throw error;
      }

      // Network errors - retry if we haven't exhausted attempts
      if (attempt < this.maxRetries) {
        const delayMs = this.retryDelayMs * attempt;
        console.log(`[Fixflo] Network error. Retrying after ${delayMs}ms (attempt ${attempt}/${this.maxRetries})`);
        await this.delay(delayMs);
        return this.request<T>(method, endpoint, body, attempt + 1);
      }

      throw new FixfloClientError(
        `Network error: ${(error as Error).message}`,
        undefined,
        error
      );
    }
  }

  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  async createIssue(payload: FixfloIssuePayload): Promise<FixfloIssueResponse> {
    console.log(`[Fixflo] Creating issue: ${payload.title}`);

    // Map our priority values to Fixflo's expected format
    const priorityMapping: Record<string, string> = {
      "low": "Low",
      "medium": "Medium", 
      "high": "High",
      "urgent": "Emergency"
    };

    const fixfloPayload = {
      PropertyId: payload.propertyId,
      Title: payload.title,
      Description: payload.description,
      Priority: priorityMapping[payload.priority] || "Medium",
      Category: payload.category || "Maintenance",
      ExternalRef: payload.externalRef,
      Reporter: payload.reporterId ? {
        Id: payload.reporterId,
        Name: payload.reporterName,
        Email: payload.reporterEmail,
        Phone: payload.reporterPhone,
      } : undefined,
    };

    return await this.request<FixfloIssueResponse>("POST", "/Issues", fixfloPayload);
  }

  async updateIssue(issueId: string, payload: FixfloUpdatePayload): Promise<FixfloIssueResponse> {
    console.log(`[Fixflo] Updating issue: ${issueId}`);

    return await this.request<FixfloIssueResponse>(
      "PATCH",
      `/Issues/${issueId}`,
      payload
    );
  }

  async assignContractor(issueId: string, contractorId: string): Promise<FixfloIssueResponse> {
    console.log(`[Fixflo] Assigning contractor ${contractorId} to issue ${issueId}`);

    return await this.request<FixfloIssueResponse>(
      "PATCH",
      `/Issues/${issueId}/AssignedAgent`,
      { AgentId: contractorId }
    );
  }

  async getIssue(issueId: string): Promise<FixfloIssueResponse> {
    console.log(`[Fixflo] Fetching issue: ${issueId}`);

    return await this.request<FixfloIssueResponse>("GET", `/Issues/${issueId}`);
  }

  async getJobCompleted(sinceDate?: Date): Promise<FixfloIssueResponse[]> {
    const dateParam = sinceDate 
      ? `?since=${sinceDate.toISOString()}`
      : "";

    console.log(`[Fixflo] Fetching completed jobs${dateParam}`);

    return await this.request<FixfloIssueResponse[]>(
      "GET",
      `/Issues/JobCompleted${dateParam}`
    );
  }

  async uploadAttachment(
    issueId: string,
    attachment: FixfloAttachment
  ): Promise<void> {
    console.log(`[Fixflo] Uploading attachment to issue ${issueId}: ${attachment.filename}`);

    // Fixflo may support URL-based attachments or require binary upload
    // This implementation assumes URL-based attachment
    await this.request("POST", `/Issues/${issueId}/Attachments`, {
      Url: attachment.url,
      Filename: attachment.filename,
      ContentType: attachment.contentType || "image/jpeg",
    });
  }

  async downloadAttachment(attachmentUrl: string): Promise<Blob> {
    console.log(`[Fixflo] Downloading attachment: ${attachmentUrl}`);

    const response = await fetch(attachmentUrl, {
      headers: {
        "Authorization": `Bearer ${this.bearerToken}`,
      },
    });

    if (!response.ok) {
      throw new FixfloClientError(
        `Failed to download attachment: ${response.status}`,
        response.status
      );
    }

    return await response.blob();
  }

  async healthCheck(): Promise<boolean> {
    try {
      // Simple API call to verify connectivity and authentication
      await this.request("GET", "/Issues?limit=1");
      return true;
    } catch (error) {
      console.error(`[Fixflo] Health check failed:`, error);
      return false;
    }
  }
}

export async function createFixfloClient(config: FixfloConfig): Promise<FixfloClient> {
  if (!config.isEnabled) {
    throw new FixfloClientError("Fixflo integration is not enabled for this organization");
  }

  return new FixfloClient(config);
}

