import type { DatabaseStorage } from "../storage";

export async function processFixfloWebhook(
  webhookLogId: string,
  organizationId: string,
  payload: any,
  storage: DatabaseStorage
): Promise<void> {
  const eventType = payload.eventType || payload.event || "Unknown";

  console.log(`[Fixflo Webhook] Processing event: ${eventType}`);

  try {
    // Extract issue/job IDs from payload
    const fixfloIssueId = payload.issueId || payload.Issue?.Id || payload.Id;
    const fixfloJobId = payload.jobId || payload.Job?.Id;

    // Process based on event type
    switch (eventType) {
      case "IssueCreated":
        await handleIssueCreated(fixfloIssueId, payload, organizationId, storage);
        break;

      case "IssueUpdated":
        await handleIssueUpdated(fixfloIssueId, payload, organizationId, storage);
        break;

      case "ContractorAssigned":
      case "AssignedAgentUpdated":
        await handleContractorAssigned(fixfloIssueId, payload, organizationId, storage);
        break;

      case "JobCompleted":
        await handleJobCompleted(fixfloIssueId, fixfloJobId, payload, organizationId, storage);
        break;

      case "InvoiceAdded":
        await handleInvoiceAdded(fixfloIssueId, payload, organizationId, storage);
        break;

      case "AttachmentAdded":
        await handleAttachmentAdded(fixfloIssueId, payload, organizationId, storage);
        break;

      default:
        console.log(`[Fixflo Webhook] Unhandled event type: ${eventType}`);
    }

    // Mark webhook as processed
    await storage.updateFixfloWebhookLog(webhookLogId, {
      processingStatus: "processed",
      processedAt: new Date(),
    });

    console.log(`[Fixflo Webhook] Successfully processed event: ${eventType}`);
  } catch (error) {
    console.error(`[Fixflo Webhook] Error processing webhook:`, error);

    // Get current retry count
    const webhookLogs = await storage.getFixfloWebhookLogs(organizationId, 1);
    const currentLog = webhookLogs.find(log => log.id === webhookLogId);
    const retryCount = (currentLog?.retryCount || 0) + 1;

    // Update webhook log with error
    await storage.updateFixfloWebhookLog(webhookLogId, {
      processingStatus: retryCount < 3 ? "retrying" : "error",
      errorMessage: (error as Error).message,
      retryCount,
    });

    // Retry if under max retries
    if (retryCount < 3) {
      console.log(`[Fixflo Webhook] Retry ${retryCount}/3 for webhook ${webhookLogId}`);
      // In production, this would be handled by a queue
      setTimeout(() => {
        processFixfloWebhook(webhookLogId, organizationId, payload, storage);
      }, 5000 * retryCount); // Exponential backoff
    }
  }
}

async function handleIssueCreated(
  fixfloIssueId: string,
  payload: any,
  organizationId: string,
  storage: DatabaseStorage
): Promise<void> {
  console.log(`[Fixflo Webhook] Issue created: ${fixfloIssueId}`);

  // Check if we already have a maintenance request with this Fixflo Issue ID
  // If not, this issue was created in Fixflo directly (not from Inspect360)
  // We could optionally create a new maintenance request here
}

async function handleIssueUpdated(
  fixfloIssueId: string,
  payload: any,
  organizationId: string,
  storage: DatabaseStorage
): Promise<void> {
  console.log(`[Fixflo Webhook] Issue updated: ${fixfloIssueId}`);

  // Find maintenance request by Fixflo Issue ID
  const maintenanceRequests = await storage.getMaintenanceByOrganization(organizationId);
  const matchingRequest = maintenanceRequests.find(
    (req: any) => req.fixfloIssueId === fixfloIssueId
  );

  if (!matchingRequest) {
    console.log(`[Fixflo Webhook] No matching maintenance request found for issue ${fixfloIssueId}`);
    return;
  }

  // Update maintenance request with latest Fixflo data
  const updates: any = {
    fixfloStatus: payload.status || payload.Issue?.Status,
    fixfloSyncedAt: new Date(),
  };

  // Map Fixflo status to our status
  const fixfloStatus = (payload.status || payload.Issue?.Status || "").toLowerCase();
  if (fixfloStatus.includes("complete") || fixfloStatus.includes("closed")) {
    updates.status = "completed";
  } else if (fixfloStatus.includes("progress")) {
    updates.status = "in_progress";
  }

  await storage.updateMaintenanceRequest(matchingRequest.id, updates);
  console.log(`[Fixflo Webhook] Updated maintenance request ${matchingRequest.id}`);
}

async function handleContractorAssigned(
  fixfloIssueId: string,
  payload: any,
  organizationId: string,
  storage: DatabaseStorage
): Promise<void> {
  console.log(`[Fixflo Webhook] Contractor assigned to issue: ${fixfloIssueId}`);

  // Find maintenance request
  const maintenanceRequests = await storage.getMaintenanceByOrganization(organizationId);
  const matchingRequest = maintenanceRequests.find(
    (req: any) => req.fixfloIssueId === fixfloIssueId
  );

  if (!matchingRequest) {
    return;
  }

  // Update with contractor info
  const contractorName = 
    payload.assignedAgent?.name || 
    payload.AssignedAgent?.Name ||
    payload.contractor?.name;

  const updates: any = {
    fixfloContractorName: contractorName,
    fixfloSyncedAt: new Date(),
  };

  await storage.updateMaintenanceRequest(matchingRequest.id, updates);
  console.log(`[Fixflo Webhook] Updated contractor for maintenance request ${matchingRequest.id}`);
}

async function handleJobCompleted(
  fixfloIssueId: string,
  fixfloJobId: string | undefined,
  payload: any,
  organizationId: string,
  storage: DatabaseStorage
): Promise<void> {
  console.log(`[Fixflo Webhook] Job completed: ${fixfloIssueId}`);

  // Find maintenance request
  const maintenanceRequests = await storage.getMaintenanceByOrganization(organizationId);
  const matchingRequest = maintenanceRequests.find(
    (req: any) => req.fixfloIssueId === fixfloIssueId || req.fixfloJobId === fixfloJobId
  );

  if (!matchingRequest) {
    return;
  }

  // Mark as completed
  const updates: any = {
    status: "completed",
    fixfloStatus: "Completed",
    fixfloJobId: fixfloJobId,
    fixfloSyncedAt: new Date(),
  };

  await storage.updateMaintenanceRequest(matchingRequest.id, updates);
  console.log(`[Fixflo Webhook] Marked maintenance request ${matchingRequest.id} as completed`);
}

async function handleInvoiceAdded(
  fixfloIssueId: string,
  payload: any,
  organizationId: string,
  storage: DatabaseStorage
): Promise<void> {
  console.log(`[Fixflo Webhook] Invoice added to issue: ${fixfloIssueId}`);

  // Find maintenance request
  const maintenanceRequests = await storage.getMaintenanceByOrganization(organizationId);
  const matchingRequest = maintenanceRequests.find(
    (req: any) => req.fixfloIssueId === fixfloIssueId
  );

  if (!matchingRequest) {
    return;
  }

  // Store invoice data in aiAnalysisJson for now
  // In future, might want a dedicated invoices table
  const currentAnalysis = (matchingRequest.aiAnalysisJson as any) || {};
  const invoiceData = {
    amount: payload.invoice?.amount || payload.Invoice?.Amount,
    invoiceNumber: payload.invoice?.number || payload.Invoice?.Number,
    date: payload.invoice?.date || payload.Invoice?.Date,
    description: payload.invoice?.description || payload.Invoice?.Description,
  };

  const updates: any = {
    aiAnalysisJson: {
      ...currentAnalysis,
      fixfloInvoice: invoiceData,
    },
    fixfloSyncedAt: new Date(),
  };

  await storage.updateMaintenanceRequest(matchingRequest.id, updates);
  console.log(`[Fixflo Webhook] Added invoice data to maintenance request ${matchingRequest.id}`);
}

async function handleAttachmentAdded(
  fixfloIssueId: string,
  payload: any,
  organizationId: string,
  storage: DatabaseStorage
): Promise<void> {
  console.log(`[Fixflo Webhook] Attachment added to issue: ${fixfloIssueId}`);

  // Find maintenance request
  const maintenanceRequests = await storage.getMaintenanceByOrganization(organizationId);
  const matchingRequest = maintenanceRequests.find(
    (req: any) => req.fixfloIssueId === fixfloIssueId
  );

  if (!matchingRequest) {
    return;
  }

  // Get attachment URL from payload
  const attachmentUrl = payload.attachment?.url || payload.Attachment?.Url;

  if (!attachmentUrl) {
    console.log(`[Fixflo Webhook] No attachment URL in payload`);
    return;
  }

  // Add to photoUrls array
  const currentPhotoUrls = matchingRequest.photoUrls || [];
  const updatedPhotoUrls = [...currentPhotoUrls, attachmentUrl];

  const updates: any = {
    photoUrls: updatedPhotoUrls,
    fixfloSyncedAt: new Date(),
  };

  await storage.updateMaintenanceRequest(matchingRequest.id, updates);
  console.log(`[Fixflo Webhook] Added attachment to maintenance request ${matchingRequest.id}`);
}

client/src/components/FixfloIntegrationS.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { CheckCircle, XCircle, AlertCircle, RefreshCw, Settings, Link as LinkIcon } from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from "@/components/ui/dialog";
import type { Property } from "@shared/schema";

interface FixfloConfig {
  id: string;
  organizationId: string;
  baseUrl: string;
  bearerToken?: string;
  webhookVerifyToken?: string;
  isEnabled: boolean;
  lastHealthCheckAt?: string;
  healthCheckStatus?: string;
  healthCheckErrorMessage?: string;
}

interface FixfloSyncState {
  id: string;
  organizationId: string;
  entityType: string;
  lastSyncAt?: string;
  lastSuccessfulSyncAt?: string;
  syncStatus: string;
  errorMessage?: string;
  recordsSynced: number;
  recordsFailed: number;
}

export default function FixfloIntegrationSettings() {
  const { toast } = useToast();
  const [baseUrl, setBaseUrl] = useState("");
  const [bearerToken, setBearerToken] = useState("");
  const [webhookVerifyToken, setWebhookVerifyToken] = useState("");
  const [isEnabled, setIsEnabled] = useState(false);
  const [selectedProperty, setSelectedProperty] = useState<Property | null>(null);
  const [fixfloPropertyId, setFixfloPropertyId] = useState("");
  const [isPropertyDialogOpen, setIsPropertyDialogOpen] = useState(false);

  // Fetch Fixflo configuration
  const { data: config, isLoading: configLoading } = useQuery<FixfloConfig>({
    queryKey: ["/api/fixflo/config"],
  });

  // Fetch sync state
  const { data: syncStates = [], isLoading: syncLoading } = useQuery<FixfloSyncState[]>({
    queryKey: ["/api/fixflo/sync-state"],
  });

  // Fetch properties for mapping
  const { data: properties = [] } = useQuery<Property[]>({
    queryKey: ["/api/properties"],
  });

  // Initialize form when config loads
  useEffect(() => {
    if (config) {
      setBaseUrl(config.baseUrl || "");
      setIsEnabled(config.isEnabled);
    }
  }, [config]);

  // Save configuration mutation
  const saveConfigMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", "/api/fixflo/config", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/fixflo/config"] });
      toast({
        title: "Configuration Saved",
        description: "Fixflo integration settings have been updated",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to save configuration",
      });
    },
  });

  // Test connection mutation
  const testConnectionMutation = useMutation({
    mutationFn: async () => {
      const testData: any = {
        baseUrl: baseUrl || config?.baseUrl,
      };
      // Only include bearerToken if user has entered a new value
      if (bearerToken.trim()) {
        testData.bearerToken = bearerToken.trim();
      }
      return await apiRequest("POST", "/api/fixflo/health-check", testData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/fixflo/config"] });
      toast({
        title: "Connection Successful",
        description: "Successfully connected to Fixflo API",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Connection Failed",
        description: error.message || "Failed to connect to Fixflo API",
      });
    },
  });

  // Update property mapping mutation
  const updatePropertyMutation = useMutation({
    mutationFn: async ({ propertyId, fixfloPropertyId }: { propertyId: string; fixfloPropertyId: string }) => {
      return await apiRequest("PATCH", `/api/properties/${propertyId}`, { fixfloPropertyId });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/properties"] });
      toast({
        title: "Property Mapping Updated",
        description: "Property has been linked to Fixflo",
      });
      setIsPropertyDialogOpen(false);
      setSelectedProperty(null);
      setFixfloPropertyId("");
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to update property mapping",
      });
    },
  });

  const handleSaveConfig = () => {
    const configData: any = {
      baseUrl: baseUrl.trim(),
      isEnabled,
    };

    if (bearerToken.trim()) {
      configData.bearerToken = bearerToken.trim();
    }
    if (webhookVerifyToken.trim()) {
      configData.webhookVerifyToken = webhookVerifyToken.trim();
    }

    saveConfigMutation.mutate(configData);
  };

  const handleTestConnection = () => {
    if (!baseUrl && !config?.baseUrl) {
      toast({
        variant: "destructive",
        title: "Base URL Required",
        description: "Please enter a Fixflo API base URL",
      });
      return;
    }
    testConnectionMutation.mutate();
  };

  const handleOpenPropertyDialog = (property: Property) => {
    setSelectedProperty(property);
    setFixfloPropertyId(property.fixfloPropertyId || "");
    setIsPropertyDialogOpen(true);
  };

  const handleSavePropertyMapping = () => {
    if (!selectedProperty) return;
    updatePropertyMutation.mutate({
      propertyId: selectedProperty.id,
      fixfloPropertyId: fixfloPropertyId.trim(),
    });
  };

  const getStatusBadge = (status?: string) => {
    switch (status) {
      case "success":
        return <Badge variant="default" className="bg-green-500"><CheckCircle className="w-3 h-3 mr-1" />Healthy</Badge>;
      case "error":
        return <Badge variant="destructive"><XCircle className="w-3 h-3 mr-1" />Error</Badge>;
      default:
        return <Badge variant="secondary"><AlertCircle className="w-3 h-3 mr-1" />Unknown</Badge>;
    }
  };

  const getSyncStatusBadge = (status: string) => {
    switch (status) {
      case "syncing":
        return <Badge variant="secondary"><RefreshCw className="w-3 h-3 mr-1 animate-spin" />Syncing</Badge>;
      case "error":
        return <Badge variant="destructive"><XCircle className="w-3 h-3 mr-1" />Error</Badge>;
      case "idle":
      default:
        return <Badge variant="outline">Idle</Badge>;
    }
  };

  if (configLoading || syncLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <RefreshCw className="w-6 h-6 animate-spin text-muted-foreground" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <Tabs defaultValue="configuration" className="w-full">
        <TabsList>
          <TabsTrigger value="configuration" data-testid="tab-fixflo-configuration">
            <Settings className="w-4 h-4 mr-2" />
            Configuration
          </TabsTrigger>
          <TabsTrigger value="property-mapping" data-testid="tab-fixflo-property-mapping">
            <LinkIcon className="w-4 h-4 mr-2" />
            Property Mapping
          </TabsTrigger>
          <TabsTrigger value="sync-status" data-testid="tab-fixflo-sync-status">
            <RefreshCw className="w-4 h-4 mr-2" />
            Sync Status
          </TabsTrigger>
        </TabsList>

        <TabsContent value="configuration">
          <Card>
            <CardHeader>
              <CardTitle>Fixflo API Configuration</CardTitle>
              <CardDescription>
                Configure your Fixflo integration settings to sync maintenance requests with Fixflo.
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="baseUrl">Fixflo API Base URL</Label>
                <Input
                  id="baseUrl"
                  placeholder="https://api.fixflo.com"
                  value={baseUrl}
                  onChange={(e) => setBaseUrl(e.target.value)}
                  data-testid="input-fixflo-base-url"
                />
                <p className="text-sm text-muted-foreground">
                  The base URL for your Fixflo API instance
                </p>
              </div>

              <div className="space-y-2">
                <Label htmlFor="bearerToken">Bearer Token</Label>
                <Input
                  id="bearerToken"
                  type="password"
                  placeholder="Enter bearer token (optional to update)"
                  value={bearerToken}
                  onChange={(e) => setBearerToken(e.target.value)}
                  data-testid="input-fixflo-bearer-token"
                />
                <p className="text-sm text-muted-foreground">
                  Your Fixflo API bearer token for authentication
                </p>
              </div>

              <div className="space-y-2">
                <Label htmlFor="webhookToken">Webhook Verify Token</Label>
                <Input
                  id="webhookToken"
                  type="password"
                  placeholder="Enter webhook token (optional to update)"
                  value={webhookVerifyToken}
                  onChange={(e) => setWebhookVerifyToken(e.target.value)}
                  data-testid="input-fixflo-webhook-token"
                />
                <p className="text-sm text-muted-foreground">
                  Token for verifying incoming webhooks from Fixflo
                </p>
              </div>

              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id="isEnabled"
                  checked={isEnabled}
                  onChange={(e) => setIsEnabled(e.target.checked)}
                  className="w-4 h-4"
                  data-testid="checkbox-fixflo-enabled"
                />
                <Label htmlFor="isEnabled" className="cursor-pointer">
                  Enable Fixflo Integration
                </Label>
              </div>

              {config?.lastHealthCheckAt && (
                <div className="flex items-center justify-between p-4 border rounded-md bg-muted/50">
                  <div>
                    <p className="text-sm font-medium">Last Health Check</p>
                    <p className="text-xs text-muted-foreground">
                      {new Date(config.lastHealthCheckAt).toLocaleString()}
                    </p>
                  </div>
                  {getStatusBadge(config.healthCheckStatus)}
                </div>
              )}

              {config?.healthCheckErrorMessage && (
                <div className="p-4 border border-destructive/50 rounded-md bg-destructive/10">
                  <p className="text-sm font-medium text-destructive">Error Message</p>
                  <p className="text-xs text-muted-foreground mt-1">{config.healthCheckErrorMessage}</p>
                </div>
              )}

              <div className="flex gap-2 pt-4">
                <Button
                  onClick={handleSaveConfig}
                  disabled={saveConfigMutation.isPending}
                  data-testid="button-save-fixflo-config"
                >
                  {saveConfigMutation.isPending ? "Saving..." : "Save Configuration"}
                </Button>
                <Button
                  variant="outline"
                  onClick={handleTestConnection}
                  disabled={testConnectionMutation.isPending}
                  data-testid="button-test-fixflo-connection"
                >
                  {testConnectionMutation.isPending ? (
                    <>
                      <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                      Testing...
                    </>
                  ) : (
                    "Test Connection"
                  )}
                </Button>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="property-mapping">
          <Card>
            <CardHeader>
              <CardTitle>Property Mapping</CardTitle>
              <CardDescription>
                Link your Inspect360 properties to Fixflo property IDs for automatic sync
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Property</TableHead>
                    <TableHead>Address</TableHead>
                    <TableHead>Fixflo Property ID</TableHead>
                    <TableHead>Action</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {properties.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={4} className="text-center text-muted-foreground">
                        No properties found
                      </TableCell>
                    </TableRow>
                  ) : (
                    properties.map((property) => (
                      <TableRow key={property.id}>
                        <TableCell className="font-medium" data-testid={`text-property-${property.id}`}>
                          {property.name}
                        </TableCell>
                        <TableCell className="text-sm text-muted-foreground">
                          {property.address || "N/A"}
                        </TableCell>
                        <TableCell>
                          {property.fixfloPropertyId ? (
                            <Badge variant="outline" data-testid={`badge-fixflo-id-${property.id}`}>
                              {property.fixfloPropertyId}
                            </Badge>
                          ) : (
                            <span className="text-sm text-muted-foreground">Not mapped</span>
                          )}
                        </TableCell>
                        <TableCell>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleOpenPropertyDialog(property)}
                            data-testid={`button-map-property-${property.id}`}
                          >
                            <LinkIcon className="w-4 h-4 mr-2" />
                            {property.fixfloPropertyId ? "Update" : "Map"}
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="sync-status">
          <Card>
            <CardHeader>
              <CardTitle>Sync Status</CardTitle>
              <CardDescription>
                Monitor the status of data synchronization with Fixflo
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Entity Type</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Last Sync</TableHead>
                    <TableHead>Records Synced</TableHead>
                    <TableHead>Records Failed</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {syncStates.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={5} className="text-center text-muted-foreground">
                        No sync activity yet
                      </TableCell>
                    </TableRow>
                  ) : (
                    syncStates.map((state) => (
                      <TableRow key={state.id}>
                        <TableCell className="font-medium capitalize">
                          {state.entityType}
                        </TableCell>
                        <TableCell>{getSyncStatusBadge(state.syncStatus)}</TableCell>
                        <TableCell className="text-sm text-muted-foreground">
                          {state.lastSyncAt
                            ? new Date(state.lastSyncAt).toLocaleString()
                            : "Never"}
                        </TableCell>
                        <TableCell>{state.recordsSynced}</TableCell>
                        <TableCell>
                          {state.recordsFailed > 0 ? (
                            <span className="text-destructive font-medium">
                              {state.recordsFailed}
                            </span>
                          ) : (
                            state.recordsFailed
                          )}
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>

              {syncStates.some((state) => state.errorMessage) && (
                <div className="mt-4 space-y-2">
                  <h4 className="font-medium text-sm">Recent Errors</h4>
                  {syncStates
                    .filter((state) => state.errorMessage)
                    .map((state) => (
                      <div
                        key={state.id}
                        className="p-3 border border-destructive/50 rounded-md bg-destructive/10"
                      >
                        <p className="text-sm font-medium text-destructive capitalize">
                          {state.entityType}
                        </p>
                        <p className="text-xs text-muted-foreground mt-1">
                          {state.errorMessage}
                        </p>
                      </div>
                    ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      <Dialog open={isPropertyDialogOpen} onOpenChange={setIsPropertyDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Map Property to Fixflo</DialogTitle>
            <DialogDescription>
              Enter the Fixflo property ID for {selectedProperty?.name}
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="fixfloPropertyId">Fixflo Property ID</Label>
              <Input
                id="fixfloPropertyId"
                placeholder="Enter Fixflo property ID"
                value={fixfloPropertyId}
                onChange={(e) => setFixfloPropertyId(e.target.value)}
                data-testid="input-fixflo-property-id"
              />
              <p className="text-sm text-muted-foreground">
                This ID is used to link maintenance requests to the correct property in Fixflo
              </p>
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setIsPropertyDialogOpen(false)}
              data-testid="button-cancel-property-mapping"
            >
              Cancel
            </Button>
            <Button
              onClick={handleSavePropertyMapping}
              disabled={updatePropertyMutation.isPending || !fixfloPropertyId.trim()}
              data-testid="button-save-property-mapping"
            >
              {updatePropertyMutation.isPending ? "Saving..." : "Save Mapping"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/FixfloSyncButton.t.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { RefreshCw, ExternalLink, CheckCircle, AlertCircle } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";

interface FixfloSyncButtonProps {
  requestId: string;
  propertyId: string;
  fixfloIssueId?: string | null;
  fixfloStatus?: string | null;
  fixfloContractorName?: string | null;
  title: string;
}

export function FixfloSyncButton({
  requestId,
  propertyId,
  fixfloIssueId,
  fixfloStatus,
  fixfloContractorName,
  title,
}: FixfloSyncButtonProps) {
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  // Sync to Fixflo mutation
  const syncMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("POST", "/api/fixflo/issues", {
        maintenanceRequestId: requestId,
        propertyId,
      });
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/maintenance"] });
      toast({
        title: "Synced to Fixflo",
        description: `Maintenance request "${title}" has been synced to Fixflo`,
      });
      setIsDialogOpen(false);
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Sync Failed",
        description: error.message || "Failed to sync to Fixflo. Please check your integration settings.",
      });
    },
  });

  const handleSync = () => {
    syncMutation.mutate();
  };

  // If already synced, show status badge
  if (fixfloIssueId) {
    return (
      <div className="flex flex-col gap-1" data-testid={`fixflo-status-${requestId}`}>
        <Tooltip>
          <TooltipTrigger asChild>
            <Badge 
              variant="outline" 
              className="gap-1 cursor-help"
              data-testid={`badge-fixflo-status-${requestId}`}
            >
              <CheckCircle className="w-3 h-3 text-green-500" />
              Synced to Fixflo
            </Badge>
          </TooltipTrigger>
          <TooltipContent>
            <div className="text-xs space-y-1">
              <p><strong>Issue ID:</strong> {fixfloIssueId}</p>
              {fixfloStatus && <p><strong>Status:</strong> {fixfloStatus}</p>}
              {fixfloContractorName && <p><strong>Contractor:</strong> {fixfloContractorName}</p>}
            </div>
          </TooltipContent>
        </Tooltip>
        {fixfloContractorName && (
          <Badge variant="secondary" className="text-xs" data-testid={`badge-contractor-${requestId}`}>
            {fixfloContractorName}
          </Badge>
        )}
      </div>
    );
  }

  // Show sync button for unsync'd requests
  return (
    <>
      <Tooltip>
        <TooltipTrigger asChild>
          <Button
            variant="outline"
            size="sm"
            onClick={() => setIsDialogOpen(true)}
            className="gap-2"
            data-testid={`button-sync-fixflo-${requestId}`}
          >
            <ExternalLink className="w-4 h-4" />
            Sync to Fixflo
          </Button>
        </TooltipTrigger>
        <TooltipContent>
          <p>Send this maintenance request to Fixflo</p>
        </TooltipContent>
      </Tooltip>

      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Sync to Fixflo</DialogTitle>
            <DialogDescription>
              This will create a new issue in Fixflo for this maintenance request.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <p className="text-sm font-medium">Maintenance Request</p>
              <p className="text-sm text-muted-foreground">{title}</p>
            </div>
            <div className="p-4 bg-muted rounded-md">
              <p className="text-xs text-muted-foreground mb-2">
                The following information will be synced to Fixflo:
              </p>
              <ul className="text-xs text-muted-foreground space-y-1 list-disc list-inside">
                <li>Request title and description</li>
                <li>Priority level</li>
                <li>Property information</li>
                <li>Any uploaded photos</li>
              </ul>
            </div>
            <div className="flex items-start gap-2 p-3 bg-blue-50 dark:bg-blue-950/20 border border-blue-200 dark:border-blue-800 rounded-md">
              <AlertCircle className="w-4 h-4 text-blue-600 dark:text-blue-400 mt-0.5" />
              <p className="text-xs text-blue-900 dark:text-blue-100">
                Once synced, status updates from Fixflo will automatically be reflected here.
              </p>
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setIsDialogOpen(false)}
              disabled={syncMutation.isPending}
              data-testid="button-cancel-sync"
            >
              Cancel
            </Button>
            <Button
              onClick={handleSync}
              disabled={syncMutation.isPending}
              data-testid="button-confirm-sync"
            >
              {syncMutation.isPending ? (
                <>
                  <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                  Syncing...
                </>
              ) : (
                <>
                  <ExternalLink className="w-4 h-4 mr-2" />
                  Sync to Fixflo
                </>
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/PWAInstallPrompt.t.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { X, Download, Share } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";

interface BeforeInstallPromptEvent extends Event {
  prompt: () => Promise<void>;
  userChoice: Promise<{ outcome: 'accepted' | 'dismissed' }>;
}

export function PWAInstallPrompt() {
  const [deferredPrompt, setDeferredPrompt] = useState<BeforeInstallPromptEvent | null>(null);
  const [showIOSInstructions, setShowIOSInstructions] = useState(false);
  const [showAndroidPrompt, setShowAndroidPrompt] = useState(false);
  const [isDismissed, setIsDismissed] = useState(false);

  // Detect if running in standalone mode (already installed)
  const isStandalone = () => {
    return window.matchMedia('(display-mode: standalone)').matches ||
           (window.navigator as any).standalone === true;
  };

  // Detect iOS Safari
  const isIOSSafari = () => {
    const ua = window.navigator.userAgent;
    const iOS = /iPhone|iPad|iPod/.test(ua);
    const webkit = /WebKit/.test(ua);
    const notChrome = !/CriOS/.test(ua);
    const notFirefox = !/FxiOS/.test(ua);

    return iOS && webkit && notChrome && notFirefox;
  };

  // Check if user has previously dismissed the prompt
  const hasBeenDismissed = () => {
    const dismissed = localStorage.getItem('pwa-install-dismissed');
    if (!dismissed) return false;

    // Check if dismissed more than 7 days ago
    const dismissedTime = parseInt(dismissed, 10);
    const sevenDaysAgo = Date.now() - (7 * 24 * 60 * 60 * 1000);
    return dismissedTime > sevenDaysAgo;
  };

  useEffect(() => {
    // Don't show if already installed or dismissed
    if (isStandalone() || hasBeenDismissed()) {
      return;
    }

    // Android: Listen for beforeinstallprompt event
    const handleBeforeInstallPrompt = (e: Event) => {
      e.preventDefault();
      setDeferredPrompt(e as BeforeInstallPromptEvent);
      setShowAndroidPrompt(true);
    };

    window.addEventListener('beforeinstallprompt', handleBeforeInstallPrompt);

    // iOS: Show instructions after 5 seconds of page load
    if (isIOSSafari()) {
      const timer = setTimeout(() => {
        setShowIOSInstructions(true);
      }, 5000);

      return () => {
        clearTimeout(timer);
        window.removeEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
      };
    }

    // Track successful installation
    const handleAppInstalled = () => {
      setShowAndroidPrompt(false);
      setDeferredPrompt(null);
      localStorage.removeItem('pwa-install-dismissed');
    };

    window.addEventListener('appinstalled', handleAppInstalled);

    return () => {
      window.removeEventListener('beforeinstallprompt', handleBeforeInstallPrompt);
      window.removeEventListener('appinstalled', handleAppInstalled);
    };
  }, []);

  const handleInstallClick = async () => {
    if (!deferredPrompt) return;

    try {
      await deferredPrompt.prompt();
      const choiceResult = await deferredPrompt.userChoice;

      if (choiceResult.outcome === 'accepted') {
        console.log('User accepted the install prompt');
      } else {
        console.log('User dismissed the install prompt');
      }

      setDeferredPrompt(null);
      setShowAndroidPrompt(false);
    } catch (error) {
      console.error('Error showing install prompt:', error);
    }
  };

  const handleDismiss = () => {
    setIsDismissed(true);
    setShowAndroidPrompt(false);
    setShowIOSInstructions(false);
    localStorage.setItem('pwa-install-dismissed', Date.now().toString());
  };

  // Don't render if dismissed
  if (isDismissed || isStandalone()) {
    return null;
  }

  // Android Install Prompt
  if (showAndroidPrompt && deferredPrompt) {
    return (
      <div className="fixed bottom-4 left-4 right-4 z-50 md:left-auto md:right-4 md:max-w-md">
        <Card className="shadow-lg border-2 border-primary/20">
          <CardHeader className="pb-3">
            <div className="flex items-start justify-between gap-2">
              <div className="flex items-center gap-2">
                <Download className="w-5 h-5 text-primary" />
                <CardTitle className="text-base">Install Inspect360</CardTitle>
              </div>
              <Button
                size="icon"
                variant="ghost"
                className="h-6 w-6 -mt-1"
                onClick={handleDismiss}
                data-testid="button-dismiss-install"
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
            <CardDescription className="text-sm">
              Install our app for quick access, offline support, and a better experience
            </CardDescription>
          </CardHeader>
          <CardContent className="pb-4">
            <div className="flex gap-2">
              <Button
                onClick={handleInstallClick}
                className="flex-1"
                data-testid="button-install-pwa"
              >
                Install App
              </Button>
              <Button
                onClick={handleDismiss}
                variant="outline"
                data-testid="button-not-now"
              >
                Not Now
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // iOS Instructions
  if (showIOSInstructions && isIOSSafari()) {
    return (
      <div className="fixed bottom-4 left-4 right-4 z-50 md:left-auto md:right-4 md:max-w-md">
        <Alert className="shadow-lg border-2 border-primary/20 bg-card">
          <div className="flex items-start justify-between gap-2">
            <Share className="w-5 h-5 text-primary mt-0.5" />
            <div className="flex-1">
              <h4 className="font-semibold mb-1 text-sm">Install Inspect360</h4>
              <AlertDescription className="text-sm space-y-2">
                <p>Add this app to your home screen for quick access:</p>
                <ol className="list-decimal list-inside space-y-1 text-xs text-muted-foreground">
                  <li>Tap the <strong>Share</strong> button <Share className="w-3 h-3 inline" /> below</li>
                  <li>Scroll down and tap <strong>"Add to Home Screen"</strong></li>
                  <li>Tap <strong>"Add"</strong> to confirm</li>
                </ol>
                <Button
                  onClick={handleDismiss}
                  variant="outline"
                  size="sm"
                  className="w-full mt-2"
                  data-testid="button-dismiss-ios-instructions"
                >
                  Got it
                </Button>
              </AlertDescription>
            </div>
            <Button
              size="icon"
              variant="ghost"
              className="h-6 w-6 -mt-1"
              onClick={handleDismiss}
              data-testid="button-close-ios-instructions"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>
        </Alert>
      </div>
    );
  }

  return null;
}

server/subscriptionService.ts.content:
import { storage } from "./storage";
import type { Organization, CreditBatch, InsertCreditBatch, InsertCreditLedger } from "@shared/schema";

export class SubscriptionService {
  /**
   * Consume credits using FIFO logic from available batches
   * @param organizationId - Organization consuming credits
   * @param quantity - Number of credits to consume
   * @param linkedEntityType - Type of entity consuming credits (e.g., "inspection")
   * @param linkedEntityId - ID of the entity consuming credits
   * @param notes - Optional notes about the consumption
   * @returns true if successful, throws error if insufficient credits
   */
  async consumeCredits(
    organizationId: string,
    quantity: number,
    linkedEntityType: string,
    linkedEntityId: string,
    notes?: string
  ): Promise<void> {
    if (quantity <= 0) {
      throw new Error("Quantity must be positive");
    }

    // Get available batches ordered by FIFO (earliest expiry first)
    const batches = await storage.getAvailableCreditBatches(organizationId);

    // First, calculate total available credits and plan deductions WITHOUT modifying database
    let totalAvailable = 0;
    const plannedDeductions: Array<{ 
      batch: CreditBatch; 
      toConsume: number; 
      newRemaining: number;
      unitCost?: number;
    }> = [];

    let remainingToConsume = quantity;

    for (const batch of batches) {
      totalAvailable += batch.remainingQuantity;

      if (remainingToConsume > 0) {
        const toConsume = Math.min(batch.remainingQuantity, remainingToConsume);
        plannedDeductions.push({
          batch,
          toConsume,
          newRemaining: batch.remainingQuantity - toConsume,
          unitCost: batch.unitCostMinorUnits ?? undefined,
        });
        remainingToConsume -= toConsume;
      }
    }

    // Check if we have insufficient credits BEFORE making any changes
    if (remainingToConsume > 0) {
      throw new Error(`Insufficient credits. Needed ${quantity}, available ${totalAvailable}.`);
    }

    // Only NOW apply the planned deductions to the database
    const consumedBatches: Array<{ batchId: string; consumed: number; unitCost?: number }> = [];

    for (const deduction of plannedDeductions) {
      await storage.updateCreditBatch(deduction.batch.id, {
        remainingQuantity: deduction.newRemaining,
      });

      consumedBatches.push({
        batchId: deduction.batch.id,
        consumed: deduction.toConsume,
        unitCost: deduction.unitCost,
      });
    }

    // Record consumption in credit ledger
    for (const consumed of consumedBatches) {
      await storage.createCreditLedgerEntry({
        organizationId,
        source: "consumption" as any,
        quantity: -consumed.consumed,
        batchId: consumed.batchId,
        unitCostMinorUnits: consumed.unitCost,
        notes: notes || `Consumed ${consumed.consumed} credits for ${linkedEntityType}`,
        linkedEntityType,
        linkedEntityId,
      });
    }

    // Also update the legacy creditsRemaining field for backward compatibility
    const org = await storage.getOrganization(organizationId);
    if (org) {
      await storage.updateOrganizationCredits(
        organizationId,
        Math.max(0, (org.creditsRemaining ?? 0) - quantity)
      );
    }
  }

  /**
   * Grant credits to an organization (from plan inclusion, top-up, or admin grant)
   * @param organizationId - Organization receiving credits
   * @param quantity - Number of credits to grant
   * @param source - Source of credits ("plan_inclusion", "topup", "admin_grant")
   * @param expiresAt - Optional expiration date
   * @param metadata - Optional metadata (subscriptionId, topupOrderId, adminNotes)
   * @param unitCostMinorUnits - Optional unit cost for valuation
   * @returns Created credit batch
   */
  async grantCredits(
    organizationId: string,
    quantity: number,
    source: "plan_inclusion" | "topup" | "admin_grant",
    expiresAt?: Date,
    metadata?: { subscriptionId?: string; topupOrderId?: string; adminNotes?: string },
    unitCostMinorUnits?: number
  ): Promise<CreditBatch> {
    if (quantity <= 0) {
      throw new Error("Quantity must be positive");
    }

    // Create credit batch
    const batch = await storage.createCreditBatch({
      organizationId,
      grantedQuantity: quantity,
      remainingQuantity: quantity,
      grantSource: source as any,
      grantedAt: new Date(),
      expiresAt: expiresAt ?? null,
      unitCostMinorUnits: unitCostMinorUnits ?? null,
      rolled: false,
      metadataJson: metadata ?? null,
    });

    // Record in credit ledger
    await storage.createCreditLedgerEntry({
      organizationId,
      source: source as any,
      quantity,
      batchId: batch.id,
      unitCostMinorUnits: unitCostMinorUnits ?? null,
      notes: `Granted ${quantity} credits from ${source}`,
      linkedEntityType: source === "topup" ? "topup_order" : "subscription",
      linkedEntityId: metadata?.topupOrderId ?? metadata?.subscriptionId ?? null,
    });

    // Also update the legacy creditsRemaining field for backward compatibility
    const org = await storage.getOrganization(organizationId);
    if (org) {
      await storage.updateOrganizationCredits(
        organizationId,
        (org.creditsRemaining ?? 0) + quantity
      );
    }

    return batch;
  }

  /**
   * Handle rollover logic at billing cycle start
   * @param organizationId - Organization to process
   * @param currentPeriodEnd - End of the current billing period
   */
  async processRollover(organizationId: string, currentPeriodEnd: Date): Promise<void> {
    const now = new Date();

    // Get all batches for the organization
    const allBatches = await storage.getCreditBatchesByOrganization(organizationId);

    // Expire old rolled batches (from previous cycle)
    const oldRolledBatches = allBatches.filter(
      b => b.rolled && b.expiresAt && b.expiresAt < now && b.remainingQuantity > 0
    );

    for (const batch of oldRolledBatches) {
      await storage.expireCreditBatch(batch.id);

      // Record expiry in ledger
      await storage.createCreditLedgerEntry({
        organizationId,
        source: "expiry" as any,
        quantity: -batch.remainingQuantity,
        batchId: batch.id,
        notes: `Expired ${batch.remainingQuantity} rolled credits from previous cycle`,
      });
    }

    // Find last cycle's main batch (non-rolled, with remaining credits)
    const lastCycleBatch = allBatches.find(
      b => !b.rolled && b.remainingQuantity > 0 && b.expiresAt && b.expiresAt <= now
    );

    if (lastCycleBatch && lastCycleBatch.remainingQuantity > 0) {
      // Roll over remaining credits to new batch
      const rolledBatch = await storage.createCreditBatch({
        organizationId,
        grantedQuantity: lastCycleBatch.remainingQuantity,
        remainingQuantity: lastCycleBatch.remainingQuantity,
        grantSource: "plan_inclusion" as any,
        grantedAt: new Date(),
        expiresAt: currentPeriodEnd,
        rolled: true,
        metadataJson: {
          originalBatchId: lastCycleBatch.id,
        } as any,
      });

      // Record rollover in ledger
      await storage.createCreditLedgerEntry({
        organizationId,
        source: "plan_inclusion" as any,
        quantity: lastCycleBatch.remainingQuantity,
        batchId: rolledBatch.id,
        notes: `Rolled over ${lastCycleBatch.remainingQuantity} credits from previous cycle`,
      });

      // Mark original batch as consumed (set to 0)
      await storage.updateCreditBatch(lastCycleBatch.id, {
        remainingQuantity: 0,
      });
    }
  }

  /**
   * Get effective pricing for a plan and country
   * @param planId - Plan ID
   * @param countryCode - ISO 3166-1 alpha-2 country code
   * @returns Pricing information
   */
  async getEffectivePricing(
    planId: string,
    countryCode: string
  ): Promise<{
    monthlyPrice: number;
    currency: string;
    includedCredits: number;
    topupPricePerCredit?: number;
  }> {
    // Get base plan
    const plan = await storage.getPlan(planId);
    if (!plan) {
      throw new Error("Plan not found");
    }

    // Check for country override
    const override = await storage.getCountryPricingOverride(countryCode, planId);

    if (override) {
      return {
        monthlyPrice: override.monthlyPriceMinorUnits,
        currency: override.currency,
        includedCredits: override.includedCreditsOverride ?? plan.includedCredits,
        topupPricePerCredit: override.topupPricePerCreditMinorUnits ?? undefined,
      };
    }

    // Return base plan pricing (GBP)
    return {
      monthlyPrice: plan.monthlyPriceGbp,
      currency: "GBP",
      includedCredits: plan.includedCredits,
      topupPricePerCredit: 75, // Default 75 pence per credit
    };
  }

  /**
   * Calculate inspection credit cost based on complexity
   * @param inspectionType - Type of inspection
   * @param complexity - Complexity level (1, 2, or 3)
   * @returns Number of credits required
   */
  calculateInspectionCreditCost(
    inspectionType: string,
    complexity: number = 1
  ): number {
    // Base cost is the complexity level
    let cost = complexity;

    // Cap at 3 credits maximum (as per specification)
    return Math.min(cost, 3);
  }
}

export const subscriptionService = new SubscriptionService();

