import puppeteer from "puppeteer";
import chromium from "@sparticuz/chromium";
import { format } from "date-fns";

// HTML escape utility to prevent XSS
function escapeHtml(unsafe: string): string {
  if (typeof unsafe !== 'string') return String(unsafe || '');

  return unsafe
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#039;");
}

// Sanitize URL for use in HTML attributes (whitelist-based approach)
function sanitizeUrl(url: string, baseUrl?: string): string {
  if (typeof url !== 'string' || !url.trim()) return '';

  const trimmed = url.trim();
  const lower = trimmed.toLowerCase();

  // Handle relative URLs (convert to absolute using baseUrl)
  if (trimmed.startsWith('/') && baseUrl) {
    const absoluteUrl = `${baseUrl}${trimmed}`;
    return escapeHtml(absoluteUrl);
  }

  // Allow only safe protocols via whitelist
  const safeProtocols = ['https://', 'http://'];
  const isSafeProtocol = safeProtocols.some(protocol => lower.startsWith(protocol));

  if (!isSafeProtocol) {
    // For data URLs, only allow safe image types (NO SVG - can contain XSS)
    const safeDataImages = [
      'data:image/png',
      'data:image/jpeg',
      'data:image/jpg',
      'data:image/gif',
      'data:image/webp',
    ];

    const isSafeDataUrl = safeDataImages.some(prefix => lower.startsWith(prefix));

    if (!isSafeDataUrl) {
      // Reject all other protocols/schemes (javascript:, data:image/svg+xml, vbscript:, etc.)
      console.warn(`Blocked potentially unsafe URL: ${url.substring(0, 50)}...`);
      return '';
    }
  }

  // Return escaped URL (escape special chars for HTML attribute safety)
  return escapeHtml(trimmed);
}

// Format text while preserving line breaks but escaping dangerous HTML
function formatText(text: string): string {
  if (!text) return '';

  // First escape HTML to prevent XSS
  const escaped = escapeHtml(text);

  // Convert line breaks to <br> tags for proper display
  const withBreaks = escaped.replace(/\n/g, '<br>');

  return withBreaks;
}

interface TemplateField {
  id: string;
  key?: string;
  label: string;
  type: string;
  description?: string;
  required?: boolean;
  placeholder?: string;
  options?: string[];
  validation?: Record<string, any>;
  dependsOn?: Record<string, any>;
  includeCondition?: boolean;
  includeCleanliness?: boolean;
}

interface TemplateSection {
  id: string;
  title: string;
  description?: string;
  repeatable?: boolean;
  fields: TemplateField[];
}

interface Inspection {
  id: string;
  organizationId: string;
  templateId?: string;
  templateSnapshotJson?: { sections: TemplateSection[] };
  propertyId?: string;
  blockId?: string;
  inspectorId: string;
  type: string;
  status: string;
  scheduledDate?: string;
  startedAt?: string;
  completedDate?: string;
  submittedAt?: string;
  notes?: string;
  property?: {
    id: string;
    name: string;
    address: string;
    propertyType?: string;
  };
  block?: {
    id: string;
    name: string;
    address: string;
  };
  inspector?: {
    id: string;
    email: string;
    firstName?: string;
    lastName?: string;
  };
}

interface InspectionEntry {
  id: string;
  inspectionId: string;
  sectionRef: string;
  fieldKey: string;
  value?: any;
  note?: string;
  photos?: string[];
  condition?: string;
  cleanliness?: string;
  markedForReview?: boolean;
}

export async function generateInspectionPDF(
  inspection: Inspection,
  entries: InspectionEntry[],
  baseUrl: string
): Promise<Buffer> {
  const html = generateInspectionHTML(inspection, entries, baseUrl);

  let browser;
  try {
    // Use chromium for serverless environments
    browser = await puppeteer.launch({
      args: chromium.args,
      executablePath: await chromium.executablePath(),
      headless: true,
    });

    const page = await browser.newPage();
    await page.setContent(html, {
      waitUntil: "networkidle0",
    });

    const pdf = await page.pdf({
      format: "A4",
      printBackground: true,
      margin: {
        top: "20mm",
        right: "15mm",
        bottom: "20mm",
        left: "15mm",
      },
    });

    return Buffer.from(pdf);
  } finally {
    if (browser) {
      await browser.close();
    }
  }
}

function renderFieldValue(value: any, field: TemplateField): string {
  if (value === null || value === undefined || value === "") {
    return '<span style="color: #999;">Not provided</span>';
  }

  try {
    if (typeof value === "object" && value !== null) {
      if (Array.isArray(value)) {
        // Escape each array item
        const escaped = value.map(v => escapeHtml(String(v)));
        return escaped.length > 0 ? escaped.join(", ") : '<span style="color: #999;">Empty</span>';
      }
      // Escape JSON output
      return `<pre style="background: #f5f5f5; padding: 8px; border-radius: 4px; font-size: 12px; overflow-x: auto;">${escapeHtml(JSON.stringify(value, null, 2))}</pre>`;
    }

    if (field.type === "checkbox" || field.type === "toggle") {
      return value ? " Yes" : " No";
    }

    if (field.type === "date" && typeof value === "string") {
      try {
        const formatted = format(new Date(value), "PPP");
        return escapeHtml(formatted);
      } catch {
        return escapeHtml(String(value));
      }
    }

    // Escape all string values
    return escapeHtml(String(value));
  } catch (error) {
    console.error("Error rendering field value:", error);
    return escapeHtml(String(value || ""));
  }
}

function generateInspectionHTML(
  inspection: Inspection,
  entries: InspectionEntry[],
  baseUrl: string
): string {
  const templateStructure = inspection.templateSnapshotJson as { sections: TemplateSection[] } | null;
  const sections = templateStructure?.sections || [];

  const propertyName = inspection.property?.name || "Unknown Property";
  const propertyAddress = inspection.property?.address || "No address";
  const inspectorName = inspection.inspector
    ? `${inspection.inspector.firstName || ""} ${inspection.inspector.lastName || ""}`.trim() ||
      inspection.inspector.email
    : "Unknown Inspector";
  const inspectionDate = inspection.completedDate || inspection.scheduledDate || inspection.startedAt;
  const formattedDate = inspectionDate ? format(new Date(inspectionDate), "PPP") : "Not specified";

  // Build entries map
  const entriesMap = new Map<string, InspectionEntry>();
  entries.forEach((entry) => {
    const key = `${entry.sectionRef}-${entry.fieldKey}`;
    entriesMap.set(key, entry);
  });

  // Generate sections HTML
  let sectionsHTML = "";
  sections.forEach((section) => {
    let fieldsHTML = "";
    section.fields.forEach((field) => {
      const key = `${section.id}-${field.key || field.id}`;
      const entry = entriesMap.get(key);

      const value = entry?.value;
      const note = entry?.note;
      const photos = entry?.photos || [];
      const condition = entry?.condition;
      const cleanliness = entry?.cleanliness;

      // Helper function to check if a value is truly empty
      const isEmpty = (val: any): boolean => {
        if (val === null || val === undefined) return true;
        if (typeof val === 'string') return val.trim() === '';
        if (Array.isArray(val)) return val.length === 0;
        if (typeof val === 'object') return Object.keys(val).length === 0;
        if (typeof val === 'boolean') return false; // booleans are always valid
        return false;
      };

      // Skip fields with no data, photos, notes, or ratings
      const hasValue = !isEmpty(value);
      const hasPhotos = photos.length > 0;
      const hasNote = !isEmpty(note);
      const hasCondition = !isEmpty(condition);
      const hasCleanliness = !isEmpty(cleanliness);

      if (!hasValue && !hasPhotos && !hasNote && !hasCondition && !hasCleanliness) {
        return; // Skip this field
      }

      fieldsHTML += `
        <div style="margin-bottom: 24px; page-break-inside: avoid;">
          <div style="font-weight: 600; color: #1a1a1a; margin-bottom: 8px;">
            ${escapeHtml(field.label)}
            ${field.required ? '<span style="color: #ef4444;">*</span>' : ''}
          </div>
          ${field.description ? `<div style="color: #666; font-size: 14px; margin-bottom: 8px;">${escapeHtml(field.description)}</div>` : ""}

          ${value !== undefined && value !== null && value !== "" ? `
            <div style="color: #333; margin-bottom: 8px;">
              ${renderFieldValue(value, field)}
            </div>
          ` : ""}

          ${condition ? `
            <div style="margin-top: 8px;">
              <span style="font-size: 13px; color: #666;">Condition:</span>
              <span style="display: inline-block; background: #f0fdf4; color: #16a34a; padding: 2px 8px; border-radius: 4px; font-size: 13px; margin-left: 8px;">
                ${escapeHtml(condition)}
              </span>
            </div>
          ` : ""}

          ${cleanliness ? `
            <div style="margin-top: 8px;">
              <span style="font-size: 13px; color: #666;">Cleanliness:</span>
              <span style="display: inline-block; background: #eff6ff; color: #2563eb; padding: 2px 8px; border-radius: 4px; font-size: 13px; margin-left: 8px;">
                ${escapeHtml(cleanliness)}
              </span>
            </div>
          ` : ""}

          ${photos.length > 0 ? `
            <div style="margin-top: 12px;">
              <div style="font-size: 13px; color: #666; margin-bottom: 8px;">Photos (${photos.length}):</div>
              <div style="display: grid; grid-template-columns: repeat(auto-fill, minmax(200px, 1fr)); gap: 12px;">
                ${photos.map((photo) => `
                  <div style="border: 1px solid #e5e7eb; border-radius: 8px; overflow: hidden;">
                    <img src="${sanitizeUrl(photo, baseUrl)}" alt="Inspection photo" style="width: 100%; height: 150px; object-fit: cover;" />
                  </div>
                `).join("")}
              </div>
            </div>
          ` : ""}

          ${note ? `
            <div style="margin-top: 12px; background: #fef3c7; border-left: 3px solid #f59e0b; padding: 12px; border-radius: 4px;">
              <div style="font-weight: 500; color: #92400e; margin-bottom: 4px; font-size: 13px;">Note:</div>
              <div style="color: #78350f; font-size: 14px; white-space: pre-wrap;">${formatText(note)}</div>
            </div>
          ` : ""}
        </div>
      `;
    });

    // Only include section if it has fields with data
    if (fieldsHTML.trim()) {
      sectionsHTML += `
        <div style="margin-bottom: 32px; page-break-inside: avoid;">
          <h2 style="font-size: 20px; font-weight: 700; color: #00D5CC; margin-bottom: 16px; padding-bottom: 8px; border-bottom: 2px solid #00D5CC;">
            ${escapeHtml(section.title)}
          </h2>
          ${section.description ? `<p style="color: #666; margin-bottom: 16px;">${escapeHtml(section.description)}</p>` : ""}
          ${fieldsHTML}
        </div>
      `;
    }
  });

  return `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }

    body {
      font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
      line-height: 1.6;
      color: #333;
      background: white;
    }

    .container {
      max-width: 100%;
      padding: 0;
    }

    .cover-page {
      height: 100vh;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      text-align: center;
      background: linear-gradient(135deg, #00D5CC 0%, #3B7A8C 100%);
      color: white;
      page-break-after: always;
    }

    .cover-logo {
      font-size: 72px;
      font-weight: 800;
      margin-bottom: 24px;
      letter-spacing: -2px;
    }

    .cover-title {
      font-size: 48px;
      font-weight: 700;
      margin-bottom: 16px;
    }

    .cover-property {
      font-size: 32px;
      font-weight: 400;
      margin-bottom: 48px;
      opacity: 0.95;
    }

    .cover-details {
      font-size: 18px;
      opacity: 0.9;
      margin-top: 24px;
    }

    .header {
      background: white;
      padding: 32px 0;
      border-bottom: 3px solid #00D5CC;
      margin-bottom: 32px;
    }

    .header-title {
      font-size: 32px;
      font-weight: 800;
      color: #00D5CC;
      margin-bottom: 8px;
    }

    .header-subtitle {
      font-size: 24px;
      color: #3B7A8C;
      font-weight: 600;
    }

    .info-grid {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 24px;
      margin-bottom: 32px;
      page-break-inside: avoid;
    }

    .info-item {
      background: #f9fafb;
      padding: 16px;
      border-radius: 8px;
      border-left: 4px solid #00D5CC;
    }

    .info-label {
      font-size: 13px;
      color: #666;
      text-transform: uppercase;
      letter-spacing: 0.5px;
      margin-bottom: 4px;
      font-weight: 600;
    }

    .info-value {
      font-size: 16px;
      color: #1a1a1a;
      font-weight: 500;
    }

    .status-badge {
      display: inline-block;
      padding: 6px 16px;
      border-radius: 20px;
      font-size: 14px;
      font-weight: 600;
      background: #dcfce7;
      color: #166534;
    }

    @media print {
      .page-break {
        page-break-before: always;
      }
    }
  </style>
</head>
<body>
  <div class="container">
    <!-- Cover Page -->
    <div class="cover-page">
      <div class="cover-logo">INSPECT360</div>
      <div class="cover-title">Inspection Report</div>
      <div class="cover-property">${escapeHtml(propertyName)}</div>
      <div class="cover-details">
        <div>${escapeHtml(formattedDate)}</div>
        <div style="margin-top: 8px;">${escapeHtml(inspection.type.charAt(0).toUpperCase() + inspection.type.slice(1).replace(/_/g, " "))}</div>
      </div>
    </div>

    <!-- Main Content -->
    <div style="padding: 0;">
      <div class="header">
        <div class="header-title">Inspection Report</div>
        <div class="header-subtitle">${escapeHtml(propertyName)}</div>
      </div>

      <!-- Property Information -->
      <div class="info-grid">
        <div class="info-item">
          <div class="info-label">Property Address</div>
          <div class="info-value">${escapeHtml(propertyAddress)}</div>
        </div>
        <div class="info-item">
          <div class="info-label">Inspection Type</div>
          <div class="info-value">${escapeHtml(inspection.type.charAt(0).toUpperCase() + inspection.type.slice(1).replace(/_/g, " "))}</div>
        </div>
        <div class="info-item">
          <div class="info-label">Inspector</div>
          <div class="info-value">${escapeHtml(inspectorName)}</div>
        </div>
        <div class="info-item">
          <div class="info-label">Inspection Date</div>
          <div class="info-value">${escapeHtml(formattedDate)}</div>
        </div>
        <div class="info-item">
          <div class="info-label">Status</div>
          <div class="info-value">
            <span class="status-badge">${escapeHtml(inspection.status.toUpperCase())}</span>
          </div>
        </div>
        ${inspection.notes ? `
          <div class="info-item" style="grid-column: 1 / -1;">
            <div class="info-label">General Notes</div>
            <div class="info-value">${escapeHtml(inspection.notes)}</div>
          </div>
        ` : ""}
      </div>

      <!-- Inspection Sections -->
      ${sectionsHTML}
    </div>
  </div>
</body>
</html>
  `;
}

client/src/components/SettingsTeamsPanel.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage, FormDescription } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Plus, Edit2, Trash2, Users, Mail, X } from "lucide-react";
import { z } from "zod";
import type { InspectionCategory } from "@shared/schema";

// Team schema
const teamFormSchema = z.object({
  name: z.string().min(1, "Team name is required"),
  description: z.string().optional(),
  email: z.string().email("Valid email is required"),
  isActive: z.boolean().default(true),
  userIds: z.array(z.string()).default([]),
  contactIds: z.array(z.string()).default([]),
  categories: z.array(z.string()).default([]),
});

type TeamFormValues = z.infer<typeof teamFormSchema>;

interface Team {
  id: string;
  name: string;
  description: string | null;
  email: string;
  isActive: boolean;
  memberCount: number;
  createdAt: Date;
  updatedAt: Date;
}

interface TeamMember {
  id: string;
  teamId: string;
  userId: string | null;
  contactId: string | null;
  role: string;
  user?: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
    role: string;
  } | null;
  contact?: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
    type: string;
  } | null;
}

interface TeamCategory {
  id: string;
  teamId: string;
  category: string;
}

interface User {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  role: string;
}

interface Contact {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  type: string;
}

export default function SettingsTeamsPanel() {
  const { toast } = useToast();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingTeam, setEditingTeam] = useState<Team | null>(null);
  const [deletingTeam, setDeletingTeam] = useState<Team | null>(null);

  // Fetch teams
  const { data: teams = [], isLoading: teamsLoading } = useQuery<Team[]>({
    queryKey: ['/api/teams'],
  });

  // Fetch users (organization members)
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ['/api/users'],
  });

  // Fetch contacts (contractors)
  const { data: contacts = [] } = useQuery<Contact[]>({
    queryKey: ['/api/contacts'],
  });

  // Fetch inspection categories
  const { data: inspectionCategories = [] } = useQuery<InspectionCategory[]>({
    queryKey: ['/api/inspection-categories'],
  });

  // Create team mutation - uses single atomic endpoint
  const createMutation = useMutation({
    mutationFn: async (data: TeamFormValues) => {
      // Single server-side transactional create
      return await apiRequest("POST", "/api/teams/full", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/teams'] });
      toast({
        title: "Success",
        description: "Team created successfully",
      });
      setIsCreateDialogOpen(false);
      createForm.reset();
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to create team",
      });
    },
  });

  // Update team mutation - uses single atomic endpoint
  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: TeamFormValues }) => {
      // Single server-side transactional update
      return await apiRequest("PATCH", `/api/teams/${id}/full`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/teams'] });
      toast({
        title: "Success",
        description: "Team updated successfully",
      });
      setEditingTeam(null);
      editForm.reset();
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to update team",
      });
    },
  });

  // Delete team mutation
  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/teams/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/teams'] });
      toast({
        title: "Success",
        description: "Team deleted successfully",
      });
      setDeletingTeam(null);
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to delete team",
      });
    },
  });

  const createForm = useForm<TeamFormValues>({
    resolver: zodResolver(teamFormSchema),
    defaultValues: {
      name: "",
      description: "",
      email: "",
      isActive: true,
      userIds: [],
      contactIds: [],
      categories: [],
    },
  });

  const editForm = useForm<TeamFormValues>({
    resolver: zodResolver(teamFormSchema),
    defaultValues: {
      name: "",
      description: "",
      email: "",
      isActive: true,
      userIds: [],
      contactIds: [],
      categories: [],
    },
  });

  // Load team data when editing
  const loadTeamForEdit = async (team: Team) => {
    const members: any = await apiRequest("GET", `/api/teams/${team.id}/members`);
    const categories: any = await apiRequest("GET", `/api/teams/${team.id}/categories`);

    editForm.reset({
      name: team.name,
      description: team.description || "",
      email: team.email,
      isActive: team.isActive,
      userIds: members.filter((m: any) => m.userId).map((m: any) => m.userId!),
      contactIds: members.filter((m: any) => m.contactId).map((m: any) => m.contactId!),
      categories: categories.map((c: any) => c.category),
    });

    setEditingTeam(team);
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold tracking-tight">Teams</h2>
          <p className="text-muted-foreground">
            Manage work order teams and email distribution lists
          </p>
        </div>
        <Button 
          onClick={() => setIsCreateDialogOpen(true)} 
          data-testid="button-create-team"
        >
          <Plus className="h-4 w-4 mr-2" />
          Create Team
        </Button>
      </div>

      {teamsLoading ? (
        <div className="space-y-4">
          {[1, 2, 3].map(i => (
            <Card key={i} className="animate-pulse">
              <CardHeader>
                <div className="h-6 bg-muted rounded w-1/4" />
                <div className="h-4 bg-muted rounded w-1/2" />
              </CardHeader>
            </Card>
          ))}
        </div>
      ) : teams.length === 0 ? (
        <Card>
          <CardContent className="py-8 text-center">
            <Users className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
            <p className="text-lg font-medium">No teams yet</p>
            <p className="text-muted-foreground mb-4">
              Create your first team to manage work order assignments
            </p>
            <Button onClick={() => setIsCreateDialogOpen(true)} data-testid="button-create-first-team">
              <Plus className="h-4 w-4 mr-2" />
              Create Team
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-4">
          {teams.map((team) => (
            <Card key={team.id} data-testid={`card-team-${team.id}`}>
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-1">
                      <CardTitle className="text-lg" data-testid={`text-team-name-${team.id}`}>
                        {team.name}
                      </CardTitle>
                      {!team.isActive && (
                        <Badge variant="secondary" data-testid={`badge-team-inactive-${team.id}`}>
                          Inactive
                        </Badge>
                      )}
                    </div>
                    {team.description && (
                      <CardDescription data-testid={`text-team-description-${team.id}`}>
                        {team.description}
                      </CardDescription>
                    )}
                    <div className="flex items-center gap-4 mt-3 text-sm">
                      <div className="flex items-center gap-1 text-muted-foreground">
                        <Mail className="h-4 w-4" />
                        <span data-testid={`text-team-email-${team.id}`}>{team.email}</span>
                      </div>
                      <div className="flex items-center gap-1 text-muted-foreground">
                        <Users className="h-4 w-4" />
                        <span data-testid={`text-team-member-count-${team.id}`}>
                          {team.memberCount} {team.memberCount === 1 ? 'member' : 'members'}
                        </span>
                      </div>
                    </div>
                  </div>
                  <div className="flex gap-2">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => loadTeamForEdit(team)}
                      data-testid={`button-edit-team-${team.id}`}
                    >
                      <Edit2 className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => setDeletingTeam(team)}
                      data-testid={`button-delete-team-${team.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
            </Card>
          ))}
        </div>
      )}

      {/* Create Team Dialog */}
      <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto" data-testid="dialog-create-team">
          <DialogHeader>
            <DialogTitle>Create Team</DialogTitle>
            <DialogDescription>
              Create a team for work order management with email distribution list
            </DialogDescription>
          </DialogHeader>
          <Form {...createForm}>
            <form onSubmit={createForm.handleSubmit((data) => createMutation.mutate(data))} className="space-y-4">
              <FormField
                control={createForm.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Team Name</FormLabel>
                    <FormControl>
                      <Input {...field} placeholder="e.g., Maintenance Team" data-testid="input-team-name" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={createForm.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Description (Optional)</FormLabel>
                    <FormControl>
                      <Input {...field} placeholder="Team description" data-testid="input-team-description" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={createForm.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Distribution Email</FormLabel>
                    <FormControl>
                      <Input {...field} type="email" placeholder="team@example.com" data-testid="input-team-email" />
                    </FormControl>
                    <FormDescription>
                      Work order notifications will be sent to this email
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="userIds"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Team Members (Staff)</FormLabel>
                    <div className="space-y-2">
                      {users.map((user) => (
                        <div key={user.id} className="flex items-center space-x-2">
                          <Checkbox
                            checked={field.value?.includes(user.id)}
                            onCheckedChange={(checked) => {
                              const updatedValue = checked
                                ? [...(field.value || []), user.id]
                                : (field.value || []).filter((id) => id !== user.id);
                              field.onChange(updatedValue);
                            }}
                            data-testid={`checkbox-user-${user.id}`}
                          />
                          <Label className="font-normal cursor-pointer">
                            {user.firstName} {user.lastName} ({user.email})
                          </Label>
                        </div>
                      ))}
                    </div>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="contactIds"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Team Members (Contractors)</FormLabel>
                    <div className="space-y-2">
                      {contacts.filter(c => c.type === 'contractor').map((contact) => (
                        <div key={contact.id} className="flex items-center space-x-2">
                          <Checkbox
                            checked={field.value?.includes(contact.id)}
                            onCheckedChange={(checked) => {
                              const updatedValue = checked
                                ? [...(field.value || []), contact.id]
                                : (field.value || []).filter((id) => id !== contact.id);
                              field.onChange(updatedValue);
                            }}
                            data-testid={`checkbox-contact-${contact.id}`}
                          />
                          <Label className="font-normal cursor-pointer">
                            {contact.firstName} {contact.lastName} ({contact.email})
                          </Label>
                        </div>
                      ))}
                    </div>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="categories"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Maintenance Categories</FormLabel>
                    <FormDescription>
                      Assign categories to auto-route work orders to this team
                    </FormDescription>
                    <div className="space-y-2">
                      {inspectionCategories.map((category) => (
                        <div key={category.id} className="flex items-center space-x-2">
                          <Checkbox
                            checked={field.value?.includes(category.name)}
                            onCheckedChange={(checked) => {
                              const updatedValue = checked
                                ? [...(field.value || []), category.name]
                                : (field.value || []).filter((name) => name !== category.name);
                              field.onChange(updatedValue);
                            }}
                            data-testid={`checkbox-category-${category.id}`}
                          />
                          <Label className="font-normal cursor-pointer">
                            {category.name}
                          </Label>
                        </div>
                      ))}
                    </div>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="isActive"
                render={({ field }) => (
                  <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                    <FormControl>
                      <Checkbox
                        checked={field.value}
                        onCheckedChange={field.onChange}
                        data-testid="checkbox-team-active"
                      />
                    </FormControl>
                    <div className="space-y-1 leading-none">
                      <FormLabel>Active</FormLabel>
                      <FormDescription>
                        Only active teams can be assigned work orders
                      </FormDescription>
                    </div>
                  </FormItem>
                )}
              />

              <DialogFooter>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsCreateDialogOpen(false)}
                  data-testid="button-cancel-create-team"
                >
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={createMutation.isPending}
                  data-testid="button-submit-create-team"
                >
                  {createMutation.isPending ? "Creating..." : "Create Team"}
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>

      {/* Edit Team Dialog */}
      <Dialog open={!!editingTeam} onOpenChange={(open) => !open && setEditingTeam(null)}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto" data-testid="dialog-edit-team">
          <DialogHeader>
            <DialogTitle>Edit Team</DialogTitle>
            <DialogDescription>
              Update team details and assignments
            </DialogDescription>
          </DialogHeader>
          <Form {...editForm}>
            <form 
              onSubmit={editForm.handleSubmit((data) => editingTeam && updateMutation.mutate({ id: editingTeam.id, data }))} 
              className="space-y-4"
            >
              <FormField
                control={editForm.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Team Name</FormLabel>
                    <FormControl>
                      <Input {...field} placeholder="e.g., Maintenance Team" data-testid="input-edit-team-name" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={editForm.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Description (Optional)</FormLabel>
                    <FormControl>
                      <Input {...field} placeholder="Team description" data-testid="input-edit-team-description" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={editForm.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Distribution Email</FormLabel>
                    <FormControl>
                      <Input {...field} type="email" placeholder="team@example.com" data-testid="input-edit-team-email" />
                    </FormControl>
                    <FormDescription>
                      Work order notifications will be sent to this email
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={editForm.control}
                name="userIds"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Team Members (Staff)</FormLabel>
                    <div className="space-y-2 max-h-40 overflow-y-auto">
                      {users.map((user) => (
                        <div key={user.id} className="flex items-center space-x-2">
                          <Checkbox
                            checked={field.value?.includes(user.id)}
                            onCheckedChange={(checked) => {
                              const updatedValue = checked
                                ? [...(field.value || []), user.id]
                                : (field.value || []).filter((id) => id !== user.id);
                              field.onChange(updatedValue);
                            }}
                            data-testid={`checkbox-edit-user-${user.id}`}
                          />
                          <Label className="font-normal cursor-pointer">
                            {user.firstName} {user.lastName} ({user.email})
                          </Label>
                        </div>
                      ))}
                    </div>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={editForm.control}
                name="contactIds"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Team Members (Contractors)</FormLabel>
                    <div className="space-y-2 max-h-40 overflow-y-auto">
                      {contacts.filter(c => c.type === 'contractor').map((contact) => (
                        <div key={contact.id} className="flex items-center space-x-2">
                          <Checkbox
                            checked={field.value?.includes(contact.id)}
                            onCheckedChange={(checked) => {
                              const updatedValue = checked
                                ? [...(field.value || []), contact.id]
                                : (field.value || []).filter((id) => id !== contact.id);
                              field.onChange(updatedValue);
                            }}
                            data-testid={`checkbox-edit-contact-${contact.id}`}
                          />
                          <Label className="font-normal cursor-pointer">
                            {contact.firstName} {contact.lastName} ({contact.email})
                          </Label>
                        </div>
                      ))}
                    </div>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={editForm.control}
                name="categories"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Maintenance Categories</FormLabel>
                    <FormDescription>
                      Assign categories to auto-route work orders to this team
                    </FormDescription>
                    <div className="space-y-2 max-h-40 overflow-y-auto">
                      {inspectionCategories.map((category) => (
                        <div key={category.id} className="flex items-center space-x-2">
                          <Checkbox
                            checked={field.value?.includes(category.name)}
                            onCheckedChange={(checked) => {
                              const updatedValue = checked
                                ? [...(field.value || []), category.name]
                                : (field.value || []).filter((name) => name !== category.name);
                              field.onChange(updatedValue);
                            }}
                            data-testid={`checkbox-edit-category-${category.id}`}
                          />
                          <Label className="font-normal cursor-pointer">
                            {category.name}
                          </Label>
                        </div>
                      ))}
                    </div>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={editForm.control}
                name="isActive"
                render={({ field }) => (
                  <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                    <FormControl>
                      <Checkbox
                        checked={field.value}
                        onCheckedChange={field.onChange}
                        data-testid="checkbox-edit-team-active"
                      />
                    </FormControl>
                    <div className="space-y-1 leading-none">
                      <FormLabel>Active</FormLabel>
                      <FormDescription>
                        Only active teams can be assigned work orders
                      </FormDescription>
                    </div>
                  </FormItem>
                )}
              />

              <DialogFooter>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setEditingTeam(null)}
                  data-testid="button-cancel-edit-team"
                >
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={updateMutation.isPending}
                  data-testid="button-submit-edit-team"
                >
                  {updateMutation.isPending ? "Saving..." : "Save Changes"}
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={!!deletingTeam} onOpenChange={(open) => !open && setDeletingTeam(null)}>
        <DialogContent data-testid="dialog-delete-team">
          <DialogHeader>
            <DialogTitle>Delete Team</DialogTitle>
            <DialogDescription>
              Are you sure you want to delete "{deletingTeam?.name}"? This action cannot be undone.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setDeletingTeam(null)}
              data-testid="button-cancel-delete-team"
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={() => deletingTeam && deleteMutation.mutate(deletingTeam.id)}
              disabled={deleteMutation.isPending}
              data-testid="button-confirm-delete-team"
            >
              {deleteMutation.isPending ? "Deleting..." : "Delete Team"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

