import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { BarChart3, TrendingUp, Users, AlertCircle, Clock } from "lucide-react";

interface WorkOrderAnalytics {
  total: number;
  statusDistribution: { [key: string]: number };
  priorityDistribution: { [key: string]: number };
  teamDistribution: { [key: string]: { name: string; count: number } };
  categoryDistribution: { [key: string]: number };
  averageResolutionTimeMinutes: number;
}

export default function Analytics() {
  const { data: analytics, isLoading } = useQuery<WorkOrderAnalytics>({
    queryKey: ["/api/analytics/work-orders"],
  });

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="space-y-4">
          <div className="h-8 bg-muted rounded animate-pulse" />
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
            {[1, 2, 3, 4].map((i) => (
              <Card key={i}>
                <CardHeader className="space-y-2">
                  <div className="h-4 bg-muted rounded animate-pulse" />
                  <div className="h-8 bg-muted rounded animate-pulse" />
                </CardHeader>
              </Card>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (!analytics) {
    return (
      <div className="container mx-auto p-6">
        <div className="text-center py-12">
          <p className="text-muted-foreground">No analytics data available</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight" data-testid="heading-analytics">
            Work Order Analytics
          </h1>
          <p className="text-muted-foreground mt-1">
            Overview of maintenance and work order metrics
          </p>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-5">
        <Card data-testid="card-total-work-orders">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Work Orders</CardTitle>
            <BarChart3 className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-total-count">
              {analytics.total}
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-open-work-orders">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Open</CardTitle>
            <AlertCircle className="h-4 w-4 text-yellow-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-open-count">
              {analytics.statusDistribution?.open || 0}
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-in-progress-work-orders">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">In Progress</CardTitle>
            <TrendingUp className="h-4 w-4 text-blue-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-in-progress-count">
              {analytics.statusDistribution?.in_progress || 0}
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-completed-work-orders">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Completed</CardTitle>
            <Users className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-completed-count">
              {analytics.statusDistribution?.completed || 0}
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-avg-resolution-time">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Avg Resolution</CardTitle>
            <Clock className="h-4 w-4 text-purple-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-avg-resolution-time">
              {analytics.averageResolutionTimeMinutes > 0
                ? (() => {
                    const hours = Math.floor(analytics.averageResolutionTimeMinutes / 60);
                    const minutes = Math.round(analytics.averageResolutionTimeMinutes % 60);
                    return hours > 0 && minutes > 0
                      ? `${hours}h ${minutes}m`
                      : hours > 0
                      ? `${hours}h`
                      : `${minutes}m`;
                  })()
                : "N/A"}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Distribution Charts */}
      <div className="grid gap-4 md:grid-cols-2">
        {/* Priority Distribution */}
        <Card data-testid="card-priority-distribution">
          <CardHeader>
            <CardTitle>Priority Distribution</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              {Object.entries(analytics.priorityDistribution || {}).map(([priority, count]) => (
                <div key={priority} className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <div
                      className={`w-3 h-3 rounded-full ${
                        priority === "urgent"
                          ? "bg-red-500"
                          : priority === "high"
                          ? "bg-orange-500"
                          : priority === "medium"
                          ? "bg-yellow-500"
                          : "bg-green-500"
                      }`}
                    />
                    <span className="capitalize" data-testid={`text-priority-${priority}`}>
                      {priority}
                    </span>
                  </div>
                  <span className="font-medium" data-testid={`text-priority-${priority}-count`}>
                    {count as number}
                  </span>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Category Distribution */}
        <Card data-testid="card-category-distribution">
          <CardHeader>
            <CardTitle>Category Distribution</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              {Object.entries(analytics.categoryDistribution || {}).map(([category, count]) => (
                <div key={category} className="flex items-center justify-between">
                  <span className="capitalize" data-testid={`text-category-${category}`}>
                    {category}
                  </span>
                  <span className="font-medium" data-testid={`text-category-${category}-count`}>
                    {count as number}
                  </span>
                </div>
              ))}
              {Object.keys(analytics.categoryDistribution || {}).length === 0 && (
                <p className="text-sm text-muted-foreground">No categories assigned yet</p>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Team Distribution */}
        <Card data-testid="card-team-distribution" className="md:col-span-2">
          <CardHeader>
            <CardTitle>Team Distribution</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              {Object.entries(analytics.teamDistribution || {}).map(([teamId, data]: [string, any]) => (
                <div key={teamId} className="flex items-center justify-between">
                  <span data-testid={`text-team-${teamId}`}>{data.name}</span>
                  <span className="font-medium" data-testid={`text-team-${teamId}-count`}>
                    {data.count}
                  </span>
                </div>
              ))}
              {Object.keys(analytics.teamDistribution || {}).length === 0 && (
                <p className="text-sm text-muted-foreground">No teams assigned yet</p>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

server/documentProcessor.ts.content:
import mammoth from 'mammoth';

export interface ProcessedDocument {
  extractedText: string;
  pageCount?: number;
  error?: string;
}

export async function extractTextFromFile(
  fileUrl: string,
  fileType: string
): Promise<ProcessedDocument> {
  try {
    const response = await fetch(fileUrl);
    if (!response.ok) {
      throw new Error(`Failed to fetch file: ${response.statusText}`);
    }

    const arrayBuffer = await response.arrayBuffer();
    const fileBuffer = Buffer.from(arrayBuffer);

    if (fileType === 'pdf' || fileType === 'application/pdf') {
      return await extractTextFromPDF(fileBuffer);
    } else if (
      fileType === 'docx' ||
      fileType === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
    ) {
      return await extractTextFromWord(fileBuffer);
    } else if (fileType === 'txt' || fileType === 'text/plain') {
      return {
        extractedText: fileBuffer.toString('utf-8'),
      };
    } else {
      return {
        extractedText: '',
        error: `Unsupported file type: ${fileType}`,
      };
    }
  } catch (error: any) {
    console.error('[Document Processor] Error extracting text:', error);
    return {
      extractedText: '',
      error: error.message || 'Failed to extract text from document',
    };
  }
}

async function extractTextFromPDF(buffer: Buffer): Promise<ProcessedDocument> {
  try {
    const pdfParse = (await import('pdf-parse')).default;
    const data = await pdfParse(buffer);
    return {
      extractedText: data.text,
      pageCount: data.numpages,
    };
  } catch (error: any) {
    console.error('[PDF Parser] Error:', error);
    return {
      extractedText: '',
      error: error.message || 'Failed to parse PDF',
    };
  }
}

async function extractTextFromWord(buffer: Buffer): Promise<ProcessedDocument> {
  try {
    const result = await mammoth.extractRawText({ buffer });
    return {
      extractedText: result.value,
    };
  } catch (error: any) {
    console.error('[Word Parser] Error:', error);
    return {
      extractedText: '',
      error: error.message || 'Failed to parse Word document',
    };
  }
}

export function chunkText(text: string, chunkSize: number = 2000): string[] {
  const sentences = text.split(/[.!?]\s+/);
  const chunks: string[] = [];
  let currentChunk = '';

  for (const sentence of sentences) {
    if ((currentChunk + sentence).length <= chunkSize) {
      currentChunk += (currentChunk ? '. ' : '') + sentence;
    } else {
      if (currentChunk) chunks.push(currentChunk);
      currentChunk = sentence;
    }
  }

  if (currentChunk) chunks.push(currentChunk);
  return chunks;
}

export function findRelevantChunks(
  text: string,
  query: string,
  maxChunks: number = 3
): string[] {
  const chunks = chunkText(text);
  const queryTerms = query.toLowerCase().split(/\s+/);

  const scoredChunks = chunks.map((chunk) => {
    const chunkLower = chunk.toLowerCase();
    let score = 0;

    for (const term of queryTerms) {
      const matches = (chunkLower.match(new RegExp(term, 'g')) || []).length;
      score += matches;
    }

    return { chunk, score };
  });

  return scoredChunks
    .filter((item) => item.score > 0)
    .sort((a, b) => b.score - a.score)
    .slice(0, maxChunks)
    .map((item) => item.chunk);
}

